css:
@-moz-document domain("namu.wiki") {

.test-ad-card,
.ad-card,
.adsense,
.wiki-inner-content + iframe,
.ad-bottom,
.kakao_ad_area,
.adsbygoogle,
article > div:last-of-type iframe,
.ad,
div[id^="google_ads_iframe_"] {
  display: none !important;
}

/* content-wrapper */
.senkawa-fixed-1300 > div:nth-child(2) {
  margin-left: 1% !important;
  max-width: none !important;
  width: 76% !important;
}
/* wiki-article */
.senkawa-fixed-1300 > div:nth-child(2) > article {
  width: 100% !important;
  min-height: calc(100vh - 42px - 119.17px) !important;
}

.wiki-macro-footnote {
  position: fixed !important;
  bottom: 0 !important;
  background-color: #fff !important;
  max-height: 150px !important;
  width: 72.4% !important;
  overflow: auto !important;
  box-shadow: 0px 0px 3px !important;
  transition: .3s !important;
  z-index: 1 !important;
}

.wiki-macro-footnote:hover {
  max-height: 300px !important
}

/* side */
aside {
  position: fixed !important;
  right: 0 !important;
  top: 67px !important;
  display: flex !important;
  flex-direction: row !important;
  justify-content: flex-start !important;
  width: 22% !important;
  z-index: 1 !important;
  max-height: 80vh !important;
  overflow-y: auto !important;
  border: .0625rem solid #ccc !important;
  background: white !important;
}

aside > div {
  max-width: 50% !important;
  height: 100% !important;
  margin-bottom: 0 !important;
  border: none !important;
}

aside > div:nth-child(1) {
  border-right: .0625rem solid #ccc !important;
}

/* search box */
nav form > div {
  position: fixed !important;
  right: 15px !important;
  top: 42px !important;
  width: 22.05% !important;
  z-index: 2 !important;
}

/* left-search-btns */
nav form > div > span:nth-child(1) {
  width: 8% !important;
  vertical-align: top !important;
}

nav form > div > span:nth-child(1) a {
  width: 100% !important;
}
/* searchInput */
nav form > div > div:nth-child(2) {
  width: 84% !important;
}
/* right-search-btns */
nav form > div > span:nth-child(3) {
  float: right !important;
  width: 16% !important;
}

nav form > div > span:nth-child(3) button {
  float: right !important;
  margin-left: 0 !important;
  width: 50% !important;
}

nav form > div > span:nth-child(3) button:nth-child(2) {
  border-left: 0 !important;
}

.popper {
  z-index: 2 !important;
}

@media (max-width: 1110px) {
  .senkawa-fixed-1300 > div:nth-child(2) {
    margin-left: 0% !important;
    width: 100% !important;
  }

  .senkawa-fixed-1300 > div:nth-child(2) > article {
    margin-right: 0 !important;
  }

  .wiki-macro-footnote {
    max-height: 10px !important;
    width: 90% !important;
  }

  .wiki-macro-footnote:hover {
    max-height: 150px !important
  }

  nav form > div {
    right: 20px !important;
    top: 73px !important;
    width: 30.05% !important;
    background-color: white !important;
  }

  nav form > div > div:nth-child(2) {
    width: 75% !important;
  }

  nav form > div > span:nth-child(3) {
    width: 25% !important;
  }

  nav form > div > span:nth-child(1) a,
  nav form > div > div:nth-child(2),
  nav form > div > span:nth-child(3) button {
    background-color: rgba(255, 255, 255, 0.3) !important;
  }

  nav form > div > span:nth-child(3) button:nth-child(2) {
    border-right: 1px solid #ccc !important;
  }

  aside {
    top: 91px !important;
    right: 4px !important;
    max-height: 100px !important;
    border: 1px solid #ccc !important;
    width: 30% !important;
    background-color: white !important;
    transition: .3s !important;
    font-size: 0.8rem !important;
  }

  aside:hover {
    max-height: 200px !important
  }

  aside > div {
    margin-bottom: 0 !important;
  }
  
  aside > div:nth-child(1) {
    border-right: 0 !important;
  }

  aside h5 {
    font-size: 0.8rem !important;
    margin: 0 !important;
    padding: 20px !important;
    text-align: center !important;
  }

  aside > div:nth-child(1) h5 {
    border-right: 1px solid #ccc !important;
  }

  aside > div:nth-child(1) a {
    overflow: hidden !important;
    border-right: 1px solid #ccc !important;
  }
}

}
