css:
@-moz-document domain("vidlii.com") {
html body {
    background-color: #181818;
}

#hd_name {
    color: #549cf8;
}

#sm_nav a {
    color: #549cf8;
}

a.ba {
    color: #549cf8;
}

div.adh a {
    color: #549cf8;
}

a.ch_l.s {
    color: #549cf8;
}

div.mp_hr div a {
    color: #549cf8;
}

#inbox_wdg div a {
    color: #549cf8;
}

div.last_5 div a {
    color: #549cf8;
}

div.last_5 strong {
    color: #ffffff;
}

div.mv_under div a {
    color: #ffffff;
}

div.wdg div span {
    color: #ffffff;
	text-shadow:0px 1px 0 rgb(0,0,0),0px -1px 0 rgb(0,0,0),1px 0px 0 rgb(0,0,0),-1px 0px 0 rgb(0,0,0),1px 1px 0 rgb(0,0,0),1px -1px 0 rgb(0,0,0),-1px 1px 0 rgb(0,0,0),-1px -1px 0 rgb(0,0,0);
	-webkit-text-shadow:0px 1px 0 rgb(0,0,0),0px -1px 0 rgb(0,0,0),1px 0px 0 rgb(0,0,0),-1px 0px 0 rgb(0,0,0),1px 1px 0 rgb(0,0,0),1px -1px 0 rgb(0,0,0),-1px 1px 0 rgb(0,0,0),-1px -1px 0 rgb(0,0,0);
	-moz-text-shadow:0px 1px 0 rgb(0,0,0),0px -1px 0 rgb(0,0,0),1px 0px 0 rgb(0,0,0),-1px 0px 0 rgb(0,0,0),1px 1px 0 rgb(0,0,0),1px -1px 0 rgb(0,0,0),-1px 1px 0 rgb(0,0,0),-1px -1px 0 rgb(0,0,0);
}

.n_head .pr_hd_bar {
    background: linear-gradient(#4e4e4e, #2e2e2e)!important;
    border: 0px solid white !important;
    border-radius: 5px !important;
}

#pr_sel li {
    border-color: #909090 !important;
    background: linear-gradient(#4e4e4e, #2e2e2e) !important;
    color: #fff !important;
	text-shadow: 1px 1px 0px #303030 !important;
}

#pr_hd li, .n_head li {
	border: 1px solid #909090;
}

header li {
    background: linear-gradient(#4e4e4e, #2e2e2e) !important;
    color: #fff !important;
    text-shadow: 1px 1px 0px #303030;
}

.wdg > div:first-of-type {
    background: #303030;
	border-left: 1px solid #909090;
    border-top: 1px solid #909090;
    border-right: 1px solid #909090;
}

.wdg>div:first-of-type>a {
    color: #fff;
}

.wdg>div:last-of-type, .wt_des {
	border: 1px solid #909090;
}

.mv_under {
    background: linear-gradient(#4e4e4e, #2e2e2e);
}

.whats_new {
    background: #1a1a00;
    border-radius: 8px;
    border: 1px solid #f5e082;
	color: #ffffff;
    padding: 14px;
    margin: 0 0 10px;
}

a {
    color: #549cf8;
    text-decoration: none;
}

div.wrapper footer form {
    color: #ffffff;
}

div.wrapper footer {
    background-color: #303030;
}

div div strong {
    color: #909090;
}

.vc_hd {
    background: #303030;
}

div.vc_cats {
    background-color: #303030;
}

ul li a {
    color: #549cf8;
}

div.vc_cats ul li {
    color: #ffffff;
}

div.h_l div div {
    color: #ffffff;
}

.vw {
    color: #fff;
}

div.wdg div div {
    color: #ffffff;
}

div.result {
    color: #ffffff;
}

span.p_yes {
    color: #808080;
}

div a strong {
    color: #ffffff;
}

div.search_hd div {
    color: #ffffff;
}

.pr_edit_ch {
    background: #303030;
}

#pr_hd {
    background-color: #181818;
}

.pr_hd_bar {
    background: linear-gradient(#4e4e4e, #2e2e2e);
    border: .5px solid #303030;
}

.settings_menu #nav_sel {
    background: linear-gradient(#4e4e4e, #2e2e2e);
	border: 1px solid #303030;
}

.settings_menu a {
    color: #808080;
}

div.vc_r {
    color: #606060;
}

h1.pg_hd {
    color: #ffffff;
}

.partner_box {
    background: linear-gradient(#2e2e2e, #1e1e1e);
    border: 1px solid #909090;
}

.you_wnt > div {
    background: #303030;
}

.wdg>div:last-of-type, .wt_des {
    border: 1px solid #909090;
}
.wt_des {
    background: #303030;
}

#des_text {
    color: #ffffff;
}

div.wt_person {
    color: #ffffff;
}

.w_title>h1 {
    color: white;
}

.w_views {
	color: white;
}

.w_lnks span {
    border-bottom: 1px dotted #549cf8;
}

span.u_sct_hd {
    color: #ffffff;
}

#no_video_comments {
    color: #ffffff;
}

#w_l_cnts {
    background-color: #303030;
    border-color: #909090;
}

.bottom_wrapper {
    font-size: 14px;
    color: #808080;
}

div.w_actions {
    border-color: #909090;
}

div.w_btm {
    border-color: #909090;
}

.wt_c_sct>div:first-of-type {
    background: #ffffff;
}

.w_big_btn {
    background: transparent linear-gradient(#2e2e2e, #1e1e1e) repeat scroll 0% 0%;
}

.w_big_btn>a {
    color: #fff;
}

#video_title_header {
    color: #000000;
}

#upload_status {
    color: #000000;
}

div div i {
    color: #000000;
}
}
