    padding: 6px !important;
    margin-left: -107px !important;
    opacity: 1 !important;
    margin-top: -3px !important;
    position: absolute !important;
}    */
#fourchanx-settings.dialog,
fieldset legend,
.yotsuba-b:root .suboption-list > div:last-of-type,
.dialog:not(#qr):not(#thread-watcher):not(#header-bar),
#fourchanx-settings code,
kbd {
    border: none !important;
}

#fourchanx-settings fieldset {
    border: 0px solid;
    border-radius: 3px;
}


/* hide notifications */
#blotter,
.globalMessage {
    display: none !important;
}

#toggleMsgBtn {
    opacity: 0 !important;
}
#toggleMsgBtn:hover {
    opacity: 0.8 !important;
}

div.post div.postInfo span.postNum {
