css:
@-moz-document domain("pub.dev") {
/*
 * Pallete taken from https://ethanschoonover.com/solarized/
 */

/* Provide solarized background and text colors for the main tags */
body, main, .site-header {
    background-color: #002b36;
}
.package-banner, select {
    background-color: #073642;
}
._banner-bg {
    background-image: none;
    background-color: #073642;
}
nav, p, h1, h2, h3, h4, h5, h6, li, .search-bar-options, .tooltip-dotted, .package-count {
    color: #839496;
}
a {
    color: #268bd2;
}
a:hover {
    color: #d33682;
}
select {
    color: #93a1a1;
}


/* Header navigation colours */
.sub-nav, .tooltip-content {
    background-color: #fdf6e3;
    color: #657b83;
}
.sub-nav > .link {
    color: #586e75;
}

/* Searchbar colours */
.search-bar {
    background-color: #002b36;
    border-color: #586e75;
}
.input {
    color: #2aa198;
}
.input::placeholder {
    color: #839496;
}

/*
 *   BEGIN SECTION: Package page
 */
/* These are the tags show just below the title. */
.package-tag {
    color: #2aa198;
    background-color: #073642;
}

/* Tabs */
.package-tabs > .tab {
    color: #839496;
}
.package-tabs > .tab.-active {
    color: #93a1a1;
    border-bottom-color: #268bd2;
}

/* Color for the publish date shown under the title. */
.package-header > .metadata {
    color: #586e75;
}

.package-container > .sidebar {
    background-color: #073642;
    border-radius: 8px;
    border-color: #586e75;
}

/* Add rounded corners to code blocks missing them (looking at you "Installing" tab) */
code {
    border-radius: 4px;
}

/* Author name/email */
.author {
    color: #2aa198;
}
/*
 *   End SECTION: Package page
 */

/*
 *   BEGIN SECTION: Home and search results pages
 */
.landing-page-title, .listing-page-title {
    color: #859900;
}
.list-item > .description {
	color: #839496;    
}
.list-item:hover{
    /* This color breaks the solarized scheme so as to avoid blending with the package tags background. */
    background-color: #002f3d;
}

.list-filters {
    border-color: #586e75;
}
.list-filters > .filter {
    background-color: #073642;
    color: #2aa198;
    border-color: #586e75;
}
.list-filters > .filter.-active {
    background-color: #002b36;
    color: #d33682;
}
.score-box > .new {
    color: #fdf6e3;
    background-color: #b58900;
}
.pagination > li {
    background-color: #073642;
    color: #2aa198;
    border-radius: 999px;
}
.pagination > li.-disabled {
    color: #93a1a1;
}
.pagination > li.-active {
    background-color: #2aa198;
    color: #fdf6e3;
}
/*
 *   END SECTION: Home and search results pages
 */
}
