css:
/* i really want this to be global */


/* General modal container */

.modal {
	height: 100% !important;
	margin: 0 !important;
	right: 0px !important;
	margin-right: 0 !important;
	position: absolute !important;
}

/* Specific modals */
.modal--message {
	height: 270px !important;	
}

.alm-entity-form-entity-dialog {
	width: 900px !important;
	left: calc(100% - 900px) !important;
}

.user-preference-dialog {
	position: fixed !important;
	margin: auto !important;
	top: 56px !important;
	left: 50% !important;
	height: auto !important;
	width: 400px !important;
}

.modal-business-rule-edit-dialog {
	width: 900px !important;
	left: calc(100% - 900px) !important;
}

.modal-business-rule-edit-dialog .business-rule-edit-dialog-body {
	height: calc(100% - 90px);
}

.modal-business-rule-edit-dialog .business-rule-edit-dialog-body textarea.br-action-tab-remark {
	width: 600px;
}

.alm-app-dialog .workspace_user-entity-dialog {
	width: 500px !important;
	min-width: 500px !important;
	height: 100% !important;
	margin-left: 0 !important;
	left: calc(100% - 500px) !important;
}

.run-entity-dialog {
	top: 0 !important;
	width: 900px !important;
	min-width: 900px !important;
	left: calc(100% - 900px) !important;
}

.split-work-item-dialog {
	width: 500px !important;
	min-width: 500px !important;
	left: calc(100% - 500px) !important;
}

.split-work-item-dialog .modal__body {
	padding: 0 !important;
	margin: 20px 10px;
}

.split-work-item-dialog .modal__body .actions-second-column {
	width: auto;	
}

.modal-send-mail-dialog {
	width: 700px !important;
	min-width: 700px !important;
	left: calc(100% - 700px) !important;
	animation: none !important;
}

/* Modal Header */
.modal__header {
	height: 44px;	
}

.modal__header .title-bar {
	height: 44px;	
}

.modal__header .title-bar .title-bar__title {
	font-size: 16px;
	font-family: MetricWeb-Medium, Calibri, sans-serif;
}

/* Modal Footer */
.modal__footer {
	height: 44px;	
	padding: 0;
	align-items: center;
}

/* Text styles */
.modal .field-label, .modal .br-action-tab-label-container {
	font-size: 14px;
	color: #555;
}

/* Field styles */
.modal .alm-field-component .text-field-input, .modal div.select2-container .select2-choice, .modal input[type=text].text-field-input, .modal div.text-field-input, .modal .send-mail-dialog-alm-ckeditor-content {
	border: 1px solid #eee;
	margin: 0 !important;
	position: relative;
	padding: 0;
}

.modal .checkbox-label .checkbox-text {
	font-size: 14px;	
}

.modal div.select2-container .select2-choice .select2-chosen, .modal input[type=text].text-field-input, .modal div.text-field-input, .modal .send-mail-dialog-alm-ckeditor-content {
	padding-left: 4px;
	font-size: 14px;
	font-family: MetricWeb-Regular, Calibri, sans-serif;
}

.modal .alm-field-component .rich-content-editor-element {
	border: 1px solid #eee;
	font-size: 14px;
}

.modal .alm-field-component textarea {
	font-size: 14px;
	font-family: MetricWeb-Light, Calibri, sans-serif;
}

.modal .alm-field-component textarea::placeholder, .modal .business-rule-add-comment::placeholder, .modal .br-action-tab-remark::placeholder {
	color: #ccc;
	font-family: MetricWeb-Regular, Calibri, sans-serif !important;
}

.modal .alm-field-component .text-field-input:hover, .modal .alm-field-component .alm-ckeditor-content:hover, .modal .alm-field-component textarea:hover, .modal .business-rule-add-comment:hover, .modal .br-action-tab-remark:hover, .modal input[type=text].text-field-input:hover, .modal div.text-field-input:hover, .modal .send-mail-dialog-alm-ckeditor-content:hover {
	border: 1px solid #ccc;
}

.modal .alm-field-component .text-field-input:active {
	border: 1px solid #eee;	
}

.modal .alm-field-component .text-field-input:focus, .modal .alm-field-component .alm-ckeditor-content:focus, .modal .alm-field-component textarea:focus, .modal .business-rule-add-comment:focus, .modal .br-action-tab-remark:focus, .modal input[type=text].text-field-input:focus, .modal div.text-field-input:focus, .modal .send-mail-dialog-alm-ckeditor-content:focus {
	border: 1px solid #0079ef;
	outline: none;
}

.modal .alm-field-component input[type=text], .modal .alm-field-component input[type=number], .modal .alm-field-component textarea, .modal .business-rule-add-comment, .modal .br-action-tab-remark, .modal .send-mail-dialog-alm-ckeditor-content {
	padding: 4px;
	font-family: MetricWeb-Regular;
	color: #555;
	font-size: 14px;
}


.modal .alm-field-component .text-field-input > div {
	margin-left: 4px;
	top: 0 !important;
}

.modal .alm-field-component .text-field-input .field-editor-preeditor-single-value-container {
	height: 26px;
	top: 0;
}

/* selection icons, like entity type */
.modal .alm-field-component .text-field-input .field-editor-preeditor-display-value-icon {
	margin-top: 2px;
}

.modal .alm-field-component .text-field-input .field-editor-preeditor-display-value-icon .field-editor__entity-label__box {
	height: 16px;
	width: 16px;
	min-width: 16px;
	line-height: 15px;
}


/* Field value text */
.modal .alm-field-component .text-field-input .field-editor-preeditor-display-value-icon .field-editor__entity-label__box .field-editor__entity-label__text {
	top: -1px;	
}

.modal .alm-field-component .text-field-input .field-editor-preeditor-field-display-value-text, .modal .alm-field-component .text-field-input {
	font-size: 14px;
	top: 0 !important;
}

/* Tag styles */
.modal .text-field-input .multi-value-badge {
	padding: 0 !important;
	font-size: 13px !important;
}

.modal .text-field-input .multi-value-badge .multi-value-badge-item {
	height: 18px;
	border-radius: 0;
}

.modal .text-field-input .multi-value-badge > svg {
	position: absolute;
	top: 2px;
	right: 1px;
}
