css:
@-moz-document domain(marktplaats.nl) {
#page-wrapper {
    background-color: #090909;
}
body {
    background-color: #0F0F0F;
    color: #e9e9e9;
}

a {
    color: #d9ac00;
    cursor: pointer;
    text-decoration: none;
    font-weight: 400;
}
.snippet-list .snippet-description {
    color: #e9e9e9;
}

#navigation-categories .link a {
    color: #ff8244;
}

.mp-Nav-dropdown-menu, .mp-Nav-pills {
    background: #333;
}
.mp-Nav-dropdown-menu>li>a, .mp-Nav-pills>li>a {
    color: #e9e9e9;
    font-size: 15px;
    line-height: 16px;
}

#map-link img {
    opacity: 0.3;
    border-radius: 3px;
    width: 100%;
    max-width: 190px;
}

.mp-Listing-card-body .mp-Listing-card-price {
    font-size: 18px;
    line-height: 24px;
    color: #cecece;
}
.listing-thumbnail .priority-product {
    color: #43ff90;
}
.mp-Header-navBar.mp-header {
    background: #111;
    color: #ffffff;
    padding: 9px 16px;
}
.mp-Header-logo.mp-header {
    background: url(https://s.marktplaats.com/z/dist/bower_components/design-sdk/Marktplaats.Sprite-7f6e965c.svg) no-repeat;
  	padding: 10px;
  	border: 10px solid #ccc;
    border-radius: 26px;
    background-color: #ccc;
    background-size: auto 100%;
    height: 26px;
    width: 130px;
    display: inline-block;
}
.mp-Header-searchBar.mp-header {
    background: #111;
    padding: 15px 16px;
}

#ontdek-meer-op-marktplaats.sticky-tabs .feed-tabs {
    top: 120px;
    background: #222;
}
.feed-tabs .feed-tab {
    color: #f0f0f0;
    cursor: pointer;
}
.is-selected.mp-Step, .mp-Tab--selected.mp-Step, .mp-Tab.is-selected, .mp-Tab.mp-Tab--selected, .mp-Tabs .is-selected.mp-Tabs-label, .mp-Tabs .mp-Tab--selected.mp-Tabs-label, .mp-Tabs .mp-Tabs-input:checked+.mp-Tabs-label {
    border-bottom-color: #eda566;
    color: #349dd1;
}

#map-link .heading a {
    color: #ffffff;
}
.mp-Input, .mp-LicensePlate input, .mp-Select select, .mp-TagsInput, .mp-Textarea {
    font-family: Roboto,sans-serif;
    font-style: normal;
    font-weight: 300;
    font-size: 16px;
    line-height: 20px;
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    background-color: #222;
    border: 1px solid #666;
    border-radius: 3px;
    /* box-shadow: inset 0 3px 0 0 hsla(213,8%,74%,.2); */
    box-sizing: border-box;
    color: #5eb9ff;
    display: inline-block;
    height: 35px;
    padding: 4px 8px;
    vertical-align: middle;
    width: 100%;
}
.mp-Select select {
    -webkit-tap-highlight-color: rgba(0,0,0,0);
    background: #333;
    /* border-color: #d4d1cd; */
    /* box-shadow: inset 0 -2px 0 0 #f7f7f6; */
    cursor: pointer;
    padding-right: 32px;
}
.mp-SearchForm-search.mp-header {
    font-family: BreeSerif;
    font-weight: 300;
    font-size: 16px;
    background: #eda566;
    /* border-color: #ffffff; */
    /* box-shadow: inset 0 -2px 0 0 #e6e4e1; */
    color: #2d3c4d;
}

.mp-HamburgerMenu.mp-header .mp-Nav-pills.mp-header .mp-Header-hamburger-close.mp-header, .mp-Header-link.mp-header, .mp-Header-menu.mp-header>li.mp-header>a.mp-header, .mp-Nav-dropdown-toggle.mp-header, mp-header .mp-Header-links>li>a {
    background: #111;
    border: 1px solid #666;
    color: #ffffff;
    font-size: 16px;
}

.aanbieding-widget-container .fixed-group-container .heading a {
    color: #ffffff;
}

.image-group .heading-link {
    color: #ffffff;
    background-size: auto 100%;
    width: 36px;
    height: 36px;
}

.deals-icon {
    border-radius: 10px;
    background-color: #ffffff;
    margin-right: 6px;
}
.style a {
    margin: 1px 1px 16px 65px;
}

.mp-Card-block.mp-Card-block--highlight {
    background: #222;
}

.aanbieding-widget-first {
    background: linear-gradient(90deg,hsla(40,9%,94%,0) 0, #090909);
}

.heading-1, .heading-1 a, .heading-2 a, .heading-3 a, .heading-4 a, .heading-5 a, .heading-6 a, h1 {
    font-family: BreeSerif;
    font-style: normal;
    font-weight: 300;
}

.l-top-content {
    background-color: #111;
}

.mp-Card, .mp-Form-body, .mp-Form-footer, .mp-Listing-card, .mp-Listing-compact {
    background: #111;
    box-shadow: 0 1px 2px 0 rgba(0, 0, 0, .14);
    color: #FFFFFF;
}

.contact-info .mp-Card-block--custom-highlight {
    background-color: #222;
}

#footer {
    background-color: #111;
}

.mp-Footer {
    background: #111;
    color: #111;
    padding: 8px 0 32px;
}

.tabs-vertical .tab-vertical.active, .tabs-vertical .tab-vertical.active:hover {
    background-color: #222;
}
.tabs-vertical .tab-vertical.active a {
    color: #e9e9e9;
}
.tabs-vertical .tab-vertical {
    background-color: #1a1a1a;
}
.l-page {
    background-color: #111;
}
.search-results-table .row {
    min-height: 133px;
    background-color: #222;
}
.column-listing .description {
    color: #e9e9e9;
    font-weight: 300;
    font-size: 14px;
    line-height: 20px;
}
.bidding-block .mp-Card-block--custom-highlight {
    background-color: #222;
}
.mp-Button--secondary {
    font-weight: 300;
    font-size: 16px;
    line-height: 16px;
    background: #333;
    border-color: #999;
    box-shadow: inset 0 -2px 0 0 #e6e4e1;
    color: #e9e9e9;
}
#save-seller-button.mp-Button--secondary {
    line-height: 16px;
    background: #e0e0e0;
    border-color: #666666;
    box-shadow: inset 0 -2px 0 0 #e6e4e1;
}
.pageRoot {
    background-color: #111;
}
.ContentPlaceholderMolecule-root {
    color: #c9c9c9;
    background-color: #222;
}
.MessagingApp-leftUnitInner .MessagingApp-conversationsTitle {
    background-color: #222;
    height: 50px;
}
.ConversationMolecule-root {
    background: #222;
    color: #ff00ff;
    box-shadow: 0 2px 2px 0 rgba(0,0,0,.1);
    border: 2px solid transparent;
}
.ConversationMolecule-root .ConversationMolecule-title {
    font-weight: 300;
    font-size: 16px;
    color: #e9e9e9;
}
.ConversationMolecule-root .ConversationMolecule-meta {
    color: #7c9dff;
    font-weight: 400;
}
.MessagingApp-conversationMessages>article {
    background-color: #111;
}
.MessagesOrganism-root {
    background-color: #222;
}
.AdvertisementSnippetMolecule-root {
    background: #222;
    color: #e9e9e9;
    box-shadow: 0 2px 2px 0 rgba(0,0,0,.1);
}
.ConversationTopicOrganism-root {
    background: #222;
}
.ConversationMolecule-root.ConversationMolecule-rootOpen {
    background: #555;
    color: #e9e9e9;
}
.MessageMolecule-root.MessageMolecule-root_from_otherparticipant {
    background: #264c7c;
    color: #ffffff;
    box-shadow: 0 2px 2px 0 rgba(0,0,0,.1);
}
.MessageComposerOrganism-root {
    background-color: #222;
    /* border-top: 1px solid #3b3838; */
}
.InlineComposerMenuMolecule-root {
    border-top: 1px solid #222;
    background: #111;
}
body {
    background-color: #111;
    color: #dcdcdc;
}
#page-wrapper.mymp .l-page #fixed-tablehead-sticky .table-body .ad-listing, #page-wrapper.mymp .l-page #seller-panel.exp-mympredesign .table-body .ad-listing {
    background-color: #333;
    transition: background-color .2s ease-out;
}
.zeroResults {
    background-color: #111;
}
#page-wrapper.mymp #ad-listing-header, #page-wrapper.mymp #searches-listing-header {
    background: #222;
}
.mp-Button--secondary[disabled], .mp-Button--secondary[disabled]:hover {
    background: #111;
    border-color: #858585;
    box-shadow: none;
    color: #e9e9e9;
}
#searches-listing-header .select-all {
    width: 30px;
    height: 26px;
    padding-top: 5px;
    float: left;
    text-align: center;
    margin-right: 5px;
    border: 1px solid #A1A1A1;
    background: #232323;
    background: -moz-linear-gradient(top, #FEFEFE 0%, #D9D9D9 100%);
    background: -webkit-gradient(linear, left top, left bottom, left, right);
    /* background: -webkit-linear-gradient(top, #2e2b2b 0%, #D9D9D9 100%); */
    background: -o-linear-gradient(top, #FEFEFE 0%, #D9D9D9 100%);
    background: -ms-linear-gradient(top, #FEFEFE 0%, #D9D9D9 100%);
    /* background: linear-gradient(to bottom, #121010 0%, #D9D9D9 100%); */
}
}
