css:
@-moz-document domain("freenom.com") {
body {
    background: #222;
    color: #aaa;
}

.home .splash-world {
    background: #444;
    padding: 30px 0 30px;
    height: auto;
}

pageHeader, h1.splash {
    color: #FFF;
    background: transparent !important;
}

.splash-world .change {
    text-transform: uppercase;
    font-size: 20px;
    color: #aaa;
    font-weight: 600;
}

.home .splash-world h2 {
    text-align: right;
    float: right;
    width: 100%;
    font-weight: 600;
    color: rgba(0,174,239,1);
    font-size: 32px;
    line-height: 36px;
    margin-top: 10px;
}

.home .splash-world .input {
    background: #fff url(https://i.imgur.com/90CHGVD.png) repeat scroll 0 0 / cover;
    color: #ddd;
    display: block;
    font-size: 36px;
    font-weight: 400;
    height: 50px;
    letter-spacing: 3px;
    line-height: 50px;
    margin: 10px auto;
    padding: 0;
    text-align: center;
    max-width: 250px;
}

.home .splash-world li {
    float: left;
    padding: 10px;
    color: #aaa;
    font-weight: 600;
    margin-top: 20px;
    text-transform: uppercase;
}

.home .splash-world li span, .home .splash-world li a {
    color: #777;
    font-weight: 600;
    border-bottom: 1px solid #000;
    cursor: pointer;
}

.home:hover .splash-world:hover li:hover span:hover, .home:hover .splash-world:hover li:hover a:hover {
    color: #aaa;
    font-weight: 600;
    border-bottom: 1px solid #aaa;
    cursor: pointer;
}

.home .splash-world .lines {
    border-left: 1px solid #000;
    border-right: 1px solid #000;
}

.col-md-12 { 
    width: 100%;
    background: #444;
    color: #aaa;
}

h1.splash {
    font-size: 48px;
    font-weight: 400;
    text-shadow: 0px 0px 30px #aaa; 
}

.main-header .main-menu li.divider {
    border-left: 1px solid #555;
    padding: 0;
    margin: 0 5px 0 10px;
}

.main-header .main-menu span, .main-header .main-menu a {
    font-size: 13px;
    color: #aaa;
    float: left;
}

.main-header .drop-down ul li a {
    width: 100%;
    float: left;
    line-height: 44px;
    border-bottom: 1px solid #555;
    font-size: 13px;
    padding: 0 15px;
    text-align: left;
    background: #444;
}

.main-header:hover .drop-down:hover ul:hover li:hover a:hover {
    background: #666;
}

.main-footer {
    background: #222;
    letter-spacing: 0.05em;
    z-index: 100;
    position: relative;
    float: left;
}

.main-footer, .main-footer address, .main-footer a {
    line-height: 22px;
    color: #aaa;
    font-size: 13px;
    z-index: 0;
}

.sub-footer {
    border-bottom: 4px solid rgba(0,174,239,1);
    letter-spacing: 0.05em;
    background: #444;
    z-index: 100;
    position: relative;
}

.content {
    background: #444;
    padding: 20px 40px 40px;
    box-shadow: 0 5px 0 rgba(0, 0, 0, 0.15);
    margin-top: 60px;
    margin-bottom: 60px;
    color: #ddd !important;
}

h1 {
    color: #00adeb;
}


textarea, input[type="text"], input[type="password"] {
    width: 100%;
    background: #666;
    border: 0px solid #CCC;
    padding: 8px 5px;
    color: #ddd;
}

.row.padding [class*="span"] {
    padding: 20px;
    background: #444;
    color: #ddd;
}

.bottomCart, .domainResult {
    background: none repeat scroll 0 0 #222;
    float: left;
    overflow: hidden;
    width: 100%;
}







}

@-moz-document domain("my.freenom.com") {
.row {
    margin-left: 0;
    margin-right: 0;
    background: #444;
}

.primaryFontColor small {
    color: #aaa;
}

.table thead th {
    vertical-align: bottom;
    background: #666;
    border: 1px solid #000 !important;
}

.table-bordered > tbody > tr > td, .table-bordered > tbody > tr > th, .table-bordered > tfoot > tr > td, .table-bordered > tfoot > tr > th, .table-bordered > thead > tr > td, .table-bordered > thead > tr > th {
    vertical-align: middle;
    border: 1px solid #000;
}

.table th, .table td {
    padding: 8px 10px;
    line-height: 18px;
    text-align: left;
    font-weight: 400;
    vertical-align: middle;
    border: 1px solid #222;
    background: #333
}

select, .renewDomains select, .forwardingTabcontent select, .transferContent .domainreginput select, .submitTicket select, .domainTabcontent select, select#paymentmethod, select#stateselect, select#country, .resultsPerPage select {
    background: #333 url(https://i.imgur.com/2oVbyyo.png) right center no-repeat;
    border: 1px solid #000;
    border-radius: 0px;
    padding: 5px 25px 5px;
    position: relative;
    -moz-appearance: none;
    -webkit-appearance: none;
    line-height: 22px;
}

textarea, input[type="text"], input[type="password"] {
    width: 100%;
    background: #7b7b7b;
    border: 0px solid #CCC;
    border-radius: 0;
    padding: 10px 5px;
    color: #ddd !important;
}

.whiteBtn:visited, .whiteBtn {
    background: #444;
    border: 1px solid #222;
    color: #aaa;
    padding: 4px 9px;
}

.dropdown-menu {
  position: absolute;
  top: 25px;
  left: -20px;
  z-index: 1000;
  display: none;
  padding: 10px 20px;
  min-width: 200px;
  margin: 12px 0 0!important;
  list-style: none;
  background-color: #444;
  box-shadow: 0 0 0 0 !important;
  border: 1px solid #000;
}

.dropdown-menu .divider {
    height: 1px;
    margin: 9px 0;
    overflow: hidden;
    background-color: #000;
}

.dropdown-menu>li>a {
    display: block;
    padding: 3px 20px;
    clear: both;
    font-weight: 400;
    line-height: 1.42857143;
    color: #aaa;
    white-space: nowrap;
}

.nav-tabs > li.active > a, .nav-tabs > li.active > a:focus, .nav-tabs > li.active > a:hover {
    background: #aaa;
    color: #333;
}

.colInformation {
    border-right: 1px solid #000;
    padding: 0px 15px !important;
    border-radius: 0;
}

.greyBtn:visited, .greyBtn {
    background: #666;
    border: 1px solid #000;
    color: #ddd;
}

section.downloadContent ul.fileList li a, section.downloadContent ul.categoryList li a, .whoisContent h2, .primaryFontColor {
    color: rgba(0,173,236,1);
    text-shadow: 0px 0px 30px #000;
}

.table td a {
    font-weight: 400;
    text-decoration: none;
    color: #aaa;
}

a, a:visited {
    text-decoration: none;
    color: #777;
    font-size: 14px;
}

section.login, section.servicesContent, section.replyTicket, section.serverContent, section.ticketContent, section.domainContent {
    margin-top: 30px;
    padding-bottom: 50px;
    background: #222;
}

#domainForm {
    background: #7d7d7d;
    height: 78px;
    margin-top: 0 !important;
    overflow: visible;
    padding: 10px;
    border-radius: 4px;
}

.domainCheck input[type="text"] {
    -webkit-border-radius: 0px;
    -moz-border-radius: 0px;
    border-radius: 0px;
    -webkit-border-top-left-radius: 4px;
    -moz-border-top-left-radius: 4px;
    border-top-left-radius: 4px;
    -webkit-border-bottom-left-radius: 4px;
    -moz-border-bottom-left-radius: 4px;
    border-bottom-left-radius: 4px;
    font-size: 20px;
    height: 58px;
    line-height: 58px;
    width: 70%;
    float: left;
    padding-left: 10px;
    border: 0;
    background: #333;
}

.contentPage {
    background: #333;
    padding: 30px!important;
    overflow: hidden;
}

.phoneprefix {
    display: inline-flex;
    height: 39px;
    align-items: center;
    background-color: #666;
}

#pwstrengthbox {
    padding: 8px 15px;
    background: #555;
    float: left;
}

.table tr.subtotal td {
    font-weight: 600;
    font-size: 12px;
    background: #555 !important;
}

.table tr.total td {
    font-weight: 600;
    font-size: 17px;
    padding: 15px 10px;
    background: #a6ee69 !important;
    color: #444;
}


























}

@-moz-document url-prefix("http://whois.freenom.com") {
.content {
    background: transparent;
}

textarea, input[type="text"], input[type="password"] {
    width: 100%;
    background: #7b7b7b;
    border: 0px solid #CCC;
    padding: 8px 5px;
    color: #ddd !important;
}

.span12 {
    width: 100%;
    background: #444;
}

.index-page h1 {
    color: #555;
    text-align: center;
    margin: 30px 0 50px;
    text-shadow: 0px 0px 30px #000;
}

}
