css:
@-moz-document domain("jianshu.com"), domain("jianshu.io")
{
  pre
  {
    margin-left: -150px;
    min-width: 920px;
    font-family: Hack,Menlo,Monaco,Consolas,Courier New,monospace;
  }

  .hljs {
    display: inline-block;
    overflow-x: auto;
    padding: 0.5em;
    background: #2E3440
}

.hljs,.hljs-subst {
    color: #D8DEE9
}

.hljs-selector-tag {
    color: #81A1C1
}

.hljs-selector-id {
    color: #8FBCBB;
    font-weight: bold
}

.hljs-selector-class {
    color: #8FBCBB
}

.hljs-selector-attr {
    color: #8FBCBB
}

.hljs-selector-pseudo {
    color: #88C0D0
}

.hljs-addition {
    background-color: rgba(163,190,140,0.5)
}

.hljs-deletion {
    background-color: rgba(191,97,106,0.5)
}

.hljs-built_in,.hljs-type {
    color: #8FBCBB
}

.hljs-class {
    color: #8FBCBB
}

.hljs-function {
    color: #88C0D0
}

.hljs-function>.hljs-title {
    color: #88C0D0
}

.hljs-keyword,.hljs-literal,.hljs-symbol {
    color: #81A1C1
}

.hljs-number {
    color: #B48EAD
}

.hljs-regexp {
    color: #EBCB8B
}

.hljs-string {
    color: #A3BE8C
}

.hljs-title {
    color: #8FBCBB
}

.hljs-params {
    color: #D8DEE9
}

.hljs-bullet {
    color: #81A1C1
}

.hljs-code {
    color: #8FBCBB
}

.hljs-emphasis {
    font-style: italic
}

.hljs-formula {
    color: #8FBCBB
}

.hljs-strong {
    font-weight: bold
}

.hljs-link:hover {
    text-decoration: underline
}

.hljs-quote {
    color: #4C566A
}

.hljs-comment {
    color: #4C566A
}

.hljs-doctag {
    color: #8FBCBB
}

.hljs-meta,.hljs-meta-keyword {
    color: #5E81AC
}

.hljs-meta-string {
    color: #A3BE8C
}

.hljs-attr {
    color: #8FBCBB
}

.hljs-attribute {
    color: #D8DEE9
}

.hljs-builtin-name {
    color: #81A1C1
}

.hljs-name {
    color: #81A1C1
}

.hljs-section {
    color: #88C0D0
}

.hljs-tag {
    color: #81A1C1
}

.hljs-variable {
    color: #D8DEE9
}

.hljs-template-variable {
    color: #D8DEE9
}

.hljs-template-tag {
    color: #5E81AC
}

.abnf .hljs-attribute {
    color: #88C0D0
}

.abnf .hljs-symbol {
    color: #EBCB8B
}

.apache .hljs-attribute {
    color: #88C0D0
}

.apache .hljs-section {
    color: #81A1C1
}

.arduino .hljs-built_in {
    color: #88C0D0
}

.aspectj .hljs-meta {
    color: #D08770
}

.aspectj>.hljs-title {
    color: #88C0D0
}

.bnf .hljs-attribute {
    color: #8FBCBB
}

