css:
@-moz-document domain("somtoday.nl") {
/* The yellow ribbon tag that contains icons such as messages or the back/forward buttons in the rooster tab*/
  .yellow.ribbon
  {
    background: url('https://hethooghuis-elo.somtoday.nl/images/bck_ribbonyellow-ver-DDBEF94FDE2B9119280C2339B5922BBF.png') no-repeat left 0;
    position: relative;
    z-index: 2;
  }

  .yellow.ribbon p
  {
    background: url('https://hethooghuis-elo.somtoday.nl/images/bck_ribbonyellow-ver-DDBEF94FDE2B9119280C2339B5922BBF.png') no-repeat right 0;
  }

  #main-menu-bottom
  {
    background-color: #ffb700;
  }

  #main-menu a.active,
#main-menu a.active:hover
  {
    background: #ffb700;
  }

  #main-menu a:hover
  {
    background: #ffb700;
  }

  #dialog
  {
    background: #ffb700;
  }

  .IconFont.b-alert
  {
    color:#ffb700;
  }

  .today
  {
    border-bottom: solid 3px #ffb700;
  }

  .stpanel .form--input i
  {
    color: #ffb700;
  }

  .stpanel .form--select i:after
  {
    border-top: .3rem solid #ffb700;
  }

  .stpanel .form--select i:before
  {
    border-bottom: .3rem solid #ffb700;
  }

  .stpanel .form--input.state-disabled:hover i
  {
    color:#ffb700 !important;
  }

  .icon-toets,
.icon-huiswerk,
.icon-studiewijzer,
.icon-leermiddelen,
.icon-geen-van-beide,
.icon-maatregel
  {
    background: #ffb700;
  }

  .stpanel section form
  {
    background: #ffb700;
  }

  .stpanel--meldingen
  {
    background: #ffb700;
  }

  .stpanel--meldingenpage
  {
    background-color: #ffb700;
  }

  .stpanel--screensaver
  {
    background: #ffb700 !important;
  }

  .stpanel--screensaver .form--input i
  {
    color: #ffb700;
  }

  .stpanel--screensaver .form--select i:after
  {
    border-top: .3rem solid #ffb700;
  }

  .stpanel--screensaver .form--select i:before
  {
    border-bottom: .3rem solid #ffb700;
  }

  .stpanel--screensaver .form--input.state-disabled:hover i
  {
    color: #ffb700 !important;
  }

  .stpanel--screensaver section form
  {
    background: #ffb700;
  }
body, .loaderFade {
  background-color: #121212;
}

#header-wrapper, #main-menu a, .dayTitle, .roster-table-header .day .huiswerk-items, .pauze, .uurNummer, #master-panel .date div, .goverview tbody td, .goverview th, .tog-explanation, .type {
  background-color: #232323;
}

#detail-panel-wrapper {
  background-color: #1c1c1c;
}

#content {
  border: none;
  box-shadow: none;
}

#master-panel {
  background-color: #232323;
  color: #fff !important;
  padding-top: 0;
}

a, #main-menu a, .pauze, .uurNummer, #master-panel h2, body, h3, h4, .tog-explanation, .type, .IconFont.type {
  color: #fff;
}

.m-element {
  border: none;
}

.m-wrapper, #master-panel .panel-header, .load-more, #master-panel~#detail-panel-wrapper, .roster-table-header, .roster-table-content, .hours, .hour, .day, .weekitems, .goverview tbody td, .goverview th, .tog-explanation, .type {
  border: 1px solid #121212 !important;
}

h1, h2, .m-element {
  color: #fff;
}

.m-wrapper {
  min-height: 0;
}

.left {
  padding-top: 10px;
}

.m-element:hover, .load-more:hover {
  background-color: #474747;
}

.dayTitle {
  border-bottom: solid 3px #121212;
}

.roster-table-content .hours .hour {
  width: 957px;
}

.m-wrapper.active {
  background: #232323;
  border-right: none !important;
  margin-right: -1px;
}

#master-panel .date div {
  width: 32px;
  height: 44px;
  margin: -1px 0 0 -34px;
}

#master-panel .date {
  border-top-color: #121212;
}

.m-element.no-click:hover {
  border-bottom: none;
  background-color: #474747;
}

.section .block {
    background-color: #1c1c1c;
    border: none;
}

.logo {
    display: block;
    background-image: url(https://www.clker.com/cliparts/c/e/2/6/15433151231663574976ultieme%20som.med.png);
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    width: 140px;
    height: 28px;
    margin: 10px 20px 10px 0;
}

.stpanel header figure.figurelogo {
    background-image: url(https://www.clker.com/cliparts/c/e/2/6/15433151231663574976ultieme%20som.med.png);
}

.stpanel header figure {
    display: block;
    height: 5rem;
    width: 20rem;
    margin: 0 auto;
}
}
