@-moz-document domain("somtoday.nl") {
/* The yellow ribbon tag that contains icons such as messages or the back/forward buttons in the rooster tab*/
  .yellow.ribbon
  {
    background: url('https://hethooghuis-elo.somtoday.nl/images/bck_ribbonyellow-ver-DDBEF94FDE2B9119280C2339B5922BBF.png') no-repeat left 0;
    position: relative;
    z-index: 2;
  }

  .yellow.ribbon p
  {
    background: url('https://hethooghuis-elo.somtoday.nl/images/bck_ribbonyellow-ver-DDBEF94FDE2B9119280C2339B5922BBF.png') no-repeat right 0;
  }

  #main-menu-bottom
  {
    background-color: #ffb700;
  }

  #main-menu a.active,
#main-menu a.active:hover
  {
    background: #ffb700;
  }

  #main-menu a:hover
  {
    background: #ffb700;
  }

  #dialog
  {
    background: #ffb700;
  }

  .IconFont.b-alert
  {
    color:#ffb700;
  }

  .today
  {
    border-bottom: solid 3px #ffb700;
  }

  .stpanel .form--input i
  {
    color: #ffb700;
  }

  .stpanel .form--select i:after
  {
    border-top: .3rem solid #ffb700;
  }

  .stpanel .form--select i:before
  {
    border-bottom: .3rem solid #ffb700;
  }

  .stpanel .form--input.state-disabled:hover i
  {
    color:#ffb700 !important;
  }

  .icon-toets,
.icon-huiswerk,
.icon-studiewijzer,
.icon-leermiddelen,
.icon-geen-van-beide,
.icon-maatregel
  {
    background: #ffb700;
  }

  .stpanel section form
  {
    background: #ffb700;
  }

  .stpanel--meldingen
  {
    background: #ffb700;
  }

  .stpanel--meldingenpage
  {
    background-color: #ffb700;
  }

  .stpanel--screensaver
  {
    background: #ffb700 !important;
  }

  .stpanel--screensaver .form--input i
  {
    color: #ffb700;
  }

  .stpanel--screensaver .form--select i:after
  {
    border-top: .3rem solid #ffb700;
  }

  .stpanel--screensaver .form--select i:before
  {
    border-bottom: .3rem solid #ffb700;
  }

  .stpanel--screensaver .form--input.state-disabled:hover i
  {
    color: #ffb700 !important;
  }

  .stpanel--screensaver section form
  {
    background: #ffb700;
  }
body, .loaderFade {
  background-color: #121212;
}

#header-wrapper, #main-menu a, .dayTitle, .roster-table-header .day .huiswerk-items, .pauze, .uurNummer, #master-panel .date div, .goverview tbody td, .goverview th, .tog-explanation, .type {
  background-color: #232323;
}

#detail-panel-wrapper {
  background-color: #1c1c1c;
}

#content {
  border: none;
  box-shadow: none;
}

#master-panel {
  background-color: #232323;
  color: #fff !important;
  padding-top: 0;
}

a, #main-menu a, .pauze, .uurNummer, #master-panel h2, body, h3, h4, .tog-explanation, .type, .IconFont.type {
  color: #fff;
}

.m-element {
  border: none;
}

.m-wrapper, #master-panel .panel-header, .load-more, #master-panel~#detail-panel-wrapper, .roster-table-header, .roster-table-content, .hours, .hour, .day, .weekitems, .goverview tbody td, .goverview th, .tog-explanation, .type {
  border: 1px solid #121212 !important;
}

h1, h2, .m-element {
  color: #fff;
}

.m-wrapper {
  min-height: 0;
}

.left {
  padding-top: 10px;
}

.m-element:hover, .load-more:hover {
  background-color: #474747;
}

.dayTitle {
  border-bottom: solid 3px #121212;
}

.roster-table-content .hours .hour {
  width: 957px;
}

.m-wrapper.active {
  background: #232323;
  border-right: none !important;
  margin-right: -1px;
}

#master-panel .date div {
  width: 32px;
  height: 44px;
  margin: -1px 0 0 -34px;
}

#master-panel .date {
  border-top-color: #121212;
}

.m-element.no-click:hover {
  border-bottom: none;
  background-color: #474747;
}

.section .block {
    background-color: #1c1c1c;
    border: none;
}

.logo {
    display: block;
    background-image: url(https://i.imgur.com/qvj5OqH.png);
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    width: 140px;
    height: 28px;
    margin: 10px 20px 10px 0;
}

.stpanel header figure.figurelogo {
    background-image: url(https://i.imgur.com/qvj5OqH.png);
}

.stpanel header figure {
    display: block;
    height: 6rem;
    width: 30rem;
    margin: 0 auto;
}

.stpanel {
    background: url(https://c1.staticflickr.com/4/3894/14562399571_f249a504d1_b.jpg) no-repeat center center fixed;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
}

.stpanel .loader--dot1, .stpanel .loader--dot2, .stpanel .loader--dot3, .stpanel .loader--dot4 {
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: #f60;
    position: absolute;
    -webkit-animation: .5s ease infinite;
    animation: .5s ease infinite;
}

.stpanel .loader--dot1, .stpanel .loader--dot2, .stpanel .loader--dot3, .stpanel .loader--dot4 {
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: #f60;
    position: absolute;
    -webkit-animation: .5s ease infinite;
    animation: .5s ease infinite;
}

.stpanel .loader--dot1, .stpanel .loader--dot2, .stpanel .loader--dot3, .stpanel .loader--dot4 {
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: #f60;
    position: absolute;
    -webkit-animation: .5s ease infinite;
    animation: .5s ease infinite;
}

.stpanel .loader--dot1, .stpanel .loader--dot2, .stpanel .loader--dot3, .stpanel .loader--dot4 {
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: #f60;
    position: absolute;
    -webkit-animation: .5s ease infinite;
    animation: .5s ease infinite;
}

.afspraak {
    position: absolute;
    left: 5px;
    right: 5px;
    background-color: #232323;
    margin: 2px;
    text-align: left;
}

.afspraakLocatie {
    color: #ffb700;
    font-size: 12px;
    font-weight: 600;
    line-height: 20px;
    width: auto;
    word-break: break-all;
    float: left;
}

.afspraakVakNaam {
    color: #ffdd30;
    font-size: 13px;
    height: 14px;
    width: auto;
    word-break: break-all;
}

.toekenning a {
    color: #ffb700;
}

.afspraak .toekenning {
    background-color: #232323;
    margin-left: 3px;
    height: 100%;
}

#master-panel .date div p.date-day {
    height: 11px;
    padding-top: 0;
    margin: 0 1px;
    font-size: 10px;
    color: #3f5c6e;
    background-color: #ffb700;
    text-transform: uppercase;
}

#master-panel .date div {
    position: absolute;
    width: 28px;
    height: 42px;
    padding-top: 1px;
    margin: -1px 0 0 -28px;
    background-color: #424242;
    text-align: center;
    font-size: 21px;
    font-weight: bold;
    color: #ffb700;
}

#master-panel .date div p.date-month {
    height: 11px;
    padding-top: 0;
    margin-top: -1px;
    font-size: 10px;
    color: #ffb700;
    background-color: transparent;
    text-transform: uppercase;
}

.button--stpanel, input[type=submit].button--stpanel, button.button--stpanel {
    -webkit-appearance: none;
    -moz-appearance: none;
    appearance: none;
    border-radius: 0;
    cursor: pointer;
    display: inline-block;
    font-size: 13px;
    font-weight: bold !important;
    text-transform: uppercase;
    font-family: 'Roboto',Helvetica,Arial,sans-serif;
    line-height: 1.5rem !important;
    padding: .3rem 1rem;
    border: 0;
    vertical-align: baseline;
    text-decoration: none;
    color: #fff !important;
    background-color: #474747;
    -webkit-font-smoothing: antialiased;
}

.stpanel a {
    color: #565757;
    text-decoration: none;
    font-weight: 300;
    -webkit-transition: color 50ms;
    transition: color 50ms;
    cursor: pointer;
}

}
