css:
@-moz-document regexp(".*gitlab(.*)com.*") {
:root {
    --accentColor: /*[[accentColor]]*/;
    --accentColorHover: /*[[accentColorHover]]*/;
    --accentTextColor: /*[[accentTextColor]]*/;
    --headerColor: /*[[headerColor]]*/;
    --color0: #000;
    --color1: #111;
    --color2: #222;
    --color3: #333;
    --color4: #444;
    --color5: #555;
    --color6: #666;
    --color7: #777;
    --color8: #888;
    --color9: #999;
    --colorA: #AAA;
    --colorB: #BBB;
    --colorC: #CCC;
    --colorD: #DDD;
    --colorE: #EEE;
    --colorF: #FFF;
    --gray0: #0d0d0d;
    --gray1: #1d1d1d;
    --gray2: #2d2d2d;
    --gray3: #3d3d3d;
    --gray4: #4d4d4d;
    --gray5: #5d5d5d;
    --gray6: #6d6d6d;
    --gray7: #7d7d7d;
    --gray8: #8d8d8d;
    --gray9: #9d9d9d;
    --grayA: #AdAdAd;
    --grayB: #BdBdBd;
    --grayC: #CdCdCd;
    --grayD: #DdDdDd;
    --grayE: #EdEdEd;
    --grayF: #FdFdFd;
    --background1: #383838;
    --background2: #272727;
}

body {
    background-color: var(--gray2);
    color: var(--colorE);
}

a {
    color: var(--accentColor);
}

a:hover {
    color: var(--accentColorHover);
}

.gfm-project_member {
    background: transparent;
}

body[class^="ui-"] .navbar-gitlab {
    background: var(--headerColor);
}

.code.white .line a {
    color: #0ba7dc;
}

.scrolling-tabs-container.inner-page-scroll-tabs.is-smaller {
    background: var(--gray2) !important;
}

.todos-list > .todo.todo-pending.done-reversible {
    background: var(--gray3);
}

.todos-list > .todo.todo-pending.done-reversible:hover {
    background: var(--gray5);
}

.done-reversible > .todo-item > .title > * > * {
    color: var(--colorF) !important;
}

.todos-list > .todo {
    transition: background 400ms, border 400ms;
    ;
}

.todos-list > .todo:hover {
    background: var(--gray4);
    border-color: var(--gray6) !important;
}

.todo-item .todo-body .todo-note .md p {
    color: var(--colorA);
}

a.btn.btn-cancel {
    background: #fac5cda8 !important;
    color: var(--gray1) !important;
    transition: color 400ms, background 400ms !important;
}

a.btn.btn-cancel:hover {
    background: var(--gray5) !important;
    border-color: var(--gray7) !important;
    color: var(--colorA) !important;
}

input.btn.btn-success.qa-issuable-create-button {
    background: #c7f0d2a1;
    color: var(--gray1);
    transition: color 400ms, background 400ms;
}

input.btn.btn-success.qa-issuable-create-button:hover {
    color: var(--colorA);
}

.content-block.diff-files-changed.files-changed.js-diff-files-changed.oneline-block {
    background: var(--gray4);
}

.js-tabs-affix.merge-request-tabs-holder,
.commit-stat-summary.dropdown {
    background: var(--gray2) !important;
    border-color: transparent;
}

.avatar:not([href]) {
    transition: transform 400ms;
}

.avatar:not([href]):hover {
    border-color: var(--gray4);
    transform: scale(1.4);
}

.avatar {
    background: none;
}

.avatar-circle,
.avatar,
.avatar-container {
    border-color: var(--gray4);
}

.header-user-avatar {
    border-color: var(--gray4) !important;
}

.event-item .system-note-image.commented-on-icon svg,
.event-item .system-note-image-inline.commented-on-icon svg {
    fill: var(--accentColor);
}

.md a, .md:not(.use-csslab) a {
    color: var(--accentColor);
}

.content-list .group-name {
    color: var(--colorA);
}

.event-item .event-user-info .author_name a {
    color: var(--accentColor);
}

.row-content-block.white {
    background: var(--gray2);
}

.event-item {
    border-color: var(--gray4);
}

.top-area,
.wiki-page-header {
    background: var(--gray3);
    border-color: var(--gray5);
}

.profile-header > .user-info > * {
    color: var(--colorB);
}

.notes.timeline > .timeline-entry {
    background: var(--gray4);
    border-color: var(--gray6);
    padding: 16px;
}

.md img:not(.emoji),
.issuable-details .description img:not(.emoji) {
    border-color: transparent;
}

.notes > li .note-body .note-text [dir="auto"] {
    color: var(--colorB);
}

.notes > li .note-body .note-text a {
    color: var(--accentColor)
}

.mr-state-widget .ci-widget {
    color: var(--colorA)
}

body[class^="ui-"] .sidebar-top-level-items > li.active .badge.badge-pill,
body[class^="ui-"] .sidebar-top-level-items > li.active .shortcut-mappings .key,
.shortcut-mappings body[class^="ui-"] .sidebar-top-level-items > li.active .key {
    color: var(--accentColorHover);
}

.author-link {
    color: var(--accentColor);
}

.code.white .mi {
    color: #00d1d1;
}

.card > .center {
    background: var(--gray3);
}

.card > .center > .slead {
    color: var(--colorA);
}

.card > .center > .slead > .ref-name {
    color: var(--accentColor)
}

#contributors .person .spark,
#contributors-master svg {
    background: var(--gray4)
}

.sub-header-block {
    background: var(--gray3);
    border-color: var(--gray4);
}

.card,
.card-slim {
    border-color: var(--gray6);
}

.card-header,
.card-footer {
    background: var(--gray4);
    border-color: var(--gray6);
}

.sidebar-sub-level-items > li a:hover,
.sidebar-sub-level-items > li .participants-more .btn-link:hover,
.participants-more .sidebar-sub-level-items > li .btn-link:hover,
.sidebar-sub-level-items > li .user-list-more .btn-link:hover,
.user-list-more .sidebar-sub-level-items > li .btn-link:hover,
.sidebar-sub-level-items > li a:focus {
    color: var(--colorE)
}

.filtered-search-history-dropdown-toggle-button,
.scroll-container {
    background: var(--gray3);
    border: none;
}

.filtered-search-box {
    border-color: var(--gray6);
}

.flash-container .flash-notice {
    background: var(--accentColor);
    color: var(--accentTextColor);
}

.form-control:focus,
.common-note-form .md-area.is-focused,
.search form:focus,
.search.search-active form {
    box-shadow: none;
}

.breadcrumbs-list > li > a,
.breadcrumbs-list-angle {
    color: var(--colorC);
}

h1,
h2,
h3,
h4,
h5,
h6,
.breadcrumbs-sub-title a,
ul.content-list li {
    color: var(--colorE);
}

.form-text.text-muted {
    color: var(--colorA) !important;
}

hr,
.breadcrumbs-container,
.navbar-gitlab {
    border-color: var(--gray4);
}

select.form-control:not([size]):not([multiple]) {
    background: #2a2a2a;
    border-color: var(--gray4);
    color: var(--colorA);
}

.account-btn-link,
.profile-settings-sidebar a,
.settings-sidebar a {
    color: var(--accentColor);
}

.nav-sidebar,
.right-sidebar {
    background: var(--background1);
    box-shadow: none;
    border: none;
}

.context-header .sidebar-context-title,
.right-sidebar .title {
    color: var(--colorA);
}

.nav-sidebar li a {
    transition: color 200ms, background 400ms;
    color: var(--colorA);
}

.sidebar-top-level-items > li > a:hover,
.participants-more .sidebar-top-level-items > li > .btn-link:hover,
.user-list-more .sidebar-top-level-items > li > .btn-link:hover {
    color: var(--colorA);
    background: var(--background2);
}

body[class^="ui-"] .nav-sidebar li.active > a,
.profile-settings-content a,
.right-sidebar .title .lock-edit,
.right-sidebar .title .edit-link,
.right-sidebar .promotion-issue-sidebar .btn-link {
    color: var(--accentColor);
}

.right-sidebar.right-sidebar-expanded .no-value,
.right-sidebar.right-sidebar-expanded .btn-default-hover-link {
    color: var(--color9);
}

.header-user.show .dropdown-menu,
.dropdown-menu li a,
.dropdown-menu li button,
.dropdown-menu li .menu-item,
.dropdown-menu-nav li a,
.dropdown-menu-nav li button,
.dropdown-menu-nav li .menu-item {
    color: var(--colorA) !important;
}

.right-sidebar .title .lock-edit:hover,
.right-sidebar .title .edit-link:hover,
.right-sidebar .promotion-issue-sidebar .btn-link:hover,
.right-sidebar a:not(.btn):hover {
    color: var(--accentColorHover)
}

body[class^="ui-"] .nav-sidebar li.active svg {
    fill: var(--accentColor);
}

body[class^="ui-"] .nav-sidebar li.active {
    box-shadow: inset 4px 0 0 var(--accentColor);
}

.toggle-sidebar-button,
.close-nav-button {
    background: var(--background1);
    border: none;
    transition: background 400ms;
}

.toggle-sidebar-button .collapse-text,
.toggle-sidebar-button .icon-angle-double-left,
.toggle-sidebar-button .icon-angle-double-right {
    color: var(--colorA);
}

.toggle-sidebar-button:hover,
.close-nav-button:hover {
    background: var(--background2);
}

code {
    color: #e03a1f;
    background: #3c3534;
}

.form-control,
.search form {
    background: var(--gray3);
    border-color: var(--gray5);
    color: var(--colorA);
    transition: background 400ms, color 400ms;
}

.form-control:focus,
.common-note-form .md-area.is-focused,
.search form:focus,
.search.search-active form {
    background: var(--gray4);
    border-color: var(--gray6);
    color: var(--colorE);
}

.email-badge .badge-verification-status.verified {
    transition: background 400ms, color 400ms;
    cursor: pointer;
}

.email-badge .badge-verification-status.verified:not(span):hover {
    background: #393939;
    color: #19cd63;
    border-color: #19cd63;
}

.btn.disabled,
.btn[disabled],
fieldset[disabled] .btn,
.dropdown-toggle[disabled],
[disabled].dropdown-menu-toggle,
.btn.disabled:hover,
.btn[disabled]:hover,
fieldset[disabled] .btn:hover,
.dropdown-toggle[disabled]:hover,
[disabled].dropdown-menu-toggle:hover {
    background: var(--gray2) !important;
    border-color: var(--gray4) !important;
}

.dropdown-menu,
.dropdown-menu-nav {
    background: var(--gray4);
    border-color: var(--gray6);
}

.dropdown-menu .divider,
.dropdown-menu-nav .divider {
    background-color: transparent;
    border-color: var(--gray6);
}

.header-user.show .dropdown-menu,
.dropdown-menu li a,
.dropdown-menu li button,
.dropdown-menu li .menu-item,
.dropdown-menu-nav li a,
.dropdown-menu-nav li button,
.dropdown-menu-nav li .menu-item {
    color: var(--colorA);
    transition: background 400ms, color 400ms;
}

.dropdown-header {
    color: var(--colorA) !important;
}

.dropdown-menu li button:hover,
.dropdown-menu li .menu-item:hover,
.dropdown-menu li a:hover {
    background: var(--gray6) !important;
    color: var(--colorE) !important;
}

.frequent-items-dropdown-container .section-header,
.frequent-items-dropdown-container .frequent-items-list-container li.section-empty {
    color: var(--colorA);
}

.frequent-items-list-item-container .frequent-items-item-namespace {
    color: var(--color9);
}

ul.content-list li a {
    color: var(--colorA);
}

.btn:hover,
.btn:focus {
    color: var(--colorE);
}

a:hover > div > .frequent-items-list-item-container,
a:hover > div > .frequent-items-item-namespace {
    color: var(--colorB);
}

.loading-container {
    filter: invert(100);
}

.select2-container .select2-choice,
.select2-container.select2-drop-above .select2-choice {
    background: var(--gray4);
    border-color: var(--gray6);
}

.select2-container .select2-choice:hover,
.select2-container.select2-drop-above .select2-choice:hover,
.select2-container .select2-choice {
    color: var(--colorA);
}

.select2-drop,
.select2-drop.select2-drop-above {
    background: var(--gray4);
    border-color: var(--gray6);
}

.select2-search input {
    border-color: var(--gray7);
    background: var(--gray5);
    color: var(--colorA);
}

.select2-results .select2-result-label,
.select2-results .select2-more-results {
    background: var(--gray4);
    color: var(--colorB);
    transition: background 400ms;
}

.select2-results .select2-highlighted .select2-result-label {
    background: var(--gray6);
}

.select2-drop {
    color: var(--colorA);
}

.btn:not(:disabled):not(.disabled) {
    background: var(--gray4);
    border-color: var(--gray6);
    color: var(--colorA);
    transition: background 400ms, border 400ms;
}

.btn:not(:disabled):not(.disabled):hover {
    background: var(--gray5);
    border-color: var(--gray7);
}

input[type='checkbox']:hover {
    box-shadow: 0 0 2px 2px rgba(65, 65, 65, .4), 0 0 0 1px rgba(78, 78, 78, .4)
}

.account-well {
    background: var(--gray4);
    border-color: var(--gray6);
}

.pika-single {
    background: var(--gray4);
    border-color: var(--gray6);
}

.pika-label {
    background: transparent;
    color: var(--colorA) !important;
}

.pika-prev,
.pika-next {
    filter: invert(100);
}

.pika-single.gitlab-theme th,
.pika-single.gitlab-theme td {
    border-color: var(--gray6);
}

.pika-single.gitlab-theme .pika-day {
    background: var(--gray5);
    color: var(--colorB);
    transition: color 200ms, background 200ms;
}

.pika-single.gitlab-theme .pika-day:hover {
    background: var(--gray6);
    color: var(--colorC);
}

.pika-single.gitlab-theme .is-today .pika-day {
    background: var(--gray6);
    color: var(--colorC);
}

.pika-single.gitlab-theme .is-selected .pika-day {
    background: var(--gray7);
    color: var(--colorD);
}

.settings-message {
    background-color: #474139;
    color: #e29742;
    border-color: #7e694d;
}

.form-control:disabled,
.search form:disabled,
.form-control[readonly],
.search form[readonly] {
    background: var(--gray4);
}

.billing-plans .card .card-body .feature-list li,
.billing-plans .card-slim .card-body .feature-list li {
    background: var(--gray5);
    border-color: var(--gray7);
}

.billing-plans .card .card-body .price-per-month,
.billing-plans .card-slim .card-body .price-per-month,
.billing-plans .card.current .card-body .price-per-month,
.billing-plans .current.card-slim .card-body .price-per-month,
.billing-plans .card.current .card-body .price-per-year,
.billing-plans .current.card-slim .card-body .price-per-year,
.billing-plans .card .card-body .price-per-year,
.billing-plans .card-slim .card-body .price-per-year {
    background: var(--gray5);
    color: var(--accentColor);
}

.billing-plans .card .card-header,
.billing-plans .card-slim .card-header,
.billing-plans .card .card-body,
.billing-plans .card-slim .card-body {
    border: none;
}

.billing-plans .card .card-body .plan-action,
.billing-plans .card-slim .card-body .plan-action {
    background: var(--gray5);
}

.billing-plans .card .card-body .feature-list li:last-child:hover,
.billing-plans .card-slim .card-body .feature-list li:last-child:hover {
    background: var(--gray6);
}

.billing-plans .card .card-header,
.billing-plans .card-slim .card-header {
    background: var(--accentColor) !important;
    color: var(--accentTextColor) !important;
}

.provider-btn-group {
    border-color: var(--gray6);
}

.provider-btn {
    background: var(--gray5);
}

.provider-btn-image {
    border: none;
}

.provider-btn.not-active {
    color: var(--accentColorHover);
}

.input-group-text {
    background: var(--gray3);
    border-color: var(--gray5);
    color: var(--colorA);
}

.input-group .input-group-prepend,
.input-group .input-group-btn:first-child,
.input-group .input-group-btn:last-child {
    background: var(--gray2);
}

.input-group-prepend .btn:focus,
.input-group-btn:first-child .btn:focus,
.input-group-prepend .btn:active,
.input-group-btn:first-child .btn:active,
.input-group-append .btn:focus,
.input-group-btn:last-child .btn:focus,
.input-group-append .btn:active,
.input-group-btn:last-child .btn:active {
    background: var(--gray4);
    box-shadow: none;
}

.card,
.card-slim {
    background: var(--gray2);
}

ul.content-list li .description {
    color: var(--colorA);
}

.page-link {
    box-shadow: none !important;
}

.bg-success {
    background-color: var(--accentColorHover) !important;
}

.pipeline-quota .progress {
    background: var(--gray4);
}

table.table tr th,
.file-holder table tr th,
.wiki table tr th,
.md table tr th,
.notes > li .note-body .note-text table tr th {
    background: var(--gray4);
    border-color: var(--gray6);
}

table.table tr td,
.file-holder table tr td,
.wiki table tr td,
.md table tr td,
.notes > li .note-body .note-text table tr td {
    border-color: var(--gray6);
}

.page-item.disabled .page-link,
.page-link {
    background: var(--gray4);
    border-color: var(--gray6);
}

.page-item.active .page-link {
    background: var(--accentColor);
    color: var(--accentTextColor);
    border-color: var(--accentColor);
}

.page-link:hover {
    color: var(--accentColorHover);
    background: var(--gray5);
    border-color: var(--gray7);
}

button.dropdown-menu-toggle.js-project-refs-dropdown.qa-branches-select {
    background: var(--gray4);
    border-color: var(--gray6);
    color: var(--colorA);
    box-shadow: none !important;
}

.dropdown-title > span,
.dropdown-menu-selectable li a.is-active {
    color: var(--colorE) !important;
}

.info-well {
    background: var(--gray4);
    border-color: var(--gray6);
    color: var(--colorA);
}

.clipboard-group .label-monospace,
.commit-sha-group .label-monospace {
    background: var(--gray5);
    border-color: var(--gray7);
    color: var(--colorE);
}

.tree-holder .tree-table tr:not(.tree-truncated-warning) td {
    transition: background 400ms, border 400ms;
}

.tree-holder .tree-table tr:hover:not(.tree-truncated-warning) td {
    border-color: var(--gray6);
    background: var(--gray5);
    color: var(--colorE);
}

.tree-holder .tree-commit .tree-commit-link {
    color: var(--colorA);
}

.tree-holder .tree-item .tree-item-file-name i,
.tree-holder .tree-item .tree-item-file-name a {
    color: var(--colorA);
}

.commit a,
.generic_commit_status a {
    color: var(--colorE);
}

.file-holder .file-title a:not(.btn) {
    color: var(--colorE);
}

.file-holder .file-content {
    background: transparent;
}

.wiki a {
    color: var(--accentColor)
}

.wiki,
.wiki p,
.wiki h1,
.wiki h2,
.wiki h3,
.wiki h4,
.wiki h5,
.wiki h6 {
    color: var(--colorE);
}

.code.white,
.code.white .line_holder .line_content {
    background: var(--gray6);
    border-color: var(--gray8);
    color: var(--colorE);
}

.code.white pre.code {
    background: var(--gray4);
    border-color: var(--gray6);
    color: var(--color9);
}

.file-title-flex-parent,
.file-holder .file-title-flex-parent,
.code.white .line-numbers,
.code.white .diff-line-num {
    background: var(--gray4);
    border-color: var(--gray6);
}

.code.white .diff-line-num {
    color: var(--colorE);
}

.code.white .na {
    color: #00b4b4;
}

.code.white .s,
.code.white .s1,
.code.white .s2 {
    color: #f6416e;
}

.detail-page-header a,
.issuable-meta,
.detail-page-description .title {
    color: var(--colorA);
}

.toolbar-text {
    color: var(--colorE);
}

.note-textarea {
    background: var(--gray6);
    color: var(--colorE);
    box-shadow: none !important;
    border: none !important;
}

body[class^="ui-"] .nav-links li.active a,
body[class^="ui-"] .nav-links li.md-header-tab.active button,
body[class^="ui-"] .nav-links li a.active {
    border-color: var(--accentColor);
}

.note-textarea {
    outline: none !important;
}

.md,
.mr-state-widget {
    color: var(--colorA);
}

.mr-state-widget .mr-widget-section,
.mr-state-widget .mr-widget-footer {
    border-color: var(--gray6);
}

.mr-source-target,
.mr-section-container,
.mr-widget-heading {
    border-color: var(--gray6);
}

.deploy-heading {
    background: var(--gray4);
}

.deploy-body .deployment-info,
.text-plain,
.text-plain:hover {
    color: var(--colorA);
}

.dropdown-toggle-text.is-default,
.issuable-list li .issuable-milestone,
.issuable-list li .issuable-info,
.issuable-list li .task-status,
.issuable-list li .issuable-updated-at {
    color: var(--colorA);
}

.merge-request.closed,
.issue.closed,
.merge-request.merged,
.issue.merged {
    background: var(--gray2);
    border-color: var(--gray4);
