css:
@-moz-document domain("gg.pl") {
body {
    background-color:#0f0f0f;
}
#ext-comp-1002 {
    background-color: #0b0b0b !important;
    border: 1px solid #2d2d2d;
    border-top: none;
    border-left:none;
    border-right:none;
}
#sr-mainnav a, .sr-notifications-list, .sr-notifications-header, .description, .message-text, .item-showname, .message-type-1, .message-row, .last-messages-header, .show-name, .user-data-bottom, .title a, .sr-user-name, .sr-contact-name, .sr-contact-desc, .user-description, .chat-timer, .span-item-time, .item-body, #search-form, .roulette-search, .roulette-container-title, .message-content span[style] {
    color: white !important;
}
#ext-gen1159, #ext-gen1091 {
    background-image: none !important;
}
#ext-comp-1007, #sr-webgg {
    background-color:#0b0b0b !important; 
    border: 0.1px solid #2d2d2d !important;
}
#sr-account-btn {
    color: white !important;
    height: 0px !important;
    width: 0px !important;
}
.account-widget-bottom, .account-widget-bottom a {
    background: #242424 !important;
    color: white !important;
    border: 1px solid #2d2d2d  !important;
}
#sr-contact-list-panel {
    border-bottom:none !important;
}
#ext-gen1072 {
    color:white !important;
}
#ext-gen1110 {
    background-image: none !important;
    color: white !important;
}
.friend {
    background-color: #1a1a1a !important;
    border-top: none !important;
    border-bottom: 1px solid #2d2d2d  !important;
}
.friend:hover {
    background-color: #242424 !important;
    cursor:pointer;
}

#sr-contact-list-panel, #sr-contact-list-search {
    background-color:#0b0b0b !important;  
    color:white !important;  
    border:1px solid #2d2d2d !important;
}
.search-input {
    background-image: none !important;
    background-color:#0b0b0b !important;  
    color:white !important;  
    border:1px solid #2d2d2d !important;
    margin-right: 2px !important;
}
.header {
    background-color:#0b0b0b !important;
}
.opened-item {
    border: 1px solid white;
    background-color:#0b0b0b !important;
}
.title {
    color:white !important;
    text-shadow: none !important;
    font-size: 12px !important;
}
.chat-submit-toolbar {
    background-color:#0a0a0a !important;
    border: 1px solid white;
    border-top: none;
}
.avatar-widget {
    border: 1px solid #2d2d2d !important;
}
.share-links-item-title {
    color: white !important;
}
#html5-uploader-button {
    background-image: url('https://i.ibb.co/ccsMgR0/fileupload-window-btn-png.png')!important;
}
.giphy-btn {
    text-shadow: none !important;
    color: white !important;
}
.chat-data {
    background-color: #0b0b0b !important;
}
.chat-btns {
    background: #242424 !important;
    border: 1px solid #2d2d2d !important;
    margin: 1px 1px 0px 0px !important;
}
#ext-gen1098 {
    height: 22px !important;
    width: 22px !important;
}
#chat-text {
    background: #0f0f0f !important;
    color: white !important;
}
.me {
    background: #141414 !important;
}
.my {
    background: #141414 !important;
}
.you {
    background: #1b1b1b !important;
}
.writinglabel, .writingico {
    border-top: 1px solid white;
}
.emoticons-list, .last-emoticons-list {
    background-color:#1a1a1a !important;
}
.body {
    background: #141414 !important;
    border: 1px solid #2d2d2d !important;
}
.conversation-aol, .conversation-aol a {
    background: #0f0f0f !important;
    color: white !important;
}
.conversation-header, .status-li {
    background: #1a1a1a !important;
    color:white !important;
}
.status-li:hover {
    background-color: #242424 !important;
}
.date {
    color:#c3c3c3 !important;
}
.chat-data {
    height: 26px !important;
}
.user-description {
    background-image: none !important;
}
.closed-item {
    border: 1px solid #1a1a1a !important;
}
.fadeout {
    background-image: none !important;
}
.item-body {
    background:#242424 !important;
}
.list, .panel, .com {
    background-color:#1a1a1a !important;
    color:white !important;
    
}
}
