css:
@-moz-document regexp("http://endlessforest.org/community/[a-mA-Mo-zO-Z]\\D*\\S*"), regexp("http://www.endlessforest.org/community/[a-mA-Mo-zO-Z]\\D*\\S*") {
/*Body & Header*/
.left-corner{position:relative;top:50px;}
#block-menu-primary-links .content{display:none;}
#header-region .content{display:none;}
body {background: #5a5840 url(https://dl.dropboxusercontent.com/s/vefwsc6lmm4jilq/TEFcHeader-BirchBliss.png) repeat-x;}

/*Sidebar*/
.sidebar{background:#4a4835;border:3px solid #4a4835;position:relative;top:4px;}
.menu{background:transparent}
.sidebar h2{color:#ada97b}
div#sidebar-left.sidebar h3{color:#0c0c09}
#block-user-3{color:#25261e}
div#sidebar-left.sidebar a,div#sidebar-left.sidebar ul.menu a,div#block-user-1.clear-block.block.block-user a{color:#889b69}
div#sidebar-left.sidebar a:hover,div#sidebar-left.sidebar ul.menu a:hover{color:#25261e}

/*Tracker Main*/

#wrapper #container #center .right-corner .left-corner{background:transparent}
#tabs-wrapper{background:#4a4835}
.picture{background:transparent}
th.views-field-last-comment-timestamp,th.views-field-comment-count,th.views-field-name,th.views-field-title,th.views-field-type{background:#4a4835}
thead.tableHeader-processed{background:#4a4835}
td.views-field.views-field-last-comment-timestamp.active{background:transparent}
.pager{background:#25261e}
#tabs-wrapper .tabs .active{background:transparent}
#tabs-wrapper a:hover{background:transparent}
tr.even{background:#373627}
tr.odd{background:transparent}

/*Posts*/
.node{background:#4a4835}
.submitted{background:transparent; color:#ada97b!important}
#comments{background:transparent}
.comment.odd, tr.odd, tr.info{background:transparent}
.comment.even{background:#373627}
.comment.odd{background:transparent}
.new{background:transparent}
div.links, ul.links{background:transparent}
#squeeze h2{color:#ada97b}
div#comments .comment{border-top:2px solid #4a4835;border-bottom:2px solid #4a4835}
div.comment .links{background:transparent}
}

@-moz-document url-prefix("http://www.endlessforest.org/"), url-prefix("http://endlessforest.org/") {
#gon .gms-map-floor {background: url('https://dl.dropboxusercontent.com/s/3yy15cn9i0wre1p/Rut-Map-FLOOR.png'); filter: brightness(0.6);-webkit-filter: brightness(0.6)}
.gms-map-floor {background: url('https://dl.dropboxusercontent.com/s/k163of5eiyv3d6q/Landmark-Map-FLOOR.png'); filter: brightness(0.6);-webkit-filter: brightness(0.6)}
}
