css:
@-moz-document domain("console.aws.amazon.com") {
#awsgnav #nav-shortcutBar a {
    padding: 4px 0;
    display: block;
    /*max-width: 90px;*/
    overflow: hidden;
    margin-right: 0;
}

#awsgnav #nav-shortcutBar .service-label {
    font-weight: normal;
}

#awsgnav #nav-shortcutBar .service-link .service-label {
    font-size: 12px;
    font-family: monaco;
    line-height: 20px;
    max-width: 20px;
    padding-left: 0;
    transform: scaleX(0.80);
}

#awsgnav #nav-shortcutBar .service-link[data-service-id="ta"]  .service-label::before { content: 'TAD ' }
#awsgnav #nav-shortcutBar .service-link[data-service-id="cfo"] .service-label::before { content: 'CFN ' }
#awsgnav #nav-shortcutBar .service-link[data-service-id="cw"]  .service-label::before { content: 'CWA ' }
#awsgnav #nav-shortcutBar .service-link[data-service-id="lam"] .service-label::before { content: 'LAM ' }
#awsgnav #nav-shortcutBar .service-link[data-service-id="r53"] .service-label::before { content: 'R53 ' }
#awsgnav #nav-shortcutBar .service-link[data-service-id="cfr"] .service-label::before { content: 'CDN ' }
#awsgnav #nav-shortcutBar .service-link[data-service-id="elc"] .service-label::before { content: 'ELC ' }
#awsgnav #nav-shortcutBar .service-link[data-service-id="ddb"] .service-label::before { content: 'DYN ' }
#awsgnav #nav-shortcutBar .service-link[data-service-id="cr"]  .service-label::before { content: '£££ ' }
#awsgnav #nav-shortcutBar .service-link[data-service-id="cloudmap"] .service-label::before { content: 'MAP ' }
#awsgnav #nav-shortcutBar .service-link[data-service-id="appmesh"] .service-label { max-width: 25px }
#awsgnav #nav-shortcutBar .service-link[data-service-id="appmesh"] .service-label::before { content: 'MESH ' }
#awsgnav #nav-shortcutBar .service-link[data-service-id="systems-manager"] .service-label::before { content: 'SYS ' }

#awsgnav #nav-home-link #nav-logo {
    margin-left: 5px;
    margin-right: 0;
}

#awsgnav #nav-servicesMenu {
    padding: 12px 4px 11px;
}

#awsgnav #nav-resourceGroupsMenu {
    padding: 12px 4px 11px 0;
}

#awsgnav #nav-shortcutBar {
    padding: 5px 0;
}

#awsgnav .nav-menu-separator {
    display: none !important;
}

#awsgnav #nav-shortcutBar .service-link {
    margin-left: 0;
}

/* Cloudformation cols */

#console-app table.awsui-table-nowrap th,
#console-app table.awsui-table-nowrap td{
    min-width: 50px !important;
}

#console-app table.awsui-table-nowrap th:nth-child(2),
#console-app table.awsui-table-nowrap td:nth-child(2){
    max-width: 200px !important;
    font-size: 13px !important;
}

.awsui awsui-table .awsui-table-header-copy tbody tr td, .awsui awsui-table table tbody tr td {
    word-break: inherit;
}
}
