css:
@-moz-document domain(pintia.cn) {
:root {
    --colorBackground: #121212;
    --colorPrimary: #232323;
    --colorPrimaryVariant: #1c1c1c;
    --colorSecondary: #36c3ff;
    --colorSecondaryVariant: #449a89;
    --colorSuface:  #232323;
    --colorOnSurface: #e1e1e1;
    --colorOnPrimary: #e1e1e1;
    --colorOnSecondary: #e1e1e1;
    --colorDivider: #252525;
    --colorCodeBackground: #1e1e1e;
    --colorCodeText: #7c7c7c;
}

body {
    background-color: var(--colorBackground) !important;
     color:  var(--colorOnPrimary);
}

nav {
    background-color: var(--colorPrimary) !important;
    box-shadow: unset;
}

pre, code, .problemInfo_jpKfZ {
    background-color: var(--colorSuface) !important;
    color: var(--colorOnSurface) !important;
}

a {
    color: var(--colorSecondary)
}

table {
    background: var(--colorPrimary) !important;
    border: 1px solid var(--colorOnPrimary);
}

tr {
    background: var(--colorSuface) !important;
    color: var(--colorOnSurface)
}

tr:hover{
    background-image: unset !important;
    background: unset !important;
}

select, input {
    background-color: var(--colorSuface) !important;
    color: var(--colorOnSurface)!important;
}

label, h5, h3, p, .card-header, span {
    color:  var(--colorOnPrimary)
}

hr {
    border-top: 1px solid #252525;
}

button {
    border: 1px solid var(--colorOnPrimary) !important;
    background: unset !important;
    color: var(--colorOnSurface)!important;
}

.content_-mD_M col-sm-12 {
    color:  var(--colorOnPrimary);
}

.logo_2jKwz {
    fill:  var(--colorOnPrimary)
}

div.sidebar_3aAZX.collapsed_befe2 {
    background-color: #1c1c1c !important; 
}

.problemSetSummaryListItem_1_61u:before{
    background-color:     #474747;
}

.nav-link {
    color: var(--colorOnPrimary) !important;
     background-color: var(--colorPrimaryVariant)!important;
}

.nav-link:hover {
         background-color: #474747!important;
}

.active {
    background-color: #333333 !important;
}

div.insideContainer_14D_s.col {
    background: #121212
}

.card {
    border: 0px;
    background-color: unset !important;
}

.card-body{
    background-color: #232323;
}

.nav-tabs {
    border-bottom-color:    var(--colorDivider);
     color:  var(--colorOnSurface) !important;
}

#splash {
    background: var(--colorBackground);
}

.buttonGroup_3VuWy{
    margin-top: 1rem;
}

.Expandable_3V9Ok {
    background: var(--colorSuface) !important;
}

.mask_1yKqL {
    background: unset;
}

.mask_1yKqL>button{
    border: 0px !important;
}

.dropdown-menu {
    background: var(--colorSuface)
}

.ProblemSetListItem_KG3lO  {
    background: var(--colorPrimary)
}

.ProblemSetListItem_KG3lO svg{
    fill: var(--colorOnPrimary)!important;
}

.ProblemSetListItem_KG3lO a{
    color: var(--colorSecondary)
}

#program {
    background-color:  var(--colorCodeBackground);
}

#program .ace_editor {
    background-color:  var(--colorCodeBackground);
}

#program  .ace_gutter {
    background-color: var(--colorCodeBackground);
}

#program .ace_gutter-cell {
    color: var(--colorCodeText)
}

#program .ace_gutter-active-line{
    background-color: unset;
}
}
