css:
@-moz-document domain('www.drebedengi.ru') {
        /* ГЛОБАЛЬНЫЕ НАСТРОЙКИ */

        /* ********************************************************* */
        /* БЛОК ОСТАТКИ НА СЕГОДНЯ НА СТРАНИЦЕ РАСХОДОВ */

        /* Сохранение ширины всплывающего блока для доп.действий над счетом */
        .rc.box_shadow.popup.dopMenu {
            width: 155px !important;
        }

        /* Уменьшение паддингов в списке счетов в правой колонке */
        div.plname {
            padding-top: 0px !important;
        }

        /* Сдвиг имени группы "Скрытые суммы" влево */
        #pln-3.limited.plname {
            padding-left: 0px !important;
        }

        /* Скрытие иконки треугольника слева от родительских счетов в правой колонке */
        .limited.plname img[id*="arrow"] {
            display: none !important;
        }

        /* Отображение иконки треугольника у родительских счетов в правой колонке */
        .limited.plname > .dash::after {
            content: url("data:img/v3_arr_d.png");
            margin-left: 4px !important;
        }

        /* Сдвиг иконки родительских счетов на место треугольника в правой колонке */
        .limited.plname > img {
            left: 0px !important;
            top: -1px !important;
        }

        /* Сдвиг текста родительских счетов влево */
        .limited.plname > .dash {
            margin-left: 0px !important;
        }


        /* ********************************************************* */
        /* БЛОК КОНТРОЛЬ РАСХОДОВ НА СТРАНИЦЕ ВНЕСЕНИЯ РАСХОДОВ */

        /* Сдвиг имени родительской группы влево */
        #plan_block .limited.plname {
            padding-left: 0px !important;
        }

        /* ********************************************************* */
        /* БЛОК ВНЕСЕНИЯ ТРАТ */

        /* Увеличение отступов между элементами в списке шаблонов расходов */
        .tgtpl {
            margin: 0px 10px 7px 0px !important;
        }

        /* Выделение цветом при выборе шаблона расхода */
        .tgtpl:hover {
            background-color: rgb(238, 238, 238) !important;
        }
        
        /* Увеличение длины подложки для категорий расходов */
        .chosen-container .chosen-drop {
            max-height: 640px !important;
        }

        /* Увеличение длины блока для категорий расходов */
        .chosen-container .chosen-results {
            max-height: 600px !important;
            font-size: 14px !important;
        }
  	
        /* Увеличение ширины блока для счетов при создании траты */
        .rc.box_shadow.popup {
            width: 300px !important;
            padding: 10px 10px !important;
        }

        /* Уменьшение вертикальных отступов в списке категорий при добавлении траты */
        .chosen-container .chosen-results > li {
            padding-top: 3px !important;
            padding-bottom: 3px !important;
        }

        /* Сурытие треугольника слева от родительских счетов в блоке добавления траты */
        .limited.yel > img[id*="arrow_pl"] {
            display: none !important;
        }

        /* Сдвиг картинки счета у родительских счетов в блоке добавления траты */
        .limited.yel > img {
            height: 20px !important;
            width: 20px !important;
            left: 0px !important;
            top: 1px !important;
        }

        /* Сдвиг имени родительского счета в блоке добавления траты */
        .limited.yel > div > .dash {
            margin-left: 25px !important;
        }


        /* ********************************************************* */
        /* БЛОК МЕНЮ */

        /* Уменьшение вертикальных паддингов в блоке меню */
        .lkMenu > div[id*="lkm"],
        .lkMenu > div[class*="active"] {
            padding-top: 5px !important;
            padding-bottom: 5px !important;
        }

        /* Сдвиг треугольника родительских пунктов меню (закрытое состояние) в блоке меню */
        .lkMenu .right {
            background-position-x: 10px !important;
            background-position-y: 12px !important;
        }

        /* Сдвиг треугольника родительских пунктов меню (открытое состояние) в блоке меню */
        .lkMenu .down {
            background-position-x: 10px !important;
            background-position-y: 12px !important;
        }

        /* Скрытие неиспользуемых пунктов меню */
        .lkMenu #lkm2,  /* Накопления, список покупок */
        .lkMenu #lkm12, /* Темы органайзера */
        .lkMenu #lkm14, /* Напоминания */
        .lkMenu #lkm16, /* Электронный органайзер */
        .lkMenu #lkm19  /* Партнерская программа */ {
            display: none !important;
        }

        /* ****************************************************************** */
        /* БЛОК НАСТРОЙКИ КАТЕГОРИЙ РАСХОДОВ */

        /* Уменьшение шрифта блока настройки категорий расходов */
        #tab_set_waste_div {
            font-size: 14px !important;
        }

        /* Уменьшение вертикальных отступов в списке категорий расходов в блоке настройки категорий расходов */
        #tab_set_waste_div .treeItem {
            padding-top: 2px !important;
            padding-bottom: 0px !important;
            margin-top: 0px !important;
        }

        /* Сдвиг трегуольника у родительских групп в списке категорий расходов в блоке настройки категорий расходов */
        #tab_set_waste_div img.arrowRest {
            padding: 5px 5px !important;
        }

        /* ****************************************************************** */
        /* БЛОК НАСТРОЙКИ КАТЕГОРИЙ ДОХОДОВ */

        /* Уменьшение шрифта блока настройки категорий доходов */
        #tab_set_income_div {
            font-size: 14px !important;
        }

        /* Уменьшение вертикальных отступов в списке категорий доходов в блоке настройки категорий доходов */
        #tab_set_income_div .treeItem {
            padding-top: 2px !important;
            padding-bottom: 0px !important;
            margin-top: 0px !important;
        }

        /* Сдвиг трегуольника у родительских групп в списке категорий доходов в блоке настройки категорий доходов */
        #tab_set_incove_div img.arrowRest {
            padding: 6px 5px !important;
        }

        /* ****************************************************************** */
        /* БЛОК НАСТРОЙКИ СЧЕТОВ */

        /* Уменьшение шрифта блока настройки счетов */
        #tab_set_place_div {
            font-size: 14px !important;
        }

        /* Расширение блока для дерева счетов в блоке настройки счетов */
        #tree.tree {
            width: 270px !important;
            margin-left: -5px !important;
        }

        /* Уменьшение вертикальных отступов в списке счетов в блоке настройки счетов */
        #tab_set_place_div .treeItem {
            padding-top: 0px !important;
            padding-bottom: 0px !important;
        }

        /* Сдвиг треугольника у родительских групп в списке счетов в блоке настройки счетов */
        #tab_set_place_div img[id*="arrow"] {
            margin-left: -30px !important;
            padding-right: 10px !important;
        }

        /* Сдвиг влево счетов в списке счетов в блоке настройки счетов */
        #tab_set_place_div .treeItem {
            margin-left: -10px !important;
        }

        /* Сдвиг влево дочерних элементов относительно родительских в списке счетов в блоке настройки счетов */
        #tab_set_place_div div[id*="place"] div[id*="par"] {
            margin-left: 15px !important;
        }

        /* ****************************************************************** */
        /* БЛОК СОЗДАНИЯ ПОЛЬЗОВАТЕЛЬСКИХ ПРАВИЛ */

        /* Уменьшение шрифта блока настройки категорий расходов в блоке создания пользовательских правил */
        #tab_set_rules_div {
            font-size: 14px !important;
        }

        /* Убрать сплошное подчеркивание счетов в блоке создания пользовательских правил */
        #tab_set_rules_div .limited.yel a[id*="rules_from"] {
            text-decoration: none !important;
        }

        /* Скрытие треугольника у родительских счетов в блоке создания пользовательских правил */
        #tab_set_rules_div .limited.yel img[id*="arrow"] {
            display: none !important;
        }

        /* Сдвиг иконки счета влево в блоке создания пользовательских правил */
        #tab_set_rules_div .limited.yel img {
            left: 0px !important;
        }

        /* Сдвиг наименование группы счетов влево в блоке создания пользовательских правил */
        #tab_set_rules_div .limited.yel .dash {
            margin-left: 30px !important;
        }

        /* Сдвиг наименование группы "Скрытые суммы" влево в блоке создания пользовательских правил */
        #tab_set_rules_div .limited.yel .dash[onclick*="id_-3"] {
            margin-left: 0px !important;
        }

        /* Сдвиг наименование группы "Выберите, пожалуйста счет" влево в блоке создания пользовательских правил */
        #tab_set_rules_div .limited.yel #rules_from_id_text.dash {
            margin-left: 0px !important;
        }

        /* Отображение треугольника у родительских групп слева в блоке создания пользовательских правил */
        #tab_set_rules_div .limited.yel .dash::after {
            content: url("data:img/v3_arr_d.png") !important;
            padding-left: 5px !important;
        }

        /* ****************************************************************** */
        /* ОБЩЕЕ */

        /* Уменьшение вертикальных паддингов и свдиг влево у элементов списка в блоках:
        Шаблоны, Валюты, Теги, Долги и кредиты */
        #tab_set_tpl_div div.treeItem, /* Шаблоны */
        #tab_set_currency_div div.treeItem, /* Валюты */
        #tab_set_duty_div div.treeItem, /* Долги, кредиты */
        #tab_set_tag_div div.treeItem /* Теги */ {
            margin: 0px !important;
            margin-left: 3px !important;
            padding: 2px !important;
        }
}
