a[href="#bottom"]:hover:before, #tobottom span:hover::before {background: var(--btn-bg-hover);}
a[href="#bottom"]::after, #tobottom span::after {font: bold 26px/27px FontAwesome; height: 27px; top: unset; bottom: 0; left: unset; left: 30px;}

.pageJump a {color: var(--alt-text) !important;}
.pageJump a:hover {color: var(--link) !important;}

#settingsWindowLinkMobile {font-size: 0;}
#settingsWindowLinkMobile::before {content: "\f013"; font: 20px/18px FontAwesome;}
.pageJump a[target="_top"] {font-size: 0;}
.pageJump a[href="//p.4chan.org/"] {font-size: 0;}
.pageJump a[href="//p.4chan.org/"]::before {content: "\f10b"; font: 20px/18px FontAwesome;}
.pageJump a[target="_top"]::before {content: "\f015"; font: 20px/18px FontAwesome;}

/* Setting */
.UIPanel {line-height: unset;}
.UIPanel > div {border: none !important; border-radius: 0; left: 0; margin-left: 0; width: 100%; vertical-align: unset; height: 100%;}
.panelHeader .panelCtrl::before {padding: 5px 0;}
.panelHeader .panelCtrl img {height: 24px;}
#settingsMenu > div {top: 0; border: none !important; max-height: 100%;}

/* Nav Buttons */

.button {background-color: var(--btn-bg); background-image: none; border-color: var(--border); color: var(--text) !important; transition: .1s;}
.button:hover {background-color: var(--btn-bg-hover);}
.button a {color: var(--text) !important;}
.button a, a.button:hover, .button a:visited {color: var(--text) !important;}
.button a:hover, a.button:hover {color: var(--text) !important;}

/* Posts */
div.board>hr {border-top-color: var(--border);}
hr + .thread {border-top: none;}

div.opContainer {background-color: var(--post-bg); border-color: var(--border) !important; border-left: none; border-right: none;}
div.op {border: none; box-shadow: none; padding: 0;}
.op .backlink.mobile, #quote-preview .backlink.mobile {display: block !important;}
div.post {margin: 0; border-radius: 0;}

div.post div.postInfoM {background-color: unset; border-bottom: none; font-size: .92em;}
.yotsuba_new .postMenuBtn, .futaba_new .postMenuBtn, .postMenuBtn {color: var(--alt-text-2) !important;}
div.post div.postInfoM span.nameBlock span.name, div.postInfo span.nameBlock span.name {color: var(--alt-text-2); font-weight: 400;}
div.post div.postInfoM span.subject {color: var(--title);}
div.post div.postInfoM span.dateTime {color: var(--alt-text-2) !important;}
div.post div.postInfoM span.postNum a, div.postInfo span.postNum a {color: var(--alt-text) !important;}
div.post div.postInfoM span.postNum a:hover {color: var(--link) !important;}
span.postNum::before {margin-right: .5em;}

.mFileInfo {color: var(--alt-text-2) !important;}

div.postLink {background-color: var(--post-bg); border-top-color: var(--border);}
div.postLink span.info {color: var(--text);}
div.postLink a {color: var(--text) !important;}

.is_index div.thread {padding: 0;}
.is_index div.opContainer {margin: 0;}
.is_index div.replyContainer {margin: 0;}

div.replyContainer {background-color: unset; margin: 0;}
div.reply {border-top: none !important; border-left: none !important; border-right: none !important; border-color: var(--border) !important;}
.post.reply {margin: 0;}
.backlink.mobile {background-color: unset; border-top-color: var(--border) !important;}

/* Quick Reply */
#quickReply {border: none !important; border-radius: 0; box-sizing: border-box; top: 0 !important; height: calc(100vh); left: 0; margin-left: 0; overflow: auto; padding: 4px; position: fixed; width: 100%; z-index: 10000;}
#qrepHeader, #qrHeader {padding: 4px;}
#quickReply input[type="text"], #quickReply textarea, .extPanel input[type="text"], .extPanel textarea {font-size: unset;}

/* Catalog */
.futaba_new #boardNavMobile, .yotsuba_new #boardNavMobile, .burichan_new #boardNavMobile, .yotsuba_b_new #boardNavMobile {background-color: var(--post-bg); border-bottom-color: var(--border); border-bottom-width: 1px;}

.panel {background-color: var(--post-bg); border-radius: 3px;}

.yotsuba_b_new .mobilebtn .btn-wrap, .burichan_new .mobilebtn .btn-wrap, .yotsuba_new .mobilebtn .btn-wrap, .futaba_new .mobilebtn .btn-wrap {background-color: var(--btn-bg); background-image: none; border-color: var(--border); transition: .1s;}
.yotsuba_b_new .mobilebtn .btn-wrap .button, .burichan_new .mobilebtn .btn-wrap .button, .yotsuba_new .mobilebtn .btn-wrap .button, .futaba_new .mobilebtn .btn-wrap .button {background-color: unset;}
.yotsuba_b_new .mobilebtn .btn-wrap:hover, .burichan_new .mobilebtn .btn-wrap:hover, .yotsuba_new .mobilebtn .btn-wrap:hover, .futaba_new .mobilebtn .btn-wrap:hover {background-color: var(--btn-bg-hover);}
}
}

