html, body {font-family: 'Ubuntu', sans-serif !important;}

body {max-width: 980px; margin: 0 auto; padding: 0 5px 0 35px;}
@media only screen and (min-width: 1480px) and (orientation:portrait),  only screen and (min-width: 1480px) and (orientation:landscape){
body {max-width: 1080px; margin: 0 auto; padding: 0 5px 0 35px;}
}
@media only screen and (min-width: 1680px) and (orientation:portrait),  only screen and (min-width: 1680px) and (orientation:landscape){
body {max-width: 1280px; margin: 0 auto; padding: 0 5px 0 35px;}
}
@media only screen and (min-width: 1880px) and (orientation:portrait),  only screen and (min-width: 1880px) and (orientation:landscape){
body {max-width: 1480px; margin: 0 auto; padding: 0 5px 0 35px;}
}
