css:
@-moz-document domain(slickdeals.net) {
	/* your code goes here */

body,.categoryBarPlaceholder {
    background:#f6f6f6;
}

/*ads*/
#giveawayItem, #crt-adblock-a, #crt-adblock-b, .featuredCouponsDiscounts, .relatedArticle, .topCouponsRCM, .abc, #ad-container,  .sideGridBox.ad {
    display:none !important;
}
#fpMainContent .gridCategory .fpGridBox {
    border:1px solid #ddd;
    border-radius:2px;
    padding:10px 0 ;
}
.viewDetailsBtn, #fpSettingsButton, #fpSettingsButton,#fpNewsAndArticles,.gridCategory[data-module-name="Top Categories"],#bottom-728x90-970x250 {
    display:none !important;
}
@media (min-width: 1557px) {
#pageContent {
    padding-top: 23px;
}
}
#middleBannerAd {
    display:none;
}
#sideColumn .popularDeals,#sideColumn .trendingDeals, .box.popularDeal {
    border: 1px solid #ddd;
    border-radius:0;
}
.globalSearch input.searchBox {
    height: 23px;
    font-size: 12px;
}
.globalSearch .icon-search2 {
    font-size:15px;
}
.sideGridBox .subBoxContent .info .rating .ratingFull .icon {
    color: #b6b6b6;
}
.sideGridBox .subBoxContent .info .rating .ratingPartial .icon {
    color: #dfdfdf;
}
.sideGridBox .subBoxContent .info .icon {
    color: #db9090;
}
.sideGridBox .subBoxContent .info .votes {
    background-color: #e5e5e5;
}
.sideGridBox h3 {
    background:#f5f5f5;
}
.globalSearch {
    border-radius:1px;
}

#mainColumn #fpMainContent .gridCategory:not([data-module-name='Featured Deals']):not(#fpNewsAndArticles) ul.gridDeals .fpGridBox .fpItem .itemImageAndName .imageContainer img, #mainColumn #fpMainContent .gridCategory:not([data-module-name='Featured Deals']):not(#fpNewsAndArticles) ul.gridDeals .fpGridBox .fpItem .itemImageAndName .smallerImage img, #mainColumn #fpMainContent #featureDealsAndCoupons .fpGridBox .fpItem .itemImageAndName .imageContainer img {
    display: block;
    max-width: 100% !important;
    height: auto;
    padding:10px;
}
.rebateBadgeGrid {
    top: -10px;
}
@media (max-width: 1115px) {
#mainColumn #fpMainContent .gridCategory:not([data-module-name='Featured Deals']):not(#fpNewsAndArticles) ul.gridDeals .fpGridBox .fpItem .itemImageAndName .imageContainer, #mainColumn #fpMainContent .gridCategory:not([data-module-name='Featured Deals']):not(#fpNewsAndArticles) ul.gridDeals .fpGridBox .fpItem .itemImageAndName .smallerImage {
    width: 100% !important;
    height: 100% !important;
}
    }
@media (max-width: 1115px) {
#mainColumn #fpMainContent .gridCategory:not([data-module-name='Featured Deals']):not(#fpNewsAndArticles) ul.gridDeals .fpGridBox .fpItem {
    height:auto !important;
}
}
@media (min-width: 1116px) {
ul.dealTiles.gridDeals {
    grid-template-columns: repeat(5, minmax(0, 1fr));
}
}
@media (min-width: 1389px) {
ul.dealTiles.gridDeals {
    grid-template-columns: repeat(6, minmax(0, 1fr));
}
}
@media (min-width: 1556px) {
ul.dealTiles.gridDeals {
    grid-template-columns: repeat(7, minmax(0, 1fr));
}
}
@media (min-width: 1557px) {
#mainColumn #fpMainContent .gridCategory:not([data-module-name='Featured Deals']):not(#fpNewsAndArticles) ul.gridDeals .fpGridBox .fpItem .itemImageAndName .imageContainer {
        width: 100% !important;
    height: 100% !important;
}
}
@media (max-width: 1235px) {
#mainColumn #fpMainContent .gridCategory ul.gridDeals .fpGridBox .fpItem {
    height: auto;
}
    }
.gridSeparator {
    display:none;
}
#globalBar .user {
    line-height:0;
}
.postADealBtnRight .button {
    width: 100%;
    padding: 15px;
    height: 100%;
}
.categoryBar.stickyHeader {
    margin-top: 32px;
}

.postADealBtnRight .button {
    height:22px;    
}
@media (min-width: 1557px) {
#mainColumn #fpMainContent .gridCategory:not([data-module-name='Featured Deals']):not(#fpNewsAndArticles) ul.gridDeals .fpGridBox .fpItem {
    
    height:auto !important;
    }

.categoryBar.stickyHeader {
    margin-top: 28px;
}
.footer, .footerBottom{
    display:none;
}


@media (max-width: 1115px) {
.globalSearch.btnLeft, .globalSearch.btnRight {
    margin: 0 20px 0 40px;
}
}
.globalSearch input.searchBox {
   height: 30px;
}
#fpNewsAndArticles, .gridCategory:last-child, .inlineStoreCoupons {
    display:none;
}
}
@media (min-width: 1221px) {
#mainColumn #fpMainContent .gridCategory:not([data-module-name='Featured Deals']):not(#fpNewsAndArticles) ul.gridDeals .fpGridBox .fpItem {
    height: auto !important;
}
    }

@media (min-width: 1221px) {
#mainColumn #fpMainContent .gridCategory:not([data-module-name='Featured Deals']):not(#fpNewsAndArticles) ul.gridDeals .fpGridBox .fpItem .itemImageAndName .imageContainer {
    width: 100% !important;
    height: 100% !important;
}
}
#fpMainContent .gridCategory ul.gridDeals .fpGridBox .fpItem .itemImageAndName .imageContainer {
    width: auto !important;
    height: 75px !important;
}

#mainColumn #fpMainContent #featureDealsAndCoupons .fpGridBox .fpItem {
    height: 200px;
}
.inner {
    display:none;
}
}
