css:
@-moz-document domain("danluu.com") {
body {
    margin-left: auto;
    margin-right: auto;
    width: 50em;
    text-align: justify;
    font-family: sans-serif;
    font-size: 15px;
    line-height: 135%;
    margin-top: 40px;
}

p {
    margin-block-start: 0em;
    margin-block-end: 1em;
}

h3 {
    font-size: 1.75em;
    font-weight: 100;
    margin-block-start: 2em;
}

h4 {
    font-size: 1.1em;
    margin-block-end: 0.5em;
}

pre {
    background: #f7f7f7;
    padding: 12px 24px;
    border-radius: 6px;
    border: 1px solid #e2e2e2;
    color: #383838;
}

a {
    color: #0068d6;
}

a:hover {
    color: #d68f00;
}

body > strong {
    font-size: 1.8em;
    font-weight: 100;
    color: #2b2b2b;
    display: inline-block;
    margin-top: 50px;
}


/* Navigation */
.np {
    /* Next/Previous buttons*/
    display: block;
    background: #404040;
    position: fixed;
    top: 0px;
    left: 0px;
    width: 100%;
    padding: 0px;
}

.np:nth-of-type(1) a:nth-of-type(2) {
    /* Next Button */
    float: right;
}


.np:nth-of-type(2) {
    /* Main Nav at Top */
    top: 0px;
    margin-left: auto;
    margin-right: auto;
    width: 50em;
    background: #2f2f2f;
    left: initial;
    font-style: normal;
}

.np a {
    color: white;
    text-decoration: none;
    padding: 10px 20px;
    background: #3b5575;;
    margin: 0px;
    display: inline-block;
}

.np a:hover {
    background:#9f6700;
}

p img {
    margin-left: auto;
    margin-right: auto;
    display: inherit;
    width: 80%;
}

hr {
    margin-bottom: 30px;
    visibility: hidden;
}

/* Tables */

table, td, th {
    margin-bottom: 1.5em;
}

td, th {
    padding: 2px 6px;
}

tr th {
    background: #353535;
    font-weight: normal;
    text-transform: capitalize;
    color: #f3f3f3;
}
}
