css:
@-moz-document domain("medialeaks.ru") {
body {
    background-color: #222;
}
.news-feed-item__title,.news-feed-date,.ml-new-post__heading,.-ns-post-date,.post-content a,
.post-content a:visited, .plista_widget_belowArticle_firstBlock .plistaHeadline,.native_itemTitle,.post-content,.wp-caption-text,.readmore-index__moreitemTitle,.readmore__themeitemTitle{
    color: #fff !important;
}

.post_tags a {
    color: #ffdc00 !important;
    transition: 0.2s;
}
.post_tags a:hover,
.post_tags a:visited:hover {
    color: #fff !important;
    background: none;
    transition: 0.2s;
}

.post-content a,
.post-content a:visited {
    color: #aaa;
}

.post-content a,
.post-content a:visited {
    color: #fff
}

.-ns-quote::before,
.-ns-quote::after,
.post_content blockquote::before,
.post_content blockquote::after {
    background-color: #222;
}

.seealso,
.seealso:visited {
    background-color: #222;
}

.seealso div:after,
.seealso:visited div:after {
    display: none;
}

.twitter_widget {
    background-color: #222;
}

.author_avatar a {
    color: #000;
}

.most-readable {
    background-color: #222;
}

.plista_widget_belowArticle_firstBlock {
    background-color: #222;
    color: #fff;
}


.native_itemTitle {
    text-decoration: none !important;
}

.plista_widget_belowArticle_firstBlock .itemTitle {
    color: #fff;
    border: none !important;
}

.native_block .native_link {
    border: none;
}

.native_block {
    background-color: #222;
}

.plista_widget_belowArticle_second {
    background-color: #222;
}

.plista_widget_belowArticle_second .itemTitle {
    color: #fff;
}

.plistaHeadline {
    color: #fff !important;
}

.itemTitle {
    color: #fff !important;
    border: none !important;
}

.plista_widget_belowArticle_firstBlock .itemLink,
.plista_widget_belowArticle_second .itemLink {
    color: #ffcd00;
    border: none;
}

.breadcrumbs,
.breadcrumbs a {
    color: #fff;
}

.social-qoute_widget {
    background-color: #222;
    color: #fff;
}

.social-qoute_widget h3 {
    color: #fff;
}

.-nsb-heading {
    color: #fff;
}

.new_didgest {
    border-top-color: #fff;
}

.-nsb-item-tags {
    color: #ccc;
}

.posts-list {
    background-color: #222;
    color: #fff;
}

.mainpage-desktop_subnav {
    color: #fff !important;
}

div.post-news_caption > p {
    color: #fff !important;
}

.page-title {
    color: #fff;
}

.-np-heading {
    color: #fff;
}

.-np-description {
    color: #ccc;
}

.material-title__sub{
    color: #fff;
}

.top-posts__items a{
    color: #fff;
}

.top-posts__items a:hover{
    text-decoration: underline #ffcd00; transition: 0.2s;
}


::-webkit-scrollbar {
    width: 8px;
    height: 8px;
}
::-webkit-scrollbar-track {
  opacity: 0.6;
  background-color: #555;
	transition: 0.4s;
	-moz-transition: 0.4s;
	-webkit-transition: 0.4s;
}
::-webkit-scrollbar-thumb {
    background-color: #eee;
	transition: 0.4s;
	-moz-transition: 0.4s;
	-webkit-transition: 0.4s;
}
::-webkit-scrollbar-thumb:hover {
    background-color: #aaa;
		transition: 0.4s;
	-moz-transition: 0.4s;
	-webkit-transition: 0.4s;
}

.readmore__themeitemTitle:after{
    background-image: -webkit-gradient(linear,left top,left bottom,from(hsla(0,0%,100%,0)),to(#222));
    background-image: linear-gradient(hsla(0,0%,100%,0),#222 100%);
}
}
