css:
@-moz-document domain(satori.tcs.uj.edu.pl) {
body {
    font-family: Arial;
    background: #252525;
    color: #dcdcdc;
}
#navigationPanel a,
#navigationPanel a:visited,
#navigationPanel a:active {
    color: #dcdcdc;
    display: block;
    text-decoration: none;
    font-family: Arial;
}
a.stdlink {
    color: #9ee8ff;
    text-decoration: none;
}
.reference{
	color: #9ee8ff !important;
}
body.mainsphinx a, body .mainsphinx a:visited {
    color: rgb(0, 195, 255);
}
.mainsphinx pre {
    padding: 5px;
    background-color: #0e0e0e;
    color: #86ff4a;
    line-height: 120%;
    border: 1px solid #848484;
    border-left: none;
    border-right: none;
}
.mainsphinx .highlight .mi {
    color: #66ff1b;
}
.highlight_back {
    display: block;
    background-color: #0e0e0e;
    color: #66ff1b;
}
.highlight_pos {
    display: block;
    background-color: hsl(146, 78%, 24%);
    font-style: italic;
}
.highlight_neg {
    display: block;
    background-color: hsla(210, 75%, 40%, 1);
    font-style: italic;
}
.button {
    background-color: hsl(240, 35%, 22%);
    color: hsl(194, 0%, 86%);
    -moz-border-radius: 5px;
    border-radius: 5px;
    border: 2px solid #134c86;
	margin-right: 4px;
}
.dataTables_wrapper .dataTables_length,
.dataTables_wrapper .dataTables_filter,
.dataTables_wrapper .dataTables_info,
.dataTables_wrapper .dataTables_processing,
.dataTables_wrapper .dataTables_paginate {
    color: #dcdcdc !important;
}

/* FOR ADMINIS */
input.transparent_box
{
    color : #dcdcdc;
}
/*end of for admins */

/* TABLES */
.mainsphinx table.docutils td, .mainsphinx table.docutils th {
    padding: 1px 8px 1px 5px;
    border-top: 0;
    border-left: 0;
    border-right: 0;
    border-bottom: 5px solid #252525;
}
.mainsphinx th {
    background-color: #3a3a3a;
}
table.results tr:nth-child(2n+1) {
    background-color: #232750;
}
table.results tr:nth-child(2n+2) {
    background-color: #2e2e6d;
}
.mainsphinx table.docutils tr:nth-child(even) td {
    background-color: #383894 !important;
    color: #dcdcdc;
}
.mainsphinx table.docutils tr:nth-child(odd) td {
    background-color: #23235c !important;
    color: #dcdcdc;
}

table.results th {
    background-color: #3a3a3a;
    font-weight: bold;
    color: #FFFFFF;
}
table.results tr.satoriEnhancementsEven td{
    background-color: #2e2e6d !important;
    padding-left: 4px;
}
table.results tr.satoriEnhancementsOdd td{
    background-color: #232750 !important;
    padding-left: 4px;
}
table.results td {
    padding-left: 4px;
}
table.docutils {
	font-weight: bold;
}

.mainsphinx table.docutils tr:nth-child(even) td {
    background-color: #383894 !important;
    color: #dcdcdc;
}

.mainsphinx table.docutils tr:nth-child(odd) td {
    background-color: #23235c !important;
    color: #dcdcdc;
}

div.ranking table tbody tr:nth-child(2n+1) > td {
    background-color: #797979 !important;
    color: #ececec;
}
div.ranking table tbody tr:nth-child(2n) > td {
    background-color: #616161 !important;
    color: #ececec;
}
div.ranking table tfoot > tr {
    background-color: rgb(49, 49, 49) !important;
    color: #ececec;
}
.DTFC_LeftBodyLiner{
	display: none;
}
/* end of tables */
/*  STATUS   */
.staOK {
    background-color: #47a300;
    color: #FFFFFF;
}

.staACC {
    background-color: #43791A;
    color: #FFFFFF;
}
.staRUL {
    background-color: #47229d;
    color: #FFFFFF;
}
.staRTE {
    background-color: #cf173c;
    color: #FFFFFF;
}

.staTLE {
    background-color: #ea6709;
    color: #FFFFFF;
}
.staEXT {
    background-color: #393939;
    color: #FFFFFF;
}

.staANS {
    background-color: #e40000;
    color: #FFFFFF;
}
.staNone{
    background-color: #ff28e3;
    color: #FFFFFF;
}
.staMEM {
    background-color: #db008c;
    color: #FFFFFF;
}

.staREJ {
    background-color: #716743;
    color: #FFFFFF;
}

.staCME {
    background-color: #765182;
    color: #FFFFFF;
}

.staQUE {
    background-color: #f3b900;
    color: #FFFFFF;
}
div.wheel {
    width: 90%;
    margin: 1em;
    padding: 0.3em;
    background-color: #3a3a3a;
}

table.results tr.satoriEnhancementsEven td.satori-enhancements-status-ok {
    background-color: #4aab00 !important;
    color: #FFFFFF;
}
table.results tr.satoriEnhancementsOdd td.satori-enhancements-status-ok {
    background-color: #4aab00 !important;
    color: #FFFFFF;
}
table.results tr.satoriEnhancementsEven td.satori-enhancements-status-err {
    background-color: #f00 !important;
    color: #FFFFFF;
}
table.results tr.satoriEnhancementsOdd td.satori-enhancements-status-err {
    background-color: #f00 !important;
    color: #FFFFFF;
}

table.results tr.satoriEnhancementsEven td.satori-enhancements-status-que {
    background-color: #f3b900 !important;
    color: #FFFFFF;
}
table.results tr.satoriEnhancementsOdd td.satori-enhancements-status-que {
    background-color: #f3b900 !important;
    color: #FFFFFF;
}
ul.errorlist {
    font-size: 0.8em;
    background-color: #ff0048;
    margin-bottom: 0em;
}

.tdpos {
    display: block;
    background-color: #7ede34;
    width: 90%;
  color: #000;
}
.tdneg {
    display: block;
    background-color: #ec1754;
    width: 90%;
    color: #000;
}
/* end of status */

/* LEFT MENU */
.col2 {
    border-right: 1px dotted hsl(0, 0%, 63%);
    filter : invert(100%) hue-rotate(0deg) saturate(0.6);
    filter : invert(100%) hue-rotate(190deg) saturate(8);
    
}

#navigationPanel {
    margin-top: 0;
    padding: 0;
    text-transform: uppercase;
    letter-spacing: 1px;
    top: 0px;
    filter : invert(100%);
}
#satoriEnhancementsTCSLogo {
    padding-top: 24px;
    padding-right: 32px;
    text-align: center;
    filter : invert(00%);
}
/* end of left menu */

/*-----QUESTIONS PANEL---------*/
.question_public .question_header {
    font-style: italic;
    padding: 5px;
    background-color: #3a3a3a;
}
.question_private .question_header {
    font-style: italic;
    padding: 5px;
    background-color: hsl(222, 83%, 15%);
}
.question_content {
    padding: 4px 10px;
    margin-top: 3px;
    font-weight: bold;
    background-color: #134c86;
}
.question_answer {
    margin-top: 3px;
    padding: 4px 50px;
    background-color: #0d3359;
}
.tdpnd {
    display: block;
    background-color: #ffd33a;
    width: 90%;
    color: #000;
}
/*end of questions panel*/
/* FIREFOX STUFF */
@supports (-moz-appearance:none){

}/*end of firefox only stuff*/
}
