css:
@-moz-document domain("reply.buffer.com") {
/* loading blur */
.conversation-list-container--loading {
    opacity: 0.90;
}

/* left sidebar */
.sidebar {
    background-color: #2F3136;
    padding: 10px;
}

.sc-fYxtnH.dPDCOc {
    background-color: #40444b;
}

.sc-hmzhuo.gWtPTU > div > label {
    color: #8e9297 !important;
}

.sc-fYxtnH:hover > div > label {
    color: #DCDDDE !important;
}

.sc-tilXH {
    color: #F27839;
}


/* search */
#searchInput {
    background-color: #484B51;
    color: #DCDDDE;
    border-color: #4A4A4A;
}

.ico-cross {
    color: #2F3136;
}

.search,
.search__arrow {
    background-color: #36393F;
    border-color: #4A4A4A;
}

.search__items {
    padding: 0;
}

.search__item:hover {
    background-color: #42464D;
}

.search__item {
    border-bottom: 1px solid #4A4A4A;
}

.search__contact,
.search__channel-icon {
    color: #3CA3CC;
}

.search__label {
    color: #DCDDDE;
}

.search__timestamp {
    color: #F27839;
}

.search__results-footer {
    background-color: #2F3136;
    border-color: #4A4A4A;
}

/* list */
.conversation-list-container {
    background-color: #202225;
    padding: 0 10px;
}
.conversation-list__container {
    overflow: hidden;
}

.conversation-list-body {
    background-color: #202225;
}

.conversation-list-header,
.pagination {
    background-color: #2F3136;
    border-color: #4A4A4A;
    color: #DCDDDE;
}

.pagination__link {
    color: #F27839;
}
.conversation-list-item {
    background-color: #36393F !important;
    border-color: #4A4A4A;
}

.conversation-list-item--selected:before {
    background-color: #3CA3CC;
}

.conversation-list-item__name,
.conversation-list-item__timestamp {
    color: #3CA3CC;
}

.conversation-list-item--pending {
    background-color: #2F3136;
}

.conversation-list-item--pending > .conversation-list-item__body > .conversation-list-item__name,
.conversation-list-item--pending > .conversation-list-item__timestamp {
    color: #F27839;
}

.conversation-list-item__preview,
.conversation-list-item__type,
.conversation-list-item__assigned-name {
    color: #DCDDDE;
}

.user-presence--list {
    background-color: rgb(0, 0, 0, 0.2);
}

.user-presence--list .user-presence__avatar {
    box-shadow: 0 0 85px 220px hsla(0, 0%, 100%, .2);
}

.user-presence--list .user-presence__typing-indicator {
    background-color: #3CA3CC;
}

/* conversation page */
.conversation,
.conversation-detail-left {
    background-image: none;
    background-color: #202225;
}

.conversation--beginning:after {
    border-color: #4A4A4A;
}

.conversation--beginning__button {
    background-color: #2A2C31;
    color: #DCDDDE;
}

.conversation-thread-item__name {
    color: #DCDDDE;
}

.conversation-thread-item__timestamp {
    color: #3CA3CC !important;
}

.conversation-thread-item__favorite:hover,
.conversation-thread-item__replyTo:hover,
.conversation-thread-item__translate:hover,
.conversation-thread-item__more:hover {
    background-color: #42464D;
}

.conversation-detail-left--closed,
.conversation-detail-left--closed > .conversation {
    background-color: #2A2C31;
}

/* tweet conversation */
.conversation-thread-item-tweet .conversation-thread-item__message,
.conversation-thread-item--dm .conversation-thread-item__message {
    background-color: #36393F;
}

.conversation-thread-item--agent .conversation-thread-item__message,
.conversation-thread-item--agent.conversation-thread-item--dm .conversation-thread-item__message,
.conversation-thread-item--agent.conversation-thread-item--selected .conversation-thread-item__message {
    /*background-color: #2F3136;*/
    background-color: #36393F;
}

.conversation-thread-item .conversation-thread-item__media,
.conversation-thread-item .conversation-thread-item--quoted,
.conversation-thread-item .conversation-thread-item--quoted .conversation-thread-item__message {
    background-color: #484B51;
}

.conversation-thread-item .conversation-thread-item--quoted a {
    color: #3CA3CC;
}

.conversation-thread-item .conversation-thread-item__message {
    border: 1px solid #4A4A4A;
}

.conversation-thread-item--dm .conversation-thread-item__message {
    border: 1px solid #3CA3CC;
}

.conversation-thread-item--note .conversation-thread-item__message {
    background-color: #484B51;
    border-color: #F27839;
}

.conversation-thread-item--selected .conversation-thread-item__message {
    box-shadow: none;
}

.conversation-thread-item__text,
.conversation-thread-item--note .conversation-thread-item__text {
    color: #DCDDDE;
}

.conversation-thread-item__text a,
.conversation-thread-item__text .js-display-url,
.conversation-thread-item__text .tco-ellipsis {
    color: #3CA3CC !important;
}

/* contract profile */
.contact-profile {
    background-color: #2F3136;
    border-color: #4A4A4A;
}
.contact-profile > * {
    color: #DCDDDE;
    border-color: #4A4A4A;
}
.contact-profile__head,
.contact-profile__links,
.contact-profile__stats,
.contact-profile__description,
.contact-profile__conversation-link-item {
    color: #DCDDDE;
    border-color: #4A4A4A;
}

.contact-profile__note {
    background-color: #484B51;
    color: #DCDDDE;
    border-color: #4A4A4A;
}

.contact-profile__section-label {
    color: #3CA3CC !important;
}

/* messsage composer */
.composer {
    background-color: #2F3136;
}

.composer-wrapper,
.composer-replyto-wrapper {
    border-color: #4A4A4A;
}

.composer-textarea-wrapper {
    padding: 5px;
    background-color: #36393F;
}

.composer-handles__label {
    color: #DCDDDE;
    border-color: #4A4A4A;
}

.composer-handles__handles {
    color: #3CA3CC;
}

.composer-input-wrapper {
    background-color: #484B51;
    border-radius: 5px;
}

.composer-input {
    color: #DCDDDE;
}

.composer__account-switcher {
    margin: 10px;
}

.composer-footer-wrapper {
    background-color: #36393F;
    padding: 5px 0;
}

.popup__notes,
.popup__notes .popup__arrow {
    background-color: #36393F;
    border-color: #F27839;
}

.popup__notes textarea {
    background-color: #484B51;
    color: #DCDDDE;
}

.emoji-dialog {
    background-color: #DCDDDE;
    border: 1px solid #4A4A4A;
}
}
