css:
@-moz-document regexp(".*?blog\\.csdn\\.net.*?") {
/*
* 清理 CSDN 博客
*/
.hide-article-box,
.recommend-box,
.meau-list,
#adContent,
#csdn-toolbar,
aside,
#reportContent,
.recommend-right,
.pulllog-box,
div[class="opt-box text-center"],
a[data-type="report"],
a[data-type="readreply"],
.comment-like,
a[data-type="vip"],
a[data-type="app"],
.login-mark,
.login-box,
.hljs-button,
.option-box[data-type="regard"],
.option-box[data-type="cs"],
.option-box[data-type="guide"],
.template-box,
.reward-user-box,
.blog-column-pay,
#blogColumnPayAdvert,
#blog_detail_zk_collection,
.more-toolbox,
.comment-edit-box,
.right-message,
.operating,
.comment-box .comment-list-container .comment-list-box ul.comment-list li.comment-line-box .comment-tag,
.new-opt-box,
#csdn-userTooltip,
.identity-icon,
.expert,
.bottom-pub-footer,
.blog-footer-bottom,
#csdn-redpack,
.recommend-tit-mod,
#csdn-shop-window,
#toolBarBox,
.reward-box-new,
#csdn-shop-window-top,
.csdn-common-logo-advert,
.passport-login-container,
#articleSearchTip,
#treeSkill,
#recommendNps,
.comment-top > .opt-comment,
#marketingBox,
.csdn-side-toolbar a[data-type="show"],
.csdn-side-toolbar a[data-type="hide"],
.hide-preCode-box
{
    display: none!important;
}

.container#mainBox main {
    margin: 0 auto!important;
    width: 100%!important;
}

#article_content {
    height: auto!important;
}

.comment-list-box {
    max-height: none!important;
}

.replay-box {
    display: list-item!important;
}

.article-bar-bottom {
    border-bottom: 0px!important;
}

.comment-box {
    padding-bottom: 0px;
}

#content_views pre code {
    user-select: text!important;
}
.set-code-hide {
    height: auto!important;
}
}
