css:
@-moz-document domain(www.megadede.com) {

#topbar {
    background: #424242;
    border: none;
    z-index: 10;
}
#topbar .nav a:hover, .btn-primary:hover, .btn-success:hover, .expandable .view-more .more-icon:hover, .load-more-icon:hover, .open>.dropdown-toggle.btn-primary:hover, .primary-color:hover {
    background: #424242;
}
#topbar .nav a.active, #topbar .nav a:active, #topbar .nav a:focus, .active.btn-primary, .active.btn-success, .active.load-more-icon, .btn-primary:active, .btn-primary:focus, .btn-success:active, .btn-success:focus, .expandable .view-more .active.more-icon, .expandable .view-more .more-icon:active, .expandable .view-more .more-icon:focus, .load-more-icon:active, .load-more-icon:focus, .open>.active.dropdown-toggle.btn-primary, .open>.dropdown-toggle.btn-primary:active, .open>.dropdown-toggle.btn-primary:focus, .primary-color.active, .primary-color:active, .primary-color:focus {
    background: #424242;
}
#topbar .nav a {
    color: #fff;
    padding: 15px 10px;
}
#topbar .nav a, .btn-primary, .btn-success, .expandable .view-more .more-icon, .load-more-icon, .open>.dropdown-toggle.btn-primary, .primary-color {
    background: #424242;
}
body {
    background: #505050;
    font-family: Roboto, sans-serif;
    font-size: 16px;
    line-height: 1.5em;
    color: #fff;
}
#topbar .top-right .notifications-container .dropdown-menu .notification-list .notification a, .btn-link, a.nice {
    font-weight: 400;
    color: #9e9e9e;
}
a {
    color: #ffffff;
}
.comments-container .comment.comment-new .main-comment {
    background: #5a5a5a;
    max-height: 66px;
}
.form-control {
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    background-color: #211e1e;
    background-image: none;
    border: 1px solid #211e1e;
    border-radius: 4px;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, .075);
    transition: border-color .15s ease-in-out, box-shadow .15s ease-in-out;
}
.comments-container .comment .subcomments {
    margin-top: -1px;
    position: relative;
    background: #5a5a5a;
    box-shadow: none;
    font-size: 13px;
    border: 1px solid #313131;
    border-top-right-radius: 0;
    border-top-left-radius: 0;
    width: calc(100% - 55px);
    margin-left: 52px;
    padding: 8px;
}
body .content {
    background: #313131;
    box-shadow: 0 1px 1px rgba(0, 0, 0, .05);
    padding: 10px;
    margin-bottom: 15px;
    border: 1px solid #5a5a5a;
    border-radius: 4px;
}
.media-container-all .media-container .media-title {
    color: #d6d6d6;
    font-size: .8em;
    width: 100%;
    white-space: nowrap;
    text-overflow: ellipsis;
    overflow: hidden;
    box-sizing: border-box;
}
element.style {
    position: relative;
    vertical-align: top;
    background-color: #211e1e;
}
form .form-control {
    border-color: #5a5a5a;
}
#topbar #search-form button {
    background-color: #757575;
    border-color: #ababab;
    margin-left: -7px;
    border-top-left-radius: 0;
    border-bottom-left-radius: 0;
}
body, body .btn {
    font-weight: 300;
}
.btn-default {
    color: #adadad;
    background-color: #fff;
    border-color: #ccc;
}
.page-home .right-column .calendar-buttons .btn:hover[data-calendar-nav=today], .page-home .right-column .calendar-buttons .btn[data-calendar-nav=today] {
    z-index: 3;
    position: relative;
    background: #636161;
}
.form-control, output {
    display: block;
    font-size: 14px;
    line-height: 1.42857143;
    color: #c7c7c7;
}
.page-item .episode-container .season-header {
    padding: 4px 15px;
    background: #4e4c4a;
    color: #fff;
}
.page-item .season-link.current-season {
    background: #8e8e8e;
    color: #f1f1f1;
    border-color: #c7c7c7;
}
body .btn-primary, body .btn-primary.active, body .btn-primary.focus, body .btn-primary:active, body .btn-primary:focus, body .btn-primary:hover, body .btn-success, body .btn-success.active, body .btn-success.focus, body .btn-success:active, body .btn-success:focus, body .btn-success:hover, body .open>.dropdown-toggle.btn-primary {
    border-color: #565656;
}
.page-item .episode-container a.seen {
    background: #404040;
}
.page-item .episode-container a {
    color: #ababab;
    padding: 10px 15px;
    border-bottom: 1px dotted #696868;
    outline: none;
    background: #f7f7f7;
    transition: all .15s ease;
}
element.style {
}
.page-item .episode-container a.seen .date, .page-item .episode-container a.seen .name {
    color: #d8d8d8;
}
.page-item .episode-container a .right-items .episode-stats .episode-stat {
    margin-left: 6px;
    display: inline-block;
    color: #afaaaa;
}
.page-item .episode-container a {
    color: #fbfbfb;
    padding: 10px 15px;
    border-bottom: 1px dotted #696868;
    outline: none;
    background: #737373;
    transition: all .15s ease;
}
.page-item .episode-container a:hover {
    background: #404040;
}
.page-item .episode-container a .right-items .icons .unseen {
    color: #fbfbfb;
    transition: all .15s ease;
}
.page-item .episode-container a .right-items .episode-stats .episode-stat {
    margin-left: 6px;
    display: inline-block;
    color: #e0e0e0;
}
.dropdown-menu {
    position: absolute;
    top: 100%;
    left: 0;
    z-index: 1000;
    display: none;
    float: left;
    min-width: 160px;
    padding: 5px 0;
    margin: 2px 0 0;
    font-size: 14px;
    text-align: left;
    list-style: none;
    background-color: #5a5a5a;
    background-clip: padding-box;
    border: 1px solid #ccc;
    border: 1px solid rgba(0, 0, 0, .15);
    border-radius: 4px;
    box-shadow: 0 6px 12px rgba(0, 0, 0, .175);
}
.media-dropdown .dropdown-menu .fa {
    margin-left: -2px;
    color: #fff;
    margin-right: 4px;
}
.page-item .item-buttons ul.dropdown-menu a {
    max-width: 140px;
    margin: auto;
    color: #ffd;
}
body .modal-footer, body .modal-header {
    background: #3a3737;
}
.modal-header {
    min-height: 16.43px;
    padding: 15px;
    border-bottom: 1px solid #5f5f5f;
}
.popup-aportes .episode-info {
    border-bottom: 1px solid #828282;
    padding-bottom: 15px;
    margin-bottom: 20px;
    font-size: .8em;
    background: #3a3737;
    margin-top: -15px;
    padding-top: 15px;
}
.popup-aportes .tab-content {
    background: #484848;
    padding: 15px 0;
}
body .alert.alert-warning {
    background-color: #929290;
    border-color: #42423f;
}
body .alert {
    padding: 12px;
}
.alert-warning {
    color: #e2e1df;
    background-color: #fcf8e3;
    border-color: #faebcc;
}
.popup-aportes .alert-warning a {
    color: #f1f0f0;
    text-decoration: underline;
}
.row {
    margin-right: -15px;
    margin-left: -15px;
    color: #fff;
}
.modal-body {
    position: relative;
    padding: 15px;
    background: #181818;
}
.popup-aportes .nav>li>a {
    color: #ded9d9;
}
#topbar .top-right .notifications-container .dropdown-menu .notification-list .notification a:active, #topbar .top-right .notifications-container .dropdown-menu .notification-list .notification a:focus, .btn-link:active, .btn-link:focus, a.nice:active, a.nice:focus {
    color: #af9a96;
}
.aporte:visited, .aporte:visited:active, .aporte:visited:focus, .aporte:visited:hover {
    background: #c7c7c7;
    color: #000;
}
.aporte {
    display: block;
    color: #dcd7d7;
    position: relative;
    padding: 7px 15px;
    cursor: pointer;
    margin-top: -1px;
    border: 1px dotted #5d5d5d;
    background: #3a3a3a;
    font-size: 12px;
}
.close {
    float: right;
    font-size: 21px;
    font-weight: 700;
    line-height: 1;
    color: #fff;
    text-shadow: 0 1px 0 #fff;
    filter: alpha(opacity=20);
    opacity: .2;
}
body .alert.alert-warning {
    background-color: #0c0c0c;
    border-color: #ddd324;
}
.alert-warning {
    color: #ada79e;
    background-color: #fcf8e3;
    border-color: #faebcc;
}
#topbar .top-right .notifications-container .dropdown-menu .notification-header {
    border-top-left-radius: 2px;
    border-top-right-radius: 2px;
    background: #737373;
    text-align: center;
    margin: -1px;
    z-index: 1;
    position: relative;
}
#topbar .top-right .notifications-container .dropdown-menu .view-all {
    text-align: center;
    background: #737373;
    margin-bottom: -5px;
    border-top: 1px solid #ccc;
    display: block;
    color: #fff;
    padding-bottom: 1px;
    border-bottom-left-radius: 2px;
    border-bottom-right-radius: 2px;
}
#topbar .top-right .notifications-container .dropdown-menu .notification-list {
    -webkit-padding-start: 0;
    height: 200px;
    min-width: 250px;
    overflow: scroll;
    margin-bottom: 0;
    border-radius: 0;
    background: #888;
}
.prev-next-navbar {
    text-align: center;
    padding: 15px;
    background: #3a3a3a;
    border-bottom: 1px solid #3a3a3a;
    display: none;
}
.page-item .main-column .plot .view-more {
    background-image: -webkit-linear-gradient(top,hsla(0, 0%, 32%, 0.8),#fff);
    -ms-filter: "progid:DXImageTransform.Microsoft.gradient(startColorStr='$from-color', EndColorStr='$to-color')";
}
}
