/* ==UserStyle==
@name           LimeTorrents Dark & Clean 2022
@namespace      USO Archive
@author         Dawwwg
@description    `Dark & clean light-weight theme for limetorrents.lol, including some ad-suppression for those not using a dedicated Ad-Blocker.`
@version        20210814.12.30
@license        CC-BY-SA-4.0
@preprocessor   uso
==/UserStyle== */
@-moz-document domain("limetorrents.lol"), domain("limetorrents.pro"), domain("limetorrents.cc"), domain("limetorrents.info") {
*                                           { font-family: Verdana, Geneva, sans-serif !important  }
body                                        { background-color: #222    !important; color: #eee !important }
tr[bgcolor='#ffffff'], .commentarea2        { background-color: #282828 !important; color: #eee !important }
tr[bgcolor='#f4f4f4']                       { background-color: #303030 !important; color: #eee !important }  
th                                          { background-color: #383838 !important; color: #eee !important }
tr:hover, tr.hovered                        { background: linear-gradient(0deg, #333f, #efe2, #333f) }
table                                       { width: 100% !important }
table, td, th                               { border: 0 !important;   }
table tr:first-child th:first-child         { border-top-left-radius    : 8px }
table tr:first-child th:last-child          { border-top-right-radius   : 8px }
table tr:last-child td:first-child          { border-bottom-left-radius : 8px }
table tr:last-child td:last-child           { border-bottom-right-radius: 8px }

#logo                                       { width: auto }
#logo p, #userlogin, #userlogin a           { color: #222 !important; font-weight: 600 }
#mainnav                                    { font-size: 1.05em }
#navbar                                     { background-color: #333; border-color: #444; letter-spacing: 0.5px }
#navbar a, #navbar2 a                       { color: #e3f1be !important }
#navbar2                                    { background-color: #282828; font-size: 0.9em; border-bottom: 1px solid #383838 }

#rightbar div .head                         { background-color: #383838; border-radius: 5px 0 0 5px }
#rightbar div div                           { border: 0 }

#searchdiv, .searchbuttonimg                { filter: hue-rotate(270deg) !important }

.search_stat a          { background-color: #333;                              border-radius: 5px; border-color: #444   ; box-shadow: 0 0 5px 0 #555   ; padding: 7px; margin: 2px 8px; min-width: 20px; display: inline-block }
.search_stat .active    { background-color: #333; color: #eee              ; border-radius: 5px; border-color: #32cd32; box-shadow: 0 0 5px 0 #32cd32; padding: 7px; margin: 2px 8px; min-width: 20px; display: inline-block }
.search_stat .disabled  { background-color: #333; color: #888    !important; border-radius: 5px; box-shadow: 0 0 5px 0 #555; padding: 7px; margin: 2px 8px;  min-width: 20px; display: inline-block; border: 1px solid #444 }
.search_stat a:hover    { background-color: #333; color: #32cd32 !important; box-shadow: 0 0 5px 0 #32cd32; text-decoration: none }



ul.lt-categories img                        { filter: invert(100%) }
.lt-home                                    { margin: 60px 0 }
.lt-home .lt-search                         { background: inherit; margin: 60px 0px; padding: 0 }
.lt-home .lt-search form                    { background-color: inherit }
.lt-home .lt-search input[type=text]        { height: 25px }
.lt-home .lt-search input[type=text],
.searchfield                                { color: #444 }
.lt-home .lt-announcements .lt-heading, a   { color: #eee !important }
.lt-navigation.lt-group ul li a             { position: inherit !important }
.lt-navigation.lt-group ul li a:hover       { color: #333 !important }

.lt-footer .lt-links                        { margin: 0 }

.tdnormal, .tdseed, .tdleft, .tdright,
.table3 td                                  { font-size: 0.95em }
.tt-name a:hover                            { text-decoration: none }
.tdseed                                     { color: #32cd32 }
.tdnormal                                   { text-align: inherit }
.torrentvoting                              { font-size: 2em }
.torrentvoting span.tooltipa                { font-size: 0.5em }

.lt-home .lt-categories a:hover span::after { right: 0; left: 0; background-color: #32cd32; box-shadow: 0 0 5px 0 #32cd32 }

.downloadarea                               { display: inline; float: none }
.torrentinfo br, .downloadareabig           { display: none }
.dltorrent a                                { margin: 0 5px }
.csprite_doc_video, .csprite_doc_img,
.csprite_doc_nfo  , .csprite_doc_sfv,
.csprite_doc_doc  , .csprite_doc_unknown    { filter: invert(86.5%) }

.recentsearch, .browsetotals                { padding-bottom: 15px }

/* Ads suppression */
#footer, .lt-footer, .addthis_bar, .lt-copyright, .lt-announcements, font, table[style='padding-bottom:10px;width:100%;font-size: 12px;font-family:Verdana'],
#glx-9561-container, #rightbar > div:nth-of-type(2) > center
{ display: none !important; z-index: -99999 !important; left: -9999px !important; top: -9999px !important; width: 0px !important; height: 0px !important; 
  transform: translateY(-9999px) !important; opacity: 0 !important; visibility: hidden !important; content: '' !important }
}
