/* ==UserStyle==
@name        Ncf Dark
@version     0.1.5
@description Transforms www.ncf.ca pages into a dark theme.
@author      Rob Johnston
@homepageURL https://github.com/robjohnston/NcfDark
==/UserStyle== */
@-moz-document domain("www.ncf.ca") {
    body {
        background: #171717 !important;
        color: #e3e3e3 !important;
        font-family: Segoe UI, SegoeUI, Segoe WP, Helvetica Neue, Helvetica, Tahoma, Arial, sans-serif !important;
        font-size: medium !important;
        font-weight: 400 !important;
    }
    
    a {
        color: #4db2ff !important;
        font-size: medium;
    }
    a:hover {
        color: #0091ff !important;
        text-decoration: underline;
    }
    a:visited {
        color: #569cd6 !important;
    }
    
    /* Form inputs */
    form select,
    form input[type="password"],
    form input[type="text"],
    form input:not([type]) {
        background: #171717;
        border: 1px solid #454545;
        color: #e3e3e3;
        padding: 3px;
    }
    form input[type="password"]:hover,
    form input[type="text"]:hover {
        border: 1px solid #4db2ff;
    }
    form input[type="password"]:focus,
    form input[type="text"]:focus {
        background: #171717;
        border: 3px solid #4db2ff;
        color: #e3e3e3;
    }
    
    
    form input[type="reset"],
    form input[type="submit"] {
        background-color: #005da6;
        border-color: #004e8c;
        border-radius: 5px;
        color: #fff;
        color: white;
        font-size: larger;
        min-width: 100px;
        padding: 10px;
    }
    
    /* Hack for existing graphs */
    div#topbar div {
        background: white;
    }
    
    
    /* Bar below banner */
    div#topbar div#topbar_linkbar {
        background: #171717;
    }
    
    div#leftbar {
        background: #171717;
    }
    
    body > table > tbody > tr:nth-child(1) > td > table > tbody > tr > td:nth-child(1) > table > tbody > tr > td {
        background: white;
    }
    }
    
    @-moz-document url-prefix("https://www.ncf.ca/ncf") {
    /* Banner */
    body > table:nth-child(1) {
        border-radius: 5px;
        margin-top: 10px;
    }
    
    /* Bar below banner */
    td.headerlinks {
        background: lightsteelblue;
        font: inherit;
        padding-top: 3px;
    }
    td.headerlinks a {
        color: darkblue !important;
    }
    }
    
    @-moz-document url-prefix("https://www.ncf.ca/ncf/home") {
    /* Headlines */
    body > table > tbody > tr > td.content > div.news > h3 {
        margin-top: 10px;
    }
    
    /* CBC logo */
    body > table:nth-child(2) > tbody > tr:nth-child(1) > td.content > div.news > a > img {
        display: none;
    }
    
    /* Centre-column NCF Announcements */
    div.messages {
        font-family: Segoe UI, SegoeUI, Segoe WP, Helvetica Neue, Helvetica, Tahoma, Arial, sans-serif;
        font-size: medium;
        font-weight: 400;
    }
    
    div.messages h3 {
        background: #242424;
        border: 1px solid #454545;
        color: #e3e3e3;
        margin-top: 20px;
        padding: 5px;
    }
    
    div.messages p {
        background: #2e2e2e;
        border: 1px solid #454545;
        padding: 10px;
    }
    
    /* Centre-column news */
    div.messages h2,
    div.news h2 {
        background: #38225d;
        color: #e3e3e3;
        font-family: inherit;
        font-size: inherit;
        font-weight: bold;
        padding: 5px;
    }
    div.news h3 {
        background: #002b4d;
        border-radius: 5px;
        color: #a3d7ff;
        font-size: larger;
        padding: 3px;
        margin-bottom: 8px;
    }
    
    /* Put the e-mail refresh and unread count on same horizontal */
    td.sidebar > p:nth-child(4) > a:nth-child(2) {
        float:left;
    }
    td.sidebar > p:nth-child(4) > span {
        float:right;
    }
    
    /* The ""...checking activity" text of DG favourites */
    #dgFavs > p > span * {
        color: #57a64a !important;
    }
    
    /* Make a donation */
    a.donate {
        background: #2B542C;
        border-radius: 5px;
        color: #fff !important;
    }
    
    /* Left-side and right-side menu */
    td.sidebar h2 {
        background: #38225d;
        border-radius: 5px;
        color: #e3e3e3;
        padding: 5px;
    }
    
    td.sidebar a:hover {
        color: #0091ff !important;
    }
    
    /* Footer */
    body > table.verdana {
        background: #245269;
        border: 1px solid transparent !important;
        border-radius: 5px;
        margin-bottom: 50px;
    }
    }
    
    @-moz-document url-prefix("https://www.ncf.ca/ncf/dg/index.jsp") {
    /* Text below header */
    body > table:nth-child(2) > tbody > tr > td.content > p {
        background: #171717 !important;
        color: #e3e3e3 !important;
    }
    
    
    /* DG description */
    body > table:nth-child(2) > tbody > tr > td.content > p:nth-child(1) {
        background: #38225d;
        color: #e3e3e3;
        padding: 20px;
        border-radius: 10px;
    }
    
    /* Page header */
    body > table:nth-child(2) > tbody > tr > td.content > div > h2 {
        background: #38225d !important;
        border-radius: 10px;
        font-size: large;
        padding: 10px;
    }
    
    
    /* "Currently know problems" Subject header (maybe others?) */
    body > table:nth-child(2) > tbody > tr > td.content > div > h2 {
