        color: #e3e3e3 !important;
        padding: 2px;
    }
    
    tr.line1 * {
        background: #171717 !important;
        color: #e3e3e3 !important;
        padding: 2px;
    }
    
    tr.line0 > td > table {
        border: solid #454545 1px;
        margin-top: 10px;
        margin-bottom: 10px;
    }
    
    tr.line1 > td > table {
        border: solid #454545 1px;
        margin-top: 10px;
        margin-bottom: 10px;
    }
    
    tr.line0 > td > table > tbody > tr:nth-child(n) * {
        background: #2e2e2e !important;
        border: none;
        color: #e3e3e3 !important;
    }
    tr.line1 > td > table > tbody > tr:nth-child(n) * {
        background: #2e2e2e !important;
        border: none;
        color: #e3e3e3 !important;
    }
    tr.line0 > td > table > tbody > tr:nth-child(1) * {
        background: #242424 !important;
        border: none;
        color: #e3e3e3 !important;
    }
    
    tr.line1 > td > table > tbody > tr:nth-child(1) * {
        background: #242424 !important;
        border: none;
        color: #e3e3e3 !important;
    }
    
    #thread0 > td,
    #thread1 > td {
        border: none !important;
    }
    
    /* Message title link */
    td.content > table > tbody > tr:nth-child(n) > td > table > tbody > tr:nth-child(1) > td:nth-child(1) > table > tbody > tr > td:nth-child(1) > a {
        color: #4db2ff !important;
    }
    
    /* Show more link */
    #showLine > td > a {
        color: #4db2ff !important;
    }
    
    /* Left-side menu */
    td.sidebar h2 {
        background: #38225d;
        border-radius: 5px;
        color: #e3e3e3;
        padding: 5px;
    }
    
    /* "New Posting" link button */
    body > table:nth-child(2) > tbody > tr > td.sidebar > table > tbody * {
        background: #245269 !important;
        border: 1px solid transparent;
        border-radius: 2px;
        color: #e2e2e2 !important;
        padding: 3px;
    }
    
    /* Footer bar */
    body > table:nth-child(2) > tbody > tr > td.content > table > tbody > tr:last-child > td > table * {
        background: #242424 !important;
        border: none;
        color: #e3e3e3 !important;
        padding: 10px
    }
    
    body > table:nth-child(2) > tbody > tr > td.content > table > tbody > tr:last-child > td > table * a {
        color: #4db2ff !important;
    }
    
    body > table:nth-child(2) > tbody > tr > td.content > table > tbody > tr:last-child > td > table * a:hover {
