        text-decoration: none;
    }
    body > table:nth-child(2) > tbody > tr > td > form > table > tbody > tr > td#body a:hover {
        color: #0091ff !important;
        text-decoration: underline;
    }
    }
    
    @-moz-document url-prefix("https://www.ncf.ca/ncf/dg/dgNew.jsp") {
    /* Group name repeated */
    body > table:nth-child(2) > tbody > tr > td > form > table > tbody > tr:nth-child(1) > td {
