css:
@-moz-document url-prefix("http://www.slader.com"), url-prefix("https://www.slader.com") {
/* Fixed front page width (no more scroll) */
.container {
 	width: 99.99999%;
}

/* Inner background color */
.container .inner {
	background-color: transparent;
}

/* Inverts Logo color */
.navigation__logo {
	filter: invert(100%);
}

/* Top header */
.container .header {
	background: #1b1b1b;
	border-bottom: 1px solid #303030;
}

/* 'Search', 'Browse', 'Scan' TEXT color */
body.new-nav .navigation .navigation__link, body.new-nav .navigation .navigation__item.navigation__account .nav-account .nav-login {
	color: #fff;
}

/* Left border color of 'Log out' */
body.new-nav .navigation .navigation__item.navigation__account .nav-account::before {
	border-left: 1px solid #303030;
}

/* 'Search' icon color */
#magnifier-2.cls-1, path.cls-1 {
	stroke: #fff;
}

/* Search fill color and "Log In" fill color */
.cls-1, .cls-2 {
	fill: none!important;
	stroke: #fff!important;
}

/* 'Scan' icon color */
.nav-scan-icon {
    fill: white;
}

/* Invert 'Browse' icon color */
#browse {
	filter: invert(100%);
} 

/* Log out text color */
body.new-nav .navigation .navigation__item.navigation__account .nav-account .nav-logout{
	color: #fff;
}

/* Footer 'Slader' & 'Homework Solved' & 'Copyright' text color */
body > footer .branding h1, body > footer .branding h2, body > footer ul.legal-copyright li {
	color: #898989;
}

/* Footer background color */
body > footer {
	background-color: #1b1b1b;
	border-top: 1px solid #303030;
}

/* Footer inner background color */
body > footer .inner {
	background-color: transparent;
}

/* Footer link color */
body > footer ul li a, body > footer ul li a:active, body > footer ul li a:link, body > footer ul li a:visited {
	color: #fff;
}

/* Footer link :hover color */
body > footer ul li a:active:hover, body > footer ul li a:hover, body > footer ul li a:link:hover, body > footer ul li a:visited:hover {
 	color:#fff;
}
}

@-moz-document url("https://www.slader.com/"), url-prefix("http://www.slader.com/subject/"), url-prefix("http://www.slader.com/search/"), url-prefix("http://www.slader.com/textbook/"), url-prefix("http://www.slader.com/about/"), url-prefix("https://www.slader.com/account/login/"), url-prefix("http://www.slader.com/home/"), url-prefix("https://www.slader.com/home/") {
.container {
    background-color: #252525;
}

/* Textbook Information background color */
.container > .content .textbook-header {
	background-color: #1b1b1b;
}

/* 'Table of Contents' background color */
.container > .content .textbook-section {
	background-color: #303030;
    border: none;
}

/* 'Table of Contents' text color */
.container > .content h1, .container > .content h2 {
	color: #fff!important;
}

/* 'Table of Contents' "PG" text color */
.container > .content .textbook-section .textbook-page-search label {
	color: #fff;
}

/* 'Chapter X' border color */
.container > .content.textbook-detail article.toc {
    border: none;
}


/* 'Chapter X' box background color when hovered AND opened */
.container > .content.textbook-detail article.toc .toc-item .toc-item-collapsed:hover, .container .inner.main-top-ac, .container > .content.textbook-detail article.toc .toc-item .toc-item-collapsed.open {
	background-color: #202020;
    -moz-transition: none;
	-webkit-transition: none;
	transition: none;
}

/* 'Chapter X' box background color when NOT hovered/opened */
.container > .content.textbook-detail article.toc .toc-item .toc-item-collapsed {
	background-color: #1b1b1b;
	border-bottom: 1px solid #303030;
}

/* 'Chapter X' text color */
.container > .content.textbook-detail article.toc .toc-item .toc-item-collapsed.open .chapter h3, .container > .content.textbook-detail article.toc .toc-item .toc-item-collapsed:hover h3, .container > .content.textbook-detail article.toc .toc-item .toc-item-collapsed .chapter h3 {
	color: #d8d8d8;
    -moz-transition: none;
	-webkit-transition: none;
	transition: none;
}

/* 'Chapter title name' text color */
.container > .content.textbook-detail article.toc .toc-item .toc-item-collapsed.open .chapter p, .container > .content.textbook-detail article.toc .toc-item .toc-item-collapsed:hover p, .container > .content.textbook-detail article.toc .toc-item .toc-item-collapsed .chapter p {
	color: #888;
    -moz-transition: none;
	-webkit-transition: none;
	transition: none;
}

/* Chapter Sub section background color */
.container > .content.textbook-detail article.toc .toc-item .toc-item-expanded table {
    background: transparent;
	margin: 0;
	width: 100%;
}

/* Chapter Sub-section title number text color */
.container > .content.textbook-detail article.toc .toc-item .toc-item-expanded table tr td:first-child {
	color: #d8d8d8;
}

/* Chapter Sub-section info text color */
.container > .content.textbook-detail article.toc .toc-item .toc-item-expanded table tr td {
	color: #888;
}

/* Chapter Sub-section hover background color */
.container > .content.textbook-detail article.toc .toc-item .toc-item-expanded table tr:hover {
	background-color: transparent;
}

/* Chapter Sub-section hover text color */
.container > .content.textbook-detail article.toc .toc-item .toc-item-expanded table tr:hover td, .container > .content.textbook-detail article.toc .toc-item .toc-item-expanded table tr:hover td:first-child {
	color: #0079C2;
}

/* Breadcrumb navigation text color */
.solutions .breadcrumbs .caret-right, .solutions .breadcrumbs a {
	color: #0079C2;
}

/* Page # - Text color, background color, border */
form input[type="text"], form input[type="email"], form input[type="password"], form input[type="tel"], form input[type="search"], form textarea {
	background-color: #1b1b1b;
	border: 1px solid #303030;
	color: #fff!important;
}

/* Chapter Sub-section top border color */
.container > .content.textbook-detail article.toc .toc-item .toc-item-expanded table tr {
	border-top: 1px solid #303030;
}

/* "Try one of these instead" border, background color */
section.other-family-texts .texts {
	background-color: #1b1b1b;
	border: none;
}

/* "Try one of these instead" text color */
a, a:active, a:link, a:visited, input[type="button"], input[type="submit"] {
	color: #fff;
}

/* "Try one of these instead" HOVER text color */
.container > .content.solutions a:not(.button):active:hover, .container > .content.solutions a:not(.button):hover, .container > .content.solutions a:not(.button):link:hover, .container > .content.solutions a:not(.button):visited:hover {
	color: #0079c2;
}

/* Weird object text color */
body.new-nav {
/* 	color: transparent; */
    background: #252525;
}

/* Slader */
section.mini-faq h5 {
    color: #d8d8d8;
}

/* -------------Cheat Sheet Box------------- */

/* Background color */
.container > .content .cheatsheet-widget, .modal-container .cheatsheet-modal-widget {
	background-color: #1b1b1b;
	border: none;
}

/* Textbox background color */
.container > .content .cheatsheet-widget input[type="text"], .modal-container .cheatsheet-modal-widget input[type="text"] {
	background-color: #303030;
	border: none;
}

/* Sign in or Register page [Start] */

/* Background color */
body.login-page .container > .content.form-only .login-component, body.login-page .container > .content.form-only header {
	background-color: #1b1b1b;
}

/* Sign in text color */
body.login-page .container > .content.form-only header h1 {
	color: #d8d8d8;
}

/* "Log In" text color */
body.login-page .container > .content.form-only .access-wrap .fb-login div span, body.login-page .container > .content.form-only .access-wrap h2 {
	color: #fff;
}
/* Sign in or Register page [End] */

/* "Search Slader" box background color */
.container > .header .search.search__global {
	background-color: #1b1b1b;
	border-bottom: 1px solid #303030;
}

/* "Search Slader" textbox background color */
.container > header .search form input {
	background-color: #303030;
}

.container > header .search form {
	background-color: transparent;
}

/* Drop Down Search Results box border */
.dropdown-search.with-results .results .type-results {
/* 	border-left: 1px solid #ccc;
	border-right: 1px solid #ccc; */
    border: none;
}

/* Drop Down 'Textbooks'/'Questions' background color */
.dropdown-search.with-results .results .type-results .search-header {
 background:#303030;
/*  border-bottom:1px solid #ccc;
 border-top:1px solid #ccc; */
    border: none;
}

/* Drop down 'Textbooks'/'Questions' text color */
.dropdown-search.with-results .results .type-results .search-header a {
 background: transparent;
 color:#fff;
}

/* Drop down 'view all' text color */
.dropdown-search.with-results .results .type-results .search-header a.view-all {
	color: #828282 !important;
}

/* Drop down 'Textbooks' results text color */
.dropdown-search.with-results .results .type-results .search-result.textbook a {
	color: #fff;
}

/* Drop down 'Textbooks' results text color */
.dropdown-search.with-results .results .type-results .search-result.textbook:hover a {
	text-decoration: underline;
}

/* Drop down 'Questions' results text color */
.dropdown-search.with-results .results .type-results .search-result.question a {
	color: #5d9cec;
}

/* Drop down 'Textbooks'/'Questions' results background color */
.dropdown-search.with-results .results .type-results .search-result {
	background-color: #1b1b1b;
	border-top: 1px solid #303030;
}

/* Drop down 'Textbooks'/'Questions' results background color:HOVER */
.dropdown-search.with-results .results .type-results .search-result:hover {
	background-color: #1b1b1b;
	border-top: 1px solid #303030;
    text-decoration: underline;
}

/* Browse clicked [Start] */
/* Background color */
body.new-nav .navigation .browse__menu .browse__subjects-parents {
	background: #1b1b1b;
}

/* Hovered over section background color and font color */
body.new-nav .navigation .browse__menu .subjects__new-desktop .browse__subjects-parents a.active-parent, body.new-nav .navigation .browse__menu .subjects__new-desktop .browse__subjects-parents a:hover {
	color: #fff;
	background-color: #303030;
	transition: .275s linear color;
	transition: .275s linear background-color;
}

/* Hovered over inside section background color */
body.new-nav .navigation .browse__menu {
	background-color: #303030;
	border-top: none;
}

/* Hovered over subject */
body.new-nav .navigation .browse__menu-link:hover {
	color: #0079c2;
	transition: color linear .0s;
}
/* Browse clicked [End] */

/* Textbook Seach page */
/* 'Textbook Search' background color */
.content.subject__landing .left .request-textbook, .content.subject__landing .left .subject__landing__container, body.textbook-search-page .container > .content.search-results .left .request-textbook, body.textbook-search-page .container > .content.search-results .left .subject__landing__container {
	background-color: #1b1b1b;
	border: none;
}

/* 'Textbook Search' 'Search box background color & font color */
.container .content.search-results .search-form input {
    background-color: #303030;
	border: none;
	color: #fff;
}

/* Hovered over book */
.textbook-work-index-widget:hover {
	background: #303030;
	border: none;
}

/* Book Name color */
.textbook-work-index-widget .infos h4 {
	color: #fff;
}

/* Hovered book: Right arrow background color */
.textbook-tooltip-wrap .textbook-tooltip::after {
	border-right-color: #303030;
}

/* Hovered book: Right arrow border color */
.textbook-tooltip-wrap .textbook-tooltip::before {
	border-right-color: transparent;
}

/* Hovered book: Left arrow background color */
.textbook-tooltip-wrap .textbook-tooltip.arrow-right::after {
	border-left-color: #303030;
}

/* Hovered book: Left arrow border color */
.textbook-tooltip-wrap .textbook-tooltip.arrow-right::before {
	border-left-color: transparent;
}

/* Hovered book: Box background color & border color */
.textbook-tooltip-wrap .textbook-tooltip {
	background: #303030;
    border: transparent;
}

/* Hovered book: Hovered title color */
.textbook-tooltip-wrap a:hover {
	color: #0079c2 !important;
}

/* 'Don't see your book?' box */
/* Input ISBN box */
.container > .content section.request-textbook form .textbook-search-wrap .input-wrap input[type="number"] {
    padding: 1px 30px 1px 10px;
    background-color: #303030;
	color: #fff;
    border: none;
    border-radius: 8px;
}

/* Exercises Box */
/* Background color */
.container > .content.textbook-page-detail .list {
	background: #1b1b1b;
	border: none;
}

/* 'Exercises' text color */
.container > .content.textbook-page-detail .list h3 {
	color: #fff;
}

/* Answer box background color */
.list .list-item {
	background-color: #303030;
}

/* Answer box left background color */
.container > .content.textbook-page-detail .list .list-item .indicator {
	background-color: #555;
}

/* Exercise # text color */
.container > .content.textbook-page-detail .list .list-item .answer-number {
	color: #fff;
}

/* Answer img inverted */
.container > .content.textbook-page-detail .list .list-item p.answer img.image-reg {
	filter: invert(100%);
}

/* Text answer text color */
.container > .content.textbook-page-detail .list .list-item p.answer {
	color: #fff;
}

/* Answer counter (X answ) */
.container > .content.textbook-page-detail .list .list-item .total-answers {
	background: transparent;
	color: #0079c2;
}

/* Exercises Box when HOVERED */
/* Answer box background color */
.container > .content.textbook-page-detail .list .list-item:hover {
	background-color: #555;
}

/* Answer box left background color */
.container > .content.textbook-page-detail .list .list-item:hover .indicator {
	background-color: #303030;
}

.container > .content.textbook-page-detail .list .list-item:hover .total-answers {
	background: transparent;
	color: #fff;
}

/* Page # box */
/* Middle box */
.container > .content.textbook-page-detail .page-navigation {
	background-color: #1b1b1b;
	border: none;
	color: #555;
}

/* Next/Previus box */
.container > .content.textbook-page-detail .page-navigation .next, .container > .content.textbook-page-detail .page-navigation .previous {
	background-color: #303030;
}

/* Next/Previus inverted img */
.container > .content.textbook-page-detail .page-navigation .previous .caret, .container > .content.textbook-page-detail .page-navigation .next .caret {
	filter: invert(100%);
}

/* Exercise page */
.container > .content .textbook-section h2.ex-heading > span {
	background: #1b1b1b;
	border: none;
}

.container > .content .textbook-section h2.ex-heading > a.next, .container > .content .textbook-section h2.ex-heading > span.next {
	border-right: none;
}

/* Answer #X border color */
.container > .content article.discussion-answer, .container > .content article.solution {
	border: none;
}

/* Header (User submitted, Star, Comments) */
.container > .content article.discussion-answer > header .top, .container > .content article.solution > header .top {
	background-color: #1b1b1b;
	border: none;
    box-shadow: none;
}

/* Answer */
.container > .content.exercise-detail .result {
	border: none;
}

/* Clicked Exercise Xy */
.container > .content .textbook-section .ex-chiclets {
	background-color: #1b1b1b;
	border: none;
}

/* Current Exercise */
.container > .content .textbook-section .ex-chiclets .ex-chiclet.current-eig {
	color: #fff;
}

/* Circle background color */
.container > .content .textbook-section .ex-chiclets .ex-chiclet:hover::after {
	background-color: #303030;
}

/* Questions text box */
/* Border background color */
.content.subject__landing .subject__questions .question-feed .top .ask-question {
	background-color: transparent;
	border: none;
}

/* Inside question search box background color and text inside */
.container > .content .question-feed .top .ask-question input.ask-question-input {
	background-color: #303030;
	color: #fff;
}

/* Home page input search box */
body.home .home-search.dropdown-search.with-results input[name="search_query"] {
	background-color: #1b1b1b;
}

/* "Textbooks (view all)" background color */
body.home .home-search.dropdown-search.with-results .results .type-results .search-header {
	background-color: #303030;
    border: none;
}

/* Textbook search results background color and bottom border */
body.home .home-search.dropdown-search.with-results .results .type-results .search-result.textbook {
	background-color: #1b1b1b;
    border-bottom: 1px solid #303030;
}

/* Textbooks results box border */
body.home .home-search.dropdown-search.with-results .results {
    border: transparent;
}
}
