css:
@-moz-document url-prefix("https://babeljs.io/docs/") {
body, pre {
	background-color: black;
	color: limegreen;
}
#docsNav > nav > div > section > div.navGroups > div > h3 {
	color: #fff;
}
#docsNav > nav > div > section > div.navGroups > div > ul > li > a {
	color: #96e100;
}
#docsNav > nav > div > section > div.navGroups > div > ul > li > a:hover {
	color: #abff02;
}
body div.navPusher div.container.mainContainer.docMainContainer div.post > article > div > span > p > a,
body div.navPusher div.container.mainContainer.docMainContainer div.post > article > div > span > ul > li > a {
	color: limegreen;
	background-color: rgba(55, 55, 55, 0.66);
	border-radius: 3px;
}
body div.navPusher div.container.mainContainer.docMainContainer div.post > article > div > span > p > a:hover,
body div.navPusher div.container.mainContainer.docMainContainer div.post > article > div > span > ul > li > a:hover {
	background-color: rgba(55, 55, 55, 1);
}
body div.navPusher div.container.mainContainer.docMainContainer div.post > article > div > span > blockquote {
	color: #4d4d4c;
}
body div.navPusher nav ul > li > a > code,
body div.navPusher nav ul > li > a {
	color: #bebebe;
}
body div.navPusher div.container.mainContainer.docMainContainer div.post > article > div > span > p > code,
body div.navPusher nav > ul > li:nth-child(4) > ul > li > a > code {
	background-color: rgba(62, 88, 114, 0.3);
}
body div.navPusher div.container.mainContainer.docMainContainer div.post > article > div > span > p > code:hover,
body div.navPusher nav > ul > li:nth-child(4) > ul > li > a > code:hover {
	background-color: rgba(62, 88, 114, 0.5);
}
body div.navPusher div.container.mainContainer.docMainContainer div.post a.button {
	color: limegreen;	
}
body div.navPusher div.container.mainContainer.docMainContainer div.post a.button:active {
	background-color: darkolivegreen;
	color: #fff;
}
}
