css:
@-moz-document domain('www.mc-market.org') {
	:root {
		--0: rgba(255, 255, 255, 0);
		--1: rgb(25, 25, 25);
		--2: rgb(50, 50, 50);
		--3: rgb(75, 75, 75);
		--4: rgb(100, 100, 100);
		--5: rgb(125, 125, 125);
		--6: rgb(150, 150, 150);
		--7: rgb(175, 175, 175);
		--8: rgb(200, 200, 200);
		--9: rgb(255, 255, 255);
		--B1: rgb(52, 152, 219);
	}

	body {
		color: var(--9) !important;
	}

	.a, html, .messageHeading, .PageNav a.currentPage, .messageUserBlock, .navTab.selected, .xbStickyBar, .textCtrl:focus, .subHeading, .xenOverlay.memberCard, #QuickSearch, #siropuChatHeader, #PostAreas .header, #DiscussionListOptionsHandle a {
		background-color: var(--1) !important;
	}

	.b, .resourceListSidebar h3, .navTabs .navTab.selected .tabLinks, .innerContent .rateBlock, .resourceListItem .main .featuredBanner, .resourceListItem .main .cost, .sectionMain, .XenBase .conversation_view .messageList, .messageUserBlock div.avatarHolder, .messageUserBlock h3.userText, .messageUserBlock .extraUserInfo, .mcAccInfoBlock, .redactor_toolbar, .bbCodeBlock .type, .sidebar .section .secondaryContent h3, .bbCodeQuote .quoteContainer, .PageNav a, .discussionListItem, .redactor_box, .tabs li a, .bbCodeSpoilerText, .navTabs .navTab.selected .tabLinks a, .profilePage .tabs.mainTabs, .textCtrl, .search_form .xenForm, .search_form_post .xenForm, .search_form_profile_post .xenForm, .search_form_resource_update .xenForm, .tag_search .xenForm, .xengallery_search_form_media .xenForm, .nflj_showcase_search_form .xenForm, .nflj_sportsbook_search_form .xenForm, .Menu .primaryContent, .primaryContent, .textWithCount.subHeading .count, .resourceListItem .resourceImage .resourceIcon img, .miniResourceList .resourceImage .resourceIcon img, .avatar img, .avatar .img, .profilePage .avatarScaler img, .featuredResourceList .featuredResource .resourceImage .resourceIcon img, .resourceInfo .resourceImage .resourceIcon, .breadcrumb, .breadcrumb .crust a.crumb, #QuickSearch, #siropuChatEnable, #siropuChat, #siropuChatEditor, #logoBlock, #peForumStats .value, #content .pageContent, #PostAreas tr:nth-child(1n+1), #PostAreas tr:nth-child(2n+1) {
		background-color: var(--2) !important;
	}

	.c, .resourceListItem, .resourceListItem .resourceImage, .resourceListItem .resourceStats, .secondaryContent, .featuredResourceList .featuredResource, .blockLinksList a, .blockLinksList label, .tagList .tag, .messageList, .messageList .message, .quickReply, .bbCodeImageFullSize, .bbCodeBlock pre, .sectionFooter, .siropuChatMessages, .profilePage .tabs.mainTabs, .Menu, .formPopup .controlsWrapper, #siropuChatSettings {
		background-color: var(--3) !important;
	}

	.d, body, .resourceListItem .main .featuredBanner, .resourceListItem .main .cost, .navTabs .tabLinks a, a.PageNavNext, a.PageNavPrev, .textHeading, .sectionFooter, #siropuChatEnable {
		color: var(--6) !important;
	}

	.e, .innerContent .rateBlock, .tagList .tag, .resourceListItem .main .featuredBanner, .resourceListItem .main .cost, .messageList .message, .bbCodeSpoilerText, .sidebar .secondaryContent, .node .nodeLastPost, .textCtrl, .featuredResourceList .featuredResource, .PageNav a, .Menu .primaryContent, .resourceListItem .resourceImage .resourceIcon img, .miniResourceList .resourceImage .resourceIcon img, .profilePage .avatarScaler img, .innerContent .updates ol, .quickReply, .redactor_box, .redactor_toolbar, .tabs li a, .sectionMain, .visitorText .stats dl, .navTabs .navTab.selected .tabLinks a, .secondaryContent, .blockLinksList a, .blockLinksList label, .resourceInfo .resourceImage .resourceIcon, .Menu .sectionFooter, .textHeading, .primaryContent, .blockLinksList a, .dark_postrating_member td, .textWithCount.subHeading .count, .subHeading, .profilePage .primaryUserBlock, .profilePage .mast, .profilePage .mainProfileColumn, .profilePage .primaryUserBlock .lastActivity, .messageUserBlock, .avatar .img, .blockLinksList label, .Menu .sectionFooter, .secondaryContent, .tabs, .actionFilterRow, .resourceListItem, .sectionFooter, .featuredResourceList .featuredResource .resourceImage .resourceIcon img, .bbCodeBlock .type, .bbCodeBlock, .avatar img, .profilePage .mast .section.infoBlock, .profilePage .mast .followBlocks .primaryContent.avatarHeap, .dark_postrating_member, #siropuChatEnable, #QuickSearch, #siropuChat, #boardStats dl, #AccountMenu .menuColumns, #peForumStats .value, #PostAreas table, #boardStats dl, #PostAreas tr:nth-child(1n+1) {
		border-color: var(--1) !important;
	}

	.f, html .navTabs .navTab.selected .navLink {
		color: var(--9) !important;
		background-color: var(--B1) !important;
	}

	.g, .bbCodeQuote .quoteContainer .quoteExpand {
		background: linear-gradient(to bottom, var(--0) 0%, var(--1) 80%) !important;
	}

	.h, .Menu .primaryContent {
		margin-left: 10px !important;
		margin-right: 10px !important;
	}

	.i, #AccountMenu .menuColumns, #AccountMenu .menuColumns ul {
		margin-top: 2px !important;
		margin-bottom: 2px !important;
	}

	.j, #AccountMenu, .navPopup {
		margin-top: 1px !important;
	}

	.k, #AccountMenu .menuHeader {
		margin-bottom: 10px !important;
	}

	.l, a.PageNavPrev, a.PageNavNext {
		border: 1px none black !important;
	}

	.m, #peForumStats .value, body .dimmed, #QuickSearch .formPopup .controlsWrapper, .bbCodeBlock .type {
		color: var(--7) !important;
	}

	.n, legend, .textCtrl, .sidebar .section .secondaryContent .footnote, .xenOverlay.memberCard, .xenOverlay.memberCard .pairsInline.lastActivity > dt, .xenOverlay.memberCard .pairsInline.lastActivity .DateTime, #AccountMenu .menuColumns label {
		color: var(--8) !important;
	}

	.o, .navTabs .navTab.selected {
		color: var(--9) !important;
	}

	a:link, a:visited, a:active {
		color: var(--8) !important;
	}

	a:hover, .tabLinks a:hover {
		color: var(--9) !important;
	}
}
