css:
@-moz-document domain("www.netvibes.com") {

/* Main */
body, div, p, .smartreader-header, .smartreader-header-title-extra, .nv-sidebarContainer, .nv-line-item-renderer .item-header, #smartreader-mainContainer, .nv-line-item-renderer .item-title {
    background-color: #353536 !important;
    color: #999 !important;
}

#top .universal-zorch-wrapper.active .universal-zorch {
    background-color: #777 !important;
    color: #222 !important;
    border: 1px solid #666 !important;
}

#top .universal-zorch .zorch {
    color: #222 !important;
}

.no-touch .current>.nv-treeview-row:hover, body .current>.nv-treeview-row, body .current>.nv-treeview-row.hasUnread, body.nv-user-unregistered .current>.nv-treeview-row {
    background-color: #777 !important;
    color: #222 !important;
}

.nv-line-item-renderer:not(.build-image):not(.this-class-does-not-exist) .item-preview-wrapper:after, .nv-treeview-header .toggle-static-sections {
    background-image: none !important;
}

.smartreader-header, .nv-line-item-renderer:after {
    border-bottom: 1px solid #666 !important;
}

.nv-item-list-renderer .group-label {
    border-top: 1px solid #666 !important;
    border-bottom: 1px solid #666 !important;
}

a {
    color: #fff !important;
    border: 0;
}

}
