css:
@-moz-document regexp('https://www.speaky.com/.*') {

#ng-view {
    background-color: #16161c;
}

div.card.community-card {
    background-color: #26262c;
}

a.change-filters-button {
    background-color: #26262c ;
}

a.rounded-button.white-rounded-button.relative.active {
    background-color: #26262c ;
}

div.container-profile-overlay-live {
    background-color: #16161c;
}

div.mini-profile-language-label {
    color: #3EA8FF;
}

div.community-personal-info-name b {
    color: #3EA8FF;
}

div.close-button-overlay-live {
    background-color: #26262c;
}

div.left-navbar.relative {
    background-color: #26262c;
}

div.messages-conversation-on-top.message-conversation-on-top-accepted-height {
    background-color: #16161c;
}

textarea.textarea-conversation-on-top-voice-note.ng-pristine.ng-untouched.ng-valid.ng-empty.ng-valid-maxlength {
    background-color: #16161c;
}

ul.dropdown-menu {
    background-color: #26262c;
}

div.one-notification-on-page:hover {
    background-color: #2d2d33;
}

div.text-side-notification-on-page {
    color: #e2e2e2;
}

div.community-description-container {
    color: #e2e2e2;
}

div.display-chat {
    background-color: #16161c;
}

div.top-bar-messages div {
    background-color: #16161c;
}

div.conversations-container {
    background-color: #16161c;
}

div.input-text-chat-container {
    background-color: #16161c;
}

textarea.newmessage.type-message.darker-scrollbar.ng-valid.ng-valid-maxlength.ng-dirty.ng-valid-parse.ng-empty.ng-touched {
    color: #e2e2e2;
}

a.conversation-in-messages.selected-conversation {
    background-color: #26262c !important;
}

a.conversation-in-messages:hover {
    background-color: #2d2d33 !important;
}

div.conversation-content-title-name span {
    color: #1E9AFF;
}

a.link-to-profile {
    color: #1E9AFF;
}

div.conversation-content-message-text {
    color: #e2e2e2;
}

div.conversation-content-title-date {
    color: #e2e2e2;
}

div.received-message-row > div > div.message-bubble-in-messages {
    background-color: #2d2d33;
    color: #e2e2e2;
}

div.sent-message-row > div > div.message-bubble-in-messages {
    background-color: #26262c;
    color: #e2e2e2;
}

div.subcontainer-profile ng-include {
    background-color: #26262c;
}

html.js.flexbox.canvas.canvastext.webgl.no-touch.geolocation.postmessage.websqldatabase.indexeddb.hashchange.history.draganddrop.websockets.rgba.hsla.multiplebgs.backgroundsize.borderimage.borderradius.boxshadow.textshadow.opacity.cssanimations.csscolumns.cssgradients.cssreflections.csstransforms.csstransforms3d.csstransitions.fontface.generatedcontent.video.audio.localstorage.sessionstorage.webworkers.applicationcache.svg.inlinesvg.smil.svgclippaths {
    background-color: #16161c;
}

div.container-user-card {
    background-color: #26262c;
}

div.profile-native-flag-container div {
    color: #e2e2e2;
}

div.profile-non-native-flag-container div {
    color: #e2e2e2;
}

ul.dropdown-menu > li > a {
    color: #e2e2e2;
}

ul.dropdown-menu > li > a:hover {
    background-color: #2d2d33;
}

div.subcontainer-profile {
    background-color: #26262c;
}

div.profile-picture-profile img {
    border-color: #2d2d33;
}

div.name-profile b {
    color: #3EA8FF;
}

img.profile-picture-navbar {
    border-color: #2d2d33;
}

div.element-profile-card.selectable div {
    color: #e2e2e2;
}

h3.caps {
    color: #3EA8FF;
}

div.element-profile-card.selectable h3 {
    color: #3EA8FF;
}

div.element-profile-card.selectable:hover, div.inline-editable-field:hover {
    background-color: #2d2d33;
}

div.card {
    background-color: #26262c;
}

div.card h3 {
    color: #fff;
}

label.toptop span {
    color: #e2e2e2;
}

div.ngdialog-content {
    background-color: #2d2d33;
}

textarea.form-control.ng-pristine.ng-untouched.ng-valid.ng-empty {
    background-color: #2d2d33;
    color: #e2e2e2;
}

p.text-left {
    color: #e2e2e2;
}

div.title-confirm.text-left {
    color: #fff;
}

input.ng-pristine.ng-untouched.ng-valid.ng-not-empty.ng-valid-maxlength.form-control.border-red.ng-valid-email {
    background-color: #26262c;
    color: #e2e2e2;
}

div.all-badges-part.framed-block {
    background-color: #26262c;
}

div.badge-name {
    color: #1E9AFF;
}

div.badge-description {
    color: #e2e2e2;
}

div.explanation-in-badges.framed-block {
    background-color: #26262c;
}

div.explanation-in-badges.framed-block div {
    color: #e2e2e2;
}

div.stats-in-badges.framed-block {
    background-color: #26262c;
}

input.form-control.inline.fs10.share-link-input {
    background-color: #2d2d33;
}

div.ambassador-description {
    color: #e2e2e2;
}

div.ambassador-text {
    color: #e2e2e2;
}

div.invited-friends-text {
    color: #fff;
}

}
