css:
@-moz-document domain("forums.stateoffirestone.xyz") {
.d-header {
    background-color: #d32f2f
}
.btn-primary {
    border: none;
    font-weight: normal;
    background: #d32f2f;
    color: #FFFFFF
}
.nav-pills > li.active > a,
.wizards-nav-button > li.active > a,
.nav-pills > li > a.active,
.wizards-nav-button > li > a.active {
    background-color: #fdd459;
    color: #ffffff
}
.d-icon.d-icon-d-tracking,
.d-icon.d-icon-d-watching {
    color: #f07373;
}

.timeline-container .topic-timeline .timeline-scrollarea {
    margin-top: 0.5em;
    margin-left: 0.5em;
    border-left: 1px solid;
    border-left-color: currentcolor;
    border-color: #d32f2f;
    position: relative;
    -webkit-transform: translate3d(0, 0, 0);
}

.timeline-container .topic-timeline .timeline-handle {
    border-radius: 0.8em;
    width: 0.35em;
    background-color: #d32f2f;
    height: 100%;
    float: left;
    z-index: 2;
}


.d-header #site-logo {

    height: 2.57em;
}

.nav-pills > li.active > a,
.wizards-nav-button > li.active > a,
.nav-pills > li > a.active,
.wizards-nav-button > li > a.active {
    background-color: #d32f2f
}
.nav-pills > li > a:hover,
.wizards-nav-button > li > a:hover {
    color: #d32f2f;
    background-color: #d32f2f36;
}
.discourse-no-touch .btn:hover,
.discourse-no-touch .btn.btn-hover {
    background: #d32f2f;
}
}
