css:
@-moz-document domain("bitrix24.ru") {
/* Font */
body.template-bitrix24 * {
    font-family: "Google Sans", "Roboto", sans-serif;
}
/* Small Tasks */
body.template-bitrix24 .tasks-kanban-item {
    padding: 3px 3px 3px 6px;
    border-radius: 0px 5px 5px 0px;
    border-bottom: 1px solid gainsboro;
}

body.template-bitrix24 a.tasks-kanban-item-title {
    font-size: 12px;
    margin-bottom: 2px;
}

body.template-bitrix24 span.tasks-kanban-item-tags {
    display: none;
}

body.template-bitrix24 .tasks-kanban-item-task-status {
    margin: 0px;
    float: left;
    margin-right: 2px;
    margin-top: 2px;
}

body.template-bitrix24 .tasks-kanban-item-deadline-hover,
body.template-bitrix24 .tasks-kanban-item-deadline {
    font-size: 10px;
}

body.template-bitrix24 .tasks-kanban-item-set-deadline,
body.template-bitrix24 .tasks-kanban-item-files,
body.template-bitrix24 .tasks-kanban-item-comments,
body.template-bitrix24 .tasks-kanban-item-status,
body.template-bitrix24 .tasks-kanban-item-status-new,
body.template-bitrix24 .tasks-kanban-item-checklist {
    font-size: 9px;
}

body.template-bitrix24 .tasks-kanban-item-users {
    padding: 2px 0px 2px 2px;
}

body.template-bitrix24 .main-kanban-item {
    padding: 0px;
    margin-bottom: 4px;
}

body.template-bitrix24 .tasks-kanban-item-red {
    margin: 0px;
    padding: 0px 3px;
    font-size: 9px;
    width: 14px;
    position: relative;
    background: none;
    color: transparent;
    border: 0;
}
body.template-bitrix24 .tasks-kanban-item-red:after {
    content: "!";
    display: block;
    font-size: 12px;
    font-family: "Arial";
    color: #fff;
    font-weight: 900;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background: #F44336;
    width: 16px;
    height: 16px;
    line-height: 16px;
    text-align: center;
    border-radius: 12px;
}
body.template-bitrix24 .feed-com-text-inner-inner {
    min-height: 20px;
}

body.template-bitrix24 a.tasks-kanban-item-image {
    background-size: cover;
    width: calc(100% + 9px);
    max-height: 15px;
    transition: 0.4s ease;
    margin: 4px -6px 4px -6px;
    position: relative;
}
body.template-bitrix24 a.tasks-kanban-item-image:after {
    content: "";
    position: absolute;
    top: 0px;
    left: 0px;
    width: 100%;
    height: 100%;
    background: linear-gradient(rgba(0, 0, 0, 0.2), rgba(0, 0, 0, 0) 50%, rgba(0, 0, 0, 0) 70%, rgba(0, 0, 0, 0.05));
    transition: 0.2s;
}

body.tasks-kanban-item:hover {
    transform: translateY(0px);
}

body.template-bitrix24 .tasks-kanban-item:hover a.tasks-kanban-item-image:after {
    opacity: 0;
}

body.template-bitrix24 .tasks-kanban-item:hover a.tasks-kanban-item-image {
    max-height: 120px;
}
body.template-bitrix24 .tasks-kanban-item-without-control {
    padding: 3px 3px 3px 6px!important;
}
body.template-bitrix24 .tasks-kanban-item-shadow {
    border-top: 0;
}
body.template-bitrix24 .main-kanban-column {
    min-width: 200px;
    max-width: 320px;
}
body.template-bitrix24 .main-kanban-column-add-item-button,
body.template-bitrix24 .main-kanban-column-subtitle-box,
body.template-bitrix24 .main-kanban-column-subtitle {
    height: 20px;
    min-height: 20px;
}
body.template-bitrix24 .tasks-kanban-item-timelogs {
    padding-top: 3px;
}
body.template-bitrix24 .tasks-kanban-item-info {
    margin: 6px 0 2px 0;
}
body.template-bitrix24 .main-kanban-column-subtitle-box {
    padding-top: 2px;
}
body.template-bitrix24 .main-kanban-column-add-item-button:before {
    height: 16px;
    width: 16px;
}

/* Left menu and header */
body.template-bitrix24 a.menu-item-link span {
    letter-spacing: -0.5px;
}

body.template-bitrix24 a.menu-item-link {
    padding-left: 35px;
}

body.template-bitrix24 .menu-favorites-more-btn {
    padding-left: 35px;
}

body.template-bitrix24 .menu-favorites-settings-btn {
    padding-left: 35px;
}

body.template-bitrix24 .menu-favorites-settings-btn span {
    font-size: 10px;
}

body.template-bitrix24 .bx-layout-inner-left {
    max-width: 220px;
}

body.template-bitrix24 .pagetitle-toolbar-field-view .pagetitle {
    padding: 5px 0px;
}

body.template-bitrix24 .pagetitle-toolbar-field-view .pagetitle-inner-container {
    min-height: 55px;
}

body.template-bitrix24 .pagetitle-wrap {
    min-height: 55px;
}

body.template-bitrix24  .header-search-inner {
    border-radius: 4px;
}

body.template-bitrix24 .header-logo-block {
    margin-right: 11px;
}
}
