/* ==UserStyle==
@name         TVmaze DARK PROFESSIONAL 2018
@namespace    de.sidneys.userstyles
@version      1.0.0
@description  A modern dark theme for TVmaze.com. Very gentle on your eyes.
@author       sidneys <sidneys.github.io@outlook.com> (https://github.com/sidneys)
@homepageURL  https://userstyles.org/users/301302
@supportURL   https://forum.userstyles.org/messages/add/sidneys
@license      MIT
@preprocessor default
==/UserStyle== */

@-moz-document domain("tvmaze.com") {
/* ====================================
   VARIABLES
   ==================================== */

:root
{
  --color-black-transparent: rgba(0, 0, 0, 0);
  --color-black-translucent: rgba(0, 0, 0, 0.8);
  --color-background-0: rgb(35, 35, 35);
  --color-background-1: rgb(40, 40, 40);
  --color-background-2: rgb(50, 50, 50);
  --color-background-3: rgb(60, 60, 60);
  --color-background-4: rgb(75, 75, 75);
  --color-text-0: rgb(200, 200, 200);
  --color-text-1: rgb(210, 210, 210);
  --color-text-2: rgb(230, 230, 230);
  --color-text-2: rgb(240, 240, 240);
  --color-text-4: rgb(255, 255, 255);
  --color-accent-green-0: rgb(74, 181, 172);
  --color-accent-green-1: rgb(146, 211, 205);
  --color-accent-green-2: rgb(219, 240, 238);
}

}

@-moz-document domain("tvmaze.com") {
/* ====================================
   COMPONENTS
   ==================================== */


/* ====================================
   GLOBAL
   ==================================== */

body
{
    background-color: var(--color-background-2);
    color: var(--color-text-4);
}

::selection,
::-moz-selection
{
    background: var(--color-text-4);
    color: var(--color-background-0);
}

a,
a:hover
{
    color: var(--color-accent-green-0);
}

h1,
.h1
{
    font-size: 2.5rem;
    font-weight: 400;
}


/* ====================================
   ADS
   ==================================== */

div.row:nth-child(3) > div:nth-child(1) > section.grid-x,
#right-side-ad,
.blad-300x600,
.blad-300x250
{
    display: none;
}


/* ====================================
   NAVIGATION
   ==================================== */

#site-header
{
    background-color: var(--color-background-0);
}

.breadcrumbs
{
    background-color: var(--color-background-4);
    box-shadow: none;
}

.breadcrumbs li
{
    color: var(--color-accent-green-0);
}

.pagination a,
.pagination button
{
    color: var(--color-text-0);
}

header.column,
header.columns
{
    padding-top: 1rem;
}


/* ====================================
   FORM
   ==================================== */

label
{
    color: var(--color-text-1);
}

[type="text"],
[type="password"],
[type="date"],
[type="datetime"],
[type="datetime-local"],
[type="month"],
[type="week"],
[type="email"],
[type="number"],
[type="search"],
[type="tel"],
[type="time"],
[type="url"],
[type="color"],
textarea,
select,
.redactor-editor,
.redactor-box,
.select2-container--default .select2-selection--single,
.select2-dropdown
{
    background-color: var(--color-background-1);
    border-color: var(--color-background-4);
    color: var(--color-text-1);
    border-width: 0.5px;
}

[type="text"]:focus,
[type="password"]:focus,
[type="date"]:focus,
[type="datetime"]:focus,
[type="datetime-local"]:focus,
[type="month"]:focus,
[type="week"]:focus,
[type="email"]:focus,
[type="number"]:focus,
[type="search"]:focus,
[type="tel"]:focus,
[type="time"]:focus,
[type="url"]:focus,
[type="color"]:focus,
textarea:focus,
select:focus
{
    background-color: var(--color-background-1);
}

select,
select:focus
{
    color: var(--color-black-transparent);
    text-shadow: 0 0 var(--color-text-1);
}

.redactor-toolbar
{
    background-color: var(--color-background-0);
}

.redactor-toolbar li a
{
    color: var(--color-text-0);
}


/* ====================================
   CALLOUT
   ==================================== */

.callout
{
    background-color: var(--color-background-3);
    color: var(--color-text-1);
    box-shadow: 0 1px 3px 1px var(--color-background-0);
}

.callout.green,
.callout.green p,
.callout.primary
{
    background-color: var(--color-background-3);
    color: var(--color-accent-green-1);
    border-width: 0.5px;
}

.callout.green a:link,
.callout.green a,
.callout.green p a:link,
.callout.green p a,
.callout.primary a:link,
.callout.primary a
{
    color: var(--color-accent-green-2);
}

/* ====================================
   MODAL
   ==================================== */

.reveal
{
    background-color: var(--color-background-0);
    border: 0.5px solid var(--color-accent-green-1);
}

.reveal h2,
.reveal .h2
{
    font-size: 1.5rem;
    padding-bottom: 0.5rem;
}

.reveal .button
{
    margin: 0;
}


/* ====================================
   TABLE
   ==================================== */

table tbody
{
    border-color: var(--color-background-0);
    background-color: var(--color-background-1);
}

table tbody tr
{
    border-bottom: 0.5px solid var(--color-background-3);
}

table thead,
table tfoot
{
    background-color: var(--color-background-0);
}

table tbody tr:nth-child(even)
{
    border-bottom: 0;
    background-color: var(--color-background-2);
}

table.unstriped tbody
{
    background-color: var(--color-background-2);
}

table.unstriped tbody tr
{
    background-color: var(--color-background-1);
}

#airing-today th
{
    background-color: var(--color-background-0);
}
}

@-moz-document domain("tvmaze.com") {
/* ====================================
   PAGES
   ==================================== */


/* ====================================
   HOME
   ==================================== */

#popular-shows-today .card,
#fp-articles .card
{
   background-color: var(--color-background-1);
}

/* ====================================
   SEARCH
   ==================================== */

#search .source,
#search .year
{
    color: var(--color-text-1);
}

#search footer
{
   background-color: var(--color-background-1);
}


/* ====================================
   SHOW
   ==================================== */

#general-info-panel,
.generic-info-panel
{
    background-color: var(--color-background-1);
}

#general-info-panel h2,
.generic-info-panel h2
{
    color: var(--color-text-4);
}

#main-img {
    position: relative;
    margin: 0;
    padding-bottom: 0;
}

#main-img #watched,
#main-img #following
{
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    padding: 1rem 0;
    background: transparent;
    filter: opacity(0.75);
}

/* ====================================
   PROFILE
   ==================================== */

#profile-settings .nav a,
#user-profile .nav a
{
    background-color: var(--color-background-1);
}

#follow-list .callout
{
    box-shadow: none;
}

#follow-list ul li:nth-child(2n+1),
#follow-list ol li:nth-child(2n+1)
{
    background-color: var(--color-background-0);
    border-bottom: 0.5px dashed var(--color-accent-green-1);
}
}
