css:
@-moz-document domain("fhir.org"), domain("hl7.org") {
body {
    background: #333;
    color: #DDD;
}
.row {
    background: #333;
}
.container {
    background-color: #222;
}
a,
a:hover,
a:focus {
    color: #5eb3fd;
}
a[style] {
    opacity: 1 !important;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: #999;
}
pre,
pre.spec,
pre.json,
pre.rdf,
pre.xml,
pre.fhirpath,
pre.graphql,
code {
    background-color: #222;
    color: #FFF;
    border-color: #444;
}
div.example {
  background-color: #333;
  border-color: #555;
}
div.use,
table.dict td.structure {
    background-color: #222;
    border-color: #555;
}
.highlighter-rouge {
    color: #ADA
}
.ui-tabs,
.ui-tabs-panel,
.ui-widget-content,
.ui-tabs .ui-tabs-panel,
.ui-widget-header,
.ui-state-default,
.ui-widget-content .ui-state-default,
.ui-widget-header .ui-state-default,
table.cols td,
td[style] {
    background-color: #333 !important;
    border-color: #555;
    color: #AAA;
    background-image: none;
}
th[style] {
    background-color: #222 !important;
    border-color: #555 !important;
    color: #AAA
}
tr[style] {
    border-color: #222 !important;
}
table.grid td {
    background-color: #333;
    border-color: #333;
}
table.colsi td,
table.colsn td  {
   background-color: #232;
}
img {
    -webkit-filter: grayscale(100%);
    /* Safari 6.0 - 9.0 */
    filter: grayscale(100%);
}
blockquote {
    border-left-color: #555;
}
.breadcrumb {
    background-color: #111;
    border-radius: 0px;
}
.breadcrumb a {
    color: #bbb;
}
.breadcrumb .btn-link {
    background-color: #111;
    border-color: #000!important;
    border-radius: 0px;
}
.nav-tabs > li > a,
.nav-tabs > li.active > a,
.nav-tabs > li.active > a:hover,
.nav-tabs > li.active > a:focus .active {
    background-color: #333 !important;
    border-color: #666
}
#segment-content > div > div > div > div > p:nth-child(52) {
    background-color: #222;
}
#segment-content p[style] {
    background-color: #222 !important;
}

#segment-content > div > div > div > div > p:nth-child(1) {
    background-color: #444 !important;
    border-color: #ffdb00 !important;
    color: #EEE
}
#IG-status {
    background-color: #000;
}
#stripe {
    background-color: #300;
}

#segment-footer,
#segment-footer .container {
    background-color: #222;
}
#segment-header,
#segment-header .container {
    background-color: #000;
}
#publish-box {
   border-color: #ffdb00 !important;
   background-color: #554901 !important;
}
#diagram {
   background-color: #222;
   border-color: #000;
}
#diagram .box .head {
   background-color: #333;
}
#diagram .box {
  background-color: #444;  
  border-color: #555;
}
#markdown-toc {
   background-color: #222 !important;
   border-color: #666 !important;
}

}
