css:
@-moz-document regexp("https\\://bbs\\.saraba1st.com/2b.*") {
/* I ❤ S1 & I ❤ Ads */
  /* 禁用广告 */
.a_h, .a_mu, .a_c, .a_p, .a_f, .a_t, .ad {
    display: none;
}
  
tr [colspan='5'] {
   display: none; 
}

.tl #forumnewshow [colspan='5'] {
    display: table-cell;
}
}

@-moz-document regexp("https\\://bbs\\.saraba1st.com/2b/forum\\.php.*") {
.fl, .bm {
    border: 2px solid #D1D9C1;
    border-radius: 4px;
}
}

@-moz-document regexp("https\\://bbs\\.saraba1st.com/2b.*") {
/* 通用内容 */

/* Top Actions 顶部操作 */
#toptb {
    border: 0;
    height: 6px;
    background: transparent;
}

/* 个人信息 */
.avt img {
    border-radius: 28px;
    border: 0;
    box-shadow: 0 0 1px 1px rgba(0,0,0,.2);
}

/* 导航 */
#nv, #mu, #scbar {
    border: 0;
}

#nv {
    border-radius: 4px;
    box-shadow: 0px 1px 1px 1px rgba(0, 0, 0, .3);
}

#qmenu {
    border-radius: 4px;
}

#qmenu_menu {
    border-radius: 4px;
    margin-top: 2px;
}


#scbar_form {
    display: flex;
    justify-content: flex-end;
}

#scbar_form table {
    flex: 1;
}

/* Footer */
#ft {
    border: 0;
    padding: 0 0 10px;
}

#frt {
    display: none;
}
}

@-moz-document regexp("https\\://bbs\\.saraba1st.com/2b/forum.*") {
/* 帖子列表 */

/* 版头 */
.bml, .bmw, #f_pst {
    border: 2px solid #D1D9C1;
    border-radius: 4px;
}

/* 子版块 */

.bmw .bm_h {
    background: #D1D9C1;
    border: 0 !important;
}

/* 页码导航 */
.pg a,
.pg strong,
.pgb a,
.pg label,
.pg input {
    border-radius: 4px;
    border: 0;
}

.pg label .px {
    padding: 0 4px;
    width: 16px;
}

.pg a,
.pgb a,
.pg label {
    background-color: #EBF2DE;
}

#thread_types {
    border: 0;
}

/* 标签 */
.ttp .a a {
    padding: 3px 12px 3px;
    height: 17px;
    border: 1px solid #D1D9C1;
}

.ttp a, .ttp strong {
    border-radius: 4px;
    display: flex;
    align-items: center;
    border: 1px solid #D1D9C1;
}

.ttp a {
    transition: .3s;
    padding: 3px 10px 3px;
    height: 17px;
}

.ttp .num {
    padding: 0 5px;
    font-size: 9px;
    background-color: #A3AA94;
}

.ttp a:hover {
    background-color: #D1D9C1;
    border: 1px solid #D1D9C1;
}

/* 帖子列表 */


.tl #forumnewshow {
    background: transparent;
}

.tl #forumnewshow a {
    width: 300px;
    border: 1px solid #F4E4B4;
    border-radius: 14px;
    background: #FFFAF3;
    margin: 2px auto;
    height: 25px;
    line-height: 25px;
    text-indent: 0;
    transition: .3s;
}

.tl #forumnewshow a:hover {
    background: #F4E4B4;
}

.tl #forumnewshow th:hover, .tl #forumnewshow td:hover {
    background: transparent;
}

.tl .th {
    background-color: #D1D9C1;
    border: 0;
    transition: .3s;
}

.tl .th:hover {
    background-color: #CCCC99;
}

.tl th, .tl td {
    border: 0;
    padding: 6px 1.5em 6px 0;
}

.tl tr th, .tl tr td {
    transition: .3s;
}

.tl .ts th, .tl .ts td {
    background-color: #D1D9C1;
}

.xst {
    font-size: 13px;
}

/* 下一页 */
#autopbn {
    border: 0;
    background: #D1D9C1;
    transition: .1s;
}
#autopbn:hover {
    background: #CCCC99;
}

/* 快速发帖 */
#f_pst .pls {
    background: transparent;
    padding: 12px 0 0;
}

#f_pst .tedt {
    border: 2px solid #D1D9C1;
    border-radius: 4px;
}

.tedt .bar {
    background: #D1D9C1;
    border: 0;
}

.bm_h {
    background: #D1D9C1;
    border: 0;
}
}

@-moz-document regexp("https\\://bbs\\.saraba1st.com/2b/(thread|forum\\.php\\?mod=viewthread).*") {
/* 帖子 */

/* 页码导航 */
.pg a,
.pg strong,
.pgb a,
.pg label,
.pg input {
    border-radius: 4px;
    border: 0;
}

.pg label .px {
    padding: 0 4px;
    width: 16px;
}

.pg a,
.pgb a,
.pg label {
    background-color: #EBF2DE;
}

/* 楼内主内容样式 */
.bm {
    border: 0;
}

#postlist .pbn {
    border: 0;
    /*background-color: #D1D9C1;*/
    border-radius: 4px;
}

#postlist table {
    border-radius: 4px;
    border: 2px solid #D1D9C1;
    border-collapse: separate;
    margin: 4px 0;
    background-color: #D1D9C1;
}

#postlist table table {
    border: 0;
    background-color: #F6F7EB;
}

.pls {
    border-right: 0;
}

td~.plc, .plc.plm {
    background-color: #F6F7EB;
}

.pls table {
    background-color: #D1D9C1 !important;
}

tr:first-child .plc {
    border-radius: 4px 4px 0 0;
}

tr:nth-last-child(2) .plc {
    border-radius: 0 0 4px 4px;
}

.plhin {
    border: 0;
}

/* 投票 */

.pcht table tr.ptl td {
    border-bottom: 1px dashed #D1D9C1;
}

.pcht table tr.ptl:first-child td {
    border-top: 1px dashed #D1D9C1;
}


/* 文本区 */
.pi {
    border: 0;
    padding: 16px 0 12px;
    margin: 0;
}

.po {
    border: 0;
    padding: 8px 0;
}

.hin {
    transition: .1s;
}

/* 引用 */
.pl .quote {
    border-radius: 4px;
    /*box-shadow: 1px 1px 1px 1px rgba(0, 0, 0, .08);*/
    /*min-width: 42em;
    display: inline-block;*/
    background: url(https://static.saraba1st.com/image/s1/icon_quote_s.gif) no-repeat 20px 6px, linear-gradient(to right, #F9F9F9 , #F6F7EB);
}

/* 评分*/
.ratl img {
    border-radius: 14px;
    border: 2px solid #fff;
    box-shadow: 0 0 1px 1px rgba(0, 0, 0, .2);
}

/* 楼内个人信息 */
.pls .favatar {
    max-height: 190px;
    transition: .3s;
    overflow: auto;
    overflow-x: hidden;
}
.pls:hover .favatar {
    max-height: 440px;
}
.pls .avatar {
    margin: 10px 15px 0;
    min-height: 150px;
}
.pls .avatar~img {
    display: none;
}
.pls .avatar::after {
    content: "﹀";
    display: block;
    margin-top: 4px;
    text-align: center;
}

.pls .avtm {
    height: 120px;
    min-height: 120px;
    display: flex;
    align-items: center;
    border-radius: 64px;
}

/* 下一页 */
.pgbtn a {
    border: 0;
    background: #D1D9C1;
    transition: .1s;
}
.pgbtn a:hover {
    background: #CCCC99;
}

/* 回复区 */
#f_pst .pls {
    background: transparent;
    padding: 12px 0 0;
}

#f_pst .tedt {
    border: 2px solid #D1D9C1;
    border-radius: 4px;
}

.tedt .bar {
    background: #D1D9C1;
    border: 0;
}
}
