@-moz-document domain("translate.google.com") {
/*MAIN BACKGROUND*/
body {
    background: linear-gradient(#666666 30%, #262626 100%);
    color: #bfbfbf;
}

.frame::before {
    background-color: #666666;
    border: solid #666666;
}


.lang-btn {
    background-color: #8c8c8c;
}

.ls-select {
    color: #cccccc;
}

/*MAIN BACKGROUND*/
/*LEFT MENU*/
.gb_oc,
.gb_yc {
    background-color: #4d4d4d;
    color: #cccccc;
}

.gb_xc,
.gb_yc {
    color: #cccccc;
}

.gb_wc:not(:last-child),
.gb_Hc:not(:last-child) {
    border-bottom: 1px solid #444444;
}

.gb_re .gb_Fc, .gb_re .gb_fe {
    background-color: #4d4d4d;
    border-bottom: 1px solid #444444;
}

/*LEFT MENU*/
/*HEADER*/
#gb {
    border-bottom: 1px solid;
}

.gb_ke, .gb_qe, .gb_ue, .gb_oc, .gb_yc {
    background-color: #4d4d4d;
    color: #cccccc;
}

.gb_da, .gb_aa, .gb_ea, .gb_fa, .gb_vc, .gb_ve, .gb_Be {
    background-color: #4d4d4d !important;
}

.gb_X, #gb a.gb_ea, .gb_vc, .gb_Wa a, .gb_vc a,
#gb a.gb_ea:visited, #gb a.gb_ea:active, #gb a.gb_fa, #gb a.gb_fa:visited {
    color: #cccccc;
}

.gb_T:hover a,
.gb_Dc:not(:last-child) {
    border: 1px solid #444444;
}

.gb_T:hover .gb_X {
    background-color: #666666;
}

/*HEADER*/
/*TOP BUTTON*/
.input-button,
.homepage.translate-text .input-button.text-icon {
    color: #bfbfbf;
    transition: all 0.1s ease-in;
}

.input-button:hover,
.input-button:focus,
.homepage.translate-text .input-button.text-icon:hover,
.homepage.translate-text .input-button.text-icon:focus {
    background-color: #595959;
    transition: all 0.1s ease-in;
}

.homepage.translate-text .input-button.text-icon,
.homepage.translate-docs .input-button.documents-icon {
    border: 1px solid #dadce0;
    background: none;
}

/*Documents page*/
.select-file-page {
    background-color: #8c8c8c;
}

.select-file-page .select-file-section .button {
    background-color: #bfbfbf;
    color: #595959;
    border: 3px solid #808080;
    border-radius: 6px;
}

.file-selected-section .file-holder {
    background-color: #bfbfbf;
}

.file-holder {
    border: 3px solid #595959;
    border-radius: 8px;
}

.file-selected-section .button {
    background-color: #bfbfbf;
    border-radius: 8px;
}

/*Documents page*/
/*TOP BUTTON*/
/*BOTTOM ICON*/
.ft-icon-oval {
    background-color: #595959;
    border: solid #333333;
    transition: all 0.3s ease-in;
}

.ft-icon-oval:hover,
.ft-icon-oval:focus {
    background-color: #404040;
    transition: all 0.1s ease-in;
}

.ft-icon-txt {
    color: #bfbfbf;
}

body.displaying-saved-page .footer-icon-container.ft-icon-img-svd .ft-icon-img-ctr .ft-icon-oval,
body.epu-dt.displaying-history-page .footer-icon-container.ft-icon-img-hst .ft-icon-img-ctr .ft-icon-oval {
    background-color: #262626;
    border-color: #262626;
}
/*BOTTOM ICON*/
/*TRANSLATE AREA*/
.sl-sugg .goog-inline-block.jfk-button,
.tl-sugg .goog-inline-block.jfk-button,
.sl-more,
.tl-more {
    color: #bfbfbf;
    /*COLOR TEXT NAME LANGUAGE*/
    transition: all 0.4s ease-in;
}

.sl-sugg .goog-inline-block.jfk-button:hover,
.sl-sugg .goog-inline-block.jfk-button:focus,
.tl-sugg .goog-inline-block.jfk-button:hover,
.tl-sugg .goog-inline-block.jfk-button:focus,
.sl-more:hover,
.sl-more:focus,
.tl-more:hover,
.tl-more:focus {
    background-color: #666666;
    transition: all 0.2s ease-in;
}


.ls-wrap {
    width: 200.3%;
    /*FIX WRAP WIDTH*/
}

.ls-wrap,
.swap.jfk-button,
.swap-wrap {
    background-color: #8c8c8c;
}

.goog-inline-block.jfk-button-checked::after {
    border-bottom: solid 3px #bfbfbf;
}

.ls-wrap .sugg-fade {
    display: none;
}

.source-wrap,
.orig,
/*LEFT AREA & CORRECT*/
.gt-spell-correct-message,
.tlid-transliteration-content,
.gt-related-suggest-message,
.source-input, 
.ita-ppe-box,
.ita-kd-menuitem-hover {
    background-color: #a6a6a6;
    color: #4d4d4d;
}

.ita-kd-menuitem-hover {
    background-color: #a6a6a6 !important; 
    color: #4d4d4d;
}

.ita-kd-dropdown-menu {
    border: 3px solid #808080;
    border-radius: 8px;
}

/*GO BUTTON*/
.jfk-button-action {
    background-image: -moz-linear-gradient(top, grey, grey);
}
/*GO BUTTON*/
body:not(.with-lang-list) .results-container.empty,
body:not(.with-lang-list) .main-header.long-text .results-container.empty,
/*RIGHT AREA*/
.main-header.long-text .results-container,
.main-header.long-text .result,
.main-header.long-text .gt-edit,
.main-header.long-text .contribute-target,
.results-container, .result, .gt-edit, .contribute-target {
    background-color: #a6a6a6;
    border-left: 2px solid #bfbfbf;
    border-radius: 0 0 8px 0;
    /*BORDER RADIUS FIX*/
}

.main-header.long-text .results-container:not(.empty) .placeholder,
.main-header.long-text .result-dict-wrapper .translation,
.main-header.long-text .contribute-target,
.main-header.long-text #st-buttons .jfk-button {
    color: #4d4d4d;
}

.alt-menu {
    background-color: #a6a6a6;
    border: 3px solid #808080;
}

.alt-menu .goog-menuitem-highlight,
.ita-kd-dropdown-menu,
.goog-menu {
    background-color: #b3b3b3;
}

.goog-menu.round-trip-content {
    border: 2px solid #808080;
}

span {
    transition: all 0.4s ease-in;
}

.trans-edit,
.trans-target-highlight {
    background-color: #d9d9d9;
    transition: all 0.2s ease-in;
}

.gt-is-mobile {
    background-color: #a6a6a6;
    border: 3px solid #808080;
}
.gt-is-mobile .gt-is-ld {
    border-top: 1px solid #444444;
}
/*TRANSLATE AREA*/
/*BOTTOM MENU*/
.gt-lc.gt-lc-mobile .gt-cc-r-i {
    border-radius: 10px;
}

.epu-dt .gt-lc.gt-lc-mobile .gt-cd,
.epu-dt .gt-lc.gt-lc-mobile.show-as-one-card {
    border: 3px solid #808080;
}

.gt-lc.gt-lc-mobile .gt-cd,
.epu-dt .gt-cc-t,
.epu-dt .gt-lc.gt-lc-mobile.show-as-one-card {
    background-color: #a6a6a6;
}

.epu-dt .gt-lc.show-as-one-card .gt-cc-r {
    border-right: none;
    width: 49%;
}
.epu-dt .gt-lc.gt-lc-mobile.show-as-one-card .gt-cc {
    border-top: 1px solid #808080;
}

.epu-dt .gt-lc.show-as-one-card .gt-cc-l {
    border-left: 1px solid #808080;
}

.epu-dt .gt-lc-mobile .gt-baf-word-clickable {
    transition: all 0.4s ease-in;
}

.epu-dt .gt-lc-mobile .gt-baf-word-clickable:hover {
    background-color: #b3b3b3;
    transition: all 0.2s ease-in;
}

.gt-baf-word-selected {
    background-color: #bfbfbf;
    border: 2px solid #808080;
    border-radius: 6px;
}

.epu-dt .gt-lc.gt-lc-mobile .gt-cd-cl,
.epu-dt .gt-lc.gt-lc-mobile .gt-cd-ncl {
    background-color: #bfbfbf;
    border: 3px solid #808080;
}

.gt-lc.gt-lc-mobile .gt-cc-r-i {
    background-color: #a6a6a6 !important;
}

/*BOTTOM MENU*/
/*LANGUAGE LIST*/
.language-list-search-box,
#sl_list-search-box,
#tl_list-search-box {
    background-color: #808080;
}

.epu-dt .language-list-toolbar {
    border: none;
}


.language-list,
.language_list_sl_list,
.language_list_tl_list,
.language_list_item_wrapper,
.language_list_section {
    background-color: #8c8c8c;
    transition: all 0.3s ease-in;
}


.language_list_item_wrapper.item-selected,
.epu-dt .language_list_item_wrapper:hover,
.epu-dt .language_list_item_wrapper:focus {
    background-color: #a6a6a6;
    transition: all 0.1s ease-in;
}

.language_list_item_language_name {
    color: #4d4d4d;
}
/*SELECTED BUTTON*/
.epu-dt .language_list_item_wrapper.item-selected {
    background-color: #bfbfbf;
    color: grey;
}

.epu-dt .language_list_item_wrapper.item-selected .language_list_item {
    color: black;
}
/*SELECTED BUTTON*/
/*LANGUAGE LIST*/
/*HISTORY & SAVES*/
body.displaying-saved-page,
body.epu-dt.displaying-history-page,

.history-body,
history-top-header,
.history-top-bar,
.epu-dt .history-container .info-bar,
.displaying-saved-page .phrasebook-page,
.phrasebook-top-header,
.phrasebook-container .nav-bar,
.phrasebook-container,
.epu-dt .history-container,
.phrasebook-container:not(.empty-search-query) .phrasebook-entry.browsed,
.phrasebook-entry.focus-within,
.phrasebook-entry:focus-within {
    background-color: #4d4d4d;
}

.epu-dt .history-page,
.epu-dt .history-top-header {
    border-bottom-color: #434343;
}

.epu-dt .history-top-header .title,
.epu-dt .history-container .info-bar,
.epu-dt .history-container .history-features .delete-all-button,
.phrasebook-top-header .title,
.phrasebook-container .phrasebook-features .sort-button,
.phrasebook-container .nav-bar {
    color: #999999;
}

.epu-dt .history-entry .tl-input,
.epu-dt .history-entry .tl-output,
.phrasebook-entry .tl-input,
.phrasebook-entry .tl-output {
    color: #cccccc;
}

.phrasebook-container .language-chip {
    background-color: #bfbfbf;
    color: black;
    border: 2px solid #808080;
    border-radius: 6px;
}

.phrasebook-container .language-chip,
.phrasebook-container .language-pair-container .language-chip.selected,
.language-chip:focus,
.language-chip:focus-within {
    transition: all 0.4s ease-in;
}

.phrasebook-container .language-chip:hover,
.phrasebook-container .language-pair-container .language-chip.selected,
.language-chip:focus,
.language-chip:focus-within {
    background-color: #d9d9d9;
    border-color: #d9d9d9;
    color: #595959;
    transition: all 0.2s ease-in;
}

.epu-dt.displaying-history-page .history-page {
    background-color: #4d4d4d;
}

/*HISTORY & SAVES*/
/*Notifications & Drop down menus*/
.epu-dt .gt-cd-pos-pop .help-icon-container .help-tooltip,
.epu-dt .gt-cd-pos-pop .help-icon-container .help-tooltip::after,
.epu-dt .gt-cd-pos-pop .help-icon-container .help-tooltip p,
.phrasebook-container .phrasebook-features .goog-menu {
    background-color: #8c8c8c;
    color: #333333;
}
/*Notifications*/
}
