css:
@-moz-document domain("sourceforge.net") {
.sandiego.l-header-nav-bottom {
    background: #03A9F4;
}

.sandiego.l-header-nav-bottom .search .social-icons a {
    display: flex;
    justify-content: center;
    align-items: center;
    margin-right: 1px;
    margin-left: 0;
    width: 30px;
    min-width: 30px;
    background: #333;
    height: 30px;
    line-height: 30px;
    border-radius: 0.21429rem;
    border: 0;
    box-shadow: 1px 0px 1px rgba(0, 188, 212, 0.62);
}

.sandiego.l-header-nav-bottom .links>a:first-child, .sandiego.l-header-nav-bottom .links>div:first-child, .sandiego.l-header-nav-bottom .links .dev-menu-when-stuck:first-child {
    border-left: 1px solid rgba(47, 47, 47, 0.24);
}

.sandiego.l-header-nav-bottom .links>a, .sandiego.l-header-nav-bottom .links>div, .sandiego.l-header-nav-bottom .links .dev-menu-when-stuck {
    flex-grow: 1;
    text-align: center;
    white-space: nowrap;
    border-right: 1px solid rgba(47, 47, 47, 0.24);
    line-height: 40px;
    max-width: 12em;
}


.sandiego.l-header-nav-collapse.stuck .l-header-nav-bottom .search-toggle-when-stuck, .sandiego.l-header-nav-bottom.stuck .search-toggle-when-stuck {
    -moz-transition: all 0.12s ease;
    -o-transition: all 0.12s ease;
    -webkit-transition: all 0.12s ease;
    transition: all 0.12s ease;
    background: #0993bd;
    border-right: 1px solid rgba(47, 47, 47, 0.24);
    display: block;
    max-height: 40px;
    width: 3.28571rem;
}

.sandiego.l-header-nav-collapse.stuck .l-header-nav-bottom .search-toggle-when-stuck:hover svg, .sandiego.l-header-nav-bottom.stuck .search-toggle-when-stuck:hover svg {
    fill: #03a9f4;
}

#taboola-right-rail-thumbnails
{
    display: none !important;
}

.sandiego.l-header-nav-top .sf-logo svg .st0, .sandiego.l-header-nav-bottom .sf-logo svg .st0 {
    fill: #03a9f4;
}

.sandiego.l-header-nav-collapse.stuck .l-header-nav-bottom .sf-logo svg .st0, .sandiego.l-header-nav-bottom.stuck .sf-logo svg .st0 {
    fill: #4a4a4a;
}

.sandiego#pg_index .intro h2 {
    line-height: 1em;
    font-size: 2em;
    color: #03a9f4;
}
.sandiego#pg_index .intro .stat a {
    color: #03a9f4;
}
.sandiego#pg_index .intro .stat a:hover {
    color: #03c4f4;
}

.m-wide-projects-widget .result-heading-texts .label {
    color: #03a9f4;
}
.sandiego#pg_index .intro .stat
{
    color: #03a6ef;
}

footer.sandiego .l-nav-top .sf-logo svg .st0 {
    fill: #03a9f4;
}

.nel .heading-main .teaser {
    color: #0f2731;
    font-size: 1.1rem;
    font-weight: 700;
    line-height: 1.2em;
    margin: .5rem 0 0 0;
}

.sandiego .nav-dropdown:hover .nav-dropdown-menu {
    display: block;
    z-index: 1005;
    list-style: none;
    position: absolute;
    text-align: left;
    margin-left: 0;
    background: #FFF;
    min-width: 12em;
    box-shadow: 5px 9px 25px rgba(0,0,0,0.3);
}
.sandiego .nav-dropdown:hover .nav-dropdown-menu li a {
    display: block;
    color: #908b8b;
    font-weight: 700;
    line-height: 30px;
    width: 100%;
    border-right: 0;
    border-left: 0;
    padding-left: 1rem;
    padding-right: 2rem;
}

}
