css:
@-moz-document domain("justdancenow.com")
{
/*----------Main-Menu----------*/
video.landing__video,
.footer,
.footer .copyright,
nav,
.footer__ubi-logo,
.footer .menu,
h1.landing-text__title,
.logo__inner,
h2.landing-text__subtitle,
.side-nav,
button.landing-arrow.scroll_down,
.landing-button::before,
.section.availability, .logo,
.section--vertical-content,
.section#how-it-works,
section#explore, .main::before{
    display: none !important;
}
.main {
    background-image: url(https://i.imgur.com/Njxrzeh.png);
    background-color: rgba(0, 0, 0, 0) !important;
    background-size: cover;
    animation: BrightnessCoverBeat 2s linear infinite;
}

    h3.landing-button__play {
    font-family: 'Bahnschrift SemiBold';
    font-size: 20px;
    text-transform: lowercase;
    font-weight: inherit;
}
.landing-button {
    top: 30%;
    left: -0.8%;
    background-image: linear-gradient(to bottom, rgba(253, 152, 2, 0) 0%, rgba(208, 77, 108, 0) 70%);
    border: none;
}
.landing-button:hover {
    background: none;
    border: none;
}
.landing-button:hover .landing-button__play {
    text-shadow: none;
    color: white;
}
.init-spinner {
    top: 40%;
    left: 40%;
    fill: transparent;
    background: none;
    -webkit-animation: fadeInDefault 0.2s linear forwards;
    animation: fadeInDefault 0.2s linear forwards;
}
.dr-landing .init-spinner {
    top: 22%;
    width: 80% !important;
    left: 3.7%;
}
/*----------Settings----------*/
.toggle::before {
    border-left: none;
}
.toggle {
    fill: rgb(121, 9, 245)!important;
    right: 5%;
}
.toggle::after {
    background-color: rgb(216, 239, 249, 0);
}
#settings {
    background-image: url(https://i.imgur.com/PPttTFm.png);
    background-size: 100%;
    background-repeat: no-repeat;
    background-position: 0% 80%;
    right: 0.50%;
}
/*----------Song-Selection----------*/
#just-dance-now {
    background: url(https://i.imgur.com/XKS0TFT.png) center/cover no-repeat;
    background-size: 100% 100%!important;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center
}
#song-selection {
    background-image: url(https://i.imgur.com/lkkIfGt.png);
    background-size: cover;
}
#song-selection:before {
    box-shadow: none;
}
.song--focused {
    transform: scale(1.05, 1.05);
    top: -6%;
}
.song__cover {
    width: 115%;
    -webkit-mask-image: url(https://i.imgur.com/lSMMj7D.png);
     -webkit-mask-size: 103%;
    -webkit-mask-position: center;
    -webkit-mask-repeat: no-repeat;
    top: 2%;
    left: -6.5%
}
.song--focused .song__decoration::after {
    background-image: url(https://i.imgur.com/n5cQr6J.png);
    z-index: 9999999;
    width: 94.5%!important;
    height: 94.5%!important;
    left: 4%;
    top: 12%
}
.song-detail {
    top: 18%;
    left: -1.6%;
}
#preview .song-details {
    left:9%;
    top: 5%;
    border-left: 2px solid #ffffff;
}
#preview .song-details h3 {
    padding-left: 0%;
    font-family: "Bahnschrift SemiBold";
    font-weight: lighter;
    font-size: 20px;
    color: #ca91fe;

}
#preview .song-details h2 {
    padding-left: none;
    font-family: 'Bahnschrift SemiBold';
    font-style: normal;
    top:-1%;
    font-size: 35px;
  
}
.song__reward {
    margin-top:-1.5%
}
.category:before {
    color: #901ef2;
    left: 17.1%;
    top: -1%;
    font-family: "Bahnschrift SemiBold";
    opacity: 1
}
.title-container .title {
    font-size: 14px;
    font-family: "Bahnschrift SemiBold";
    font-weight: 600;
    color: #4c4a4c;
    left: -3%;
}
.title-container {
    top: 75%;
}
.sprite-description, .sprite-val
{
  top:1000000000%
}
.sprites-container
{
top: 100000005%;
}
.connect-phone-info--clickable .connect-phone-info__text::before {
    content: "?";
    font-family: "Bahnschrift SemiBold";
    color: #901ef2;
    background-color: white;
    border-radius: 50%;
    width: 0.2rem;
    height: 0.2rem;
    position: absolute;
    right: -0.3rem;
    top: -0.1rem;
    text-align: center;
    font-size: 70%;
}
.song-action__price-tag__text {
    color: #901ef2;
}
/*----------video-preview----------*/
#preview .left-pane {
    left: 4%;
    top: 3%
}
.video-preview {
    border: none;
    width: auto
}
.video-preview__video {
    border-radius: 15px
}
.state-songselection .pre-beat .song-action__button:not(.transitioning) {
    background-image: url(https://i.imgur.com/6PunExI.png);
    height: 80%;
    width: 80%;
    left: 7.5%;
    top: 17%
}
.state-coachselection .pre-beat .song-action__button:not(.transitioning), #afterdance.song-action__button
{
    background-image: url(https://i.imgur.com/6PunExI.png)!important;
    height: 80%;
    width: 80%;
    left: 7.5%;
    top: 17%
}
/*----------Dance-Room----------*/
#song-selection:after,.song-detail::before,
 .song--focused .song__tag--ppp::before, .state-songselection .song-cover, .state-coachselection .song-cover, .song-cover .song-action__price-tag, .danceroom__qr-code-wrapper {
    display: none!important
}
.danceroom {
    margin-left: -36%;
}
.danceroom__number, .danceroom__label {
    text-shadow: none;
    color: #4c4a4c;
    font-family: "Bahnschrift SemiBold";
    font-size: 38%;
    text-align: center;
}
.danceroom__label {
    margin-top: -2%;
    margin-left: 10%;
}
.state-afterdance #room-info {
    display: none;
}
 .state-dance #room-info
  {
    display: none;
}
/*----------Just-Dance-Logo----------*/
#dance-transition::before {
    background: url(https://i.imgur.com/bDl0RQH.png) center no-repeat, center no-repeat #fff!important;
    background-size: cover!important;
    width: 100%;
    height: 100%;
    z-index: 1
}
/*----------Hi-Score----------*/
 #preview .social-stats.stat-song-rewards,
.social-stats.stat-total-stars, .score__number::before
  {
    display: none!important;
}
.score__display::after
  {
    border-left: 2px solid #ffffff;
	transform: rotate(1deg);
}
.highscore-display
  {
    top: 48%;
    left: 116%;
}
.score__number
  {
    left: 1%;
    margin-top: -0.42em;
    margin-bottom: 0;
    top: 41.5%;
    text-shadow: none !important;
    font-size: 85%;
    text-align: left;
    color: #ffffff;
    font-family: 'Bahnschrift SemiBold';
}
.score__stars
  {
    opacity: 1;
    top: 48%;
    left: 7%;
}
.score__owner .owner-name
  {
    color: #ffffff;
    position: absolute !important;
    font-size: 80%;
    top: -0.2rem;
    left: -2%;
	font-family: 'Bahnschrift SemiBold';
}
/*----------Coach-Selection----------*/
.coach__names-list {
    font-family: "Bahnschrift SemiBold";
}
.song-action__label{
    font-family: "Bahnschrift SemiBold";
    font-size: 28%;
    left: -15%;
}
.coach-selection__title {
    font-family: "Bahnschrift SemiBold";
    font-weight: lighter;
}
.state-coachselection .highscore-display
{
  display: none!important;
}
/*----------Player----------*/
.dancercard .player-name
{
font-family: "Bahnschrift SemiBold";
     font-size: 70%;
    top: 229%;
}
#players .dancercard
  {
    position: absolute;
    top: -28%;
 }
#players .player .controller-coins
{
    font-family: "Bahnschrift SemiBold";
    top:57%;
    left: -2%;
    font-size: 85%;
}
.dancercard .player-color,
.player--new .player-color {
   top:250%;
    margin-left: 16.3%;
    	-webkit-mask-image: url(https://i.imgur.com/ZzqVRGs.png);
    -webkit-mask-size: 100%;
    -webkit-mask-position: center;
    -webkit-mask-repeat: no-repeat;
    overflow:visible;
}
.dancercard .avatar-wrapper
{
    height:150%;
	width:150%;
    top: 130%;
    left: 15%
}
.dancercard .player-flag__image
{
	height:63%;
	width:78%;
    margin-left: -54%;
    margin-top: 130%
}
.dancercard .player-flag__inner
{
  overflow:visible;
}
.dancercard .player-flag
{
	overflow:visible;
}
.dancercard .player-exp
{
  background-image: url(https://i.imgur.com/FkoE57d.png);
}
.state-dance #players
  {
    position: absolute;
    top: 0px;
    left: 17.5%;
    z-index: 990;
    width: 67%;
    height: 20%;
  }
.state-afterdance #players
  {
    display: none;
  }
/*----------HUD----------*/
.hud.beat #players .feedback-label
{
  background-repeat: no-repeat;
  background-image: url(https://i.imgur.com/QyGm2AS.png);
}
#players .feedback-badgold
{
  width: 60%;
  height: 60%;
  left: 16%;
  top: 49%;
}
#players .feedlabel-bad
{
  width: 70%;
  height: 70%;
  left: 16%;
  top: 49%;
}
#players .feedlabel-ok
{
  width: 55%;
  height: 55%;
  left: 23.6%;
  top: 52%;
}
#players .feedlabel-good
{
  width: 55%;
  height: 55%;
  left: 23.1%;
  top: 52%;
}
#players .feedlabel-perfect
{
  width: 100%;
  height: 100%;
  top: 32%;
}
#players .feedlabel-yeah
{
  width: 100%;
  height: 100%;
  left: -3%;
  top: 18%;
}
#players .crown::before
{
  -webkit-animation: Corona 1s infinite alternate;
  animation: Corona 1s infinite alternate;
  background-image: url(https://i.imgur.com/HOBSjfW.png)!important;
  background-color: transparent!important;
  border: none;
    top: 10%;
    left: 0%;
}
/*----------STARS----------*/
#players .star {
    background-image: url(https://i.imgur.com/TvzjXIV.png)!important;
}
#racetrack .star.star-visible,
#afterdance .results .stars .star,
.score__stars.star {
    background-image: url(https://i.imgur.com/TvzjXIV.png)!important;
}
#players [data-stars="5"] .star,
[data-stars="5"] .star {
    background-image: url(https://i.imgur.com/TvzjXIV.png)!important;
}
#players [data-stars="4"] .star,
[data-stars="4"] .star {
    background-image: url(https://i.imgur.com/TvzjXIV.png)!important;
}
#players [data-stars="3"] .star,
[data-stars="3"] .star {
    background-image: url(https://i.imgur.com/TvzjXIV.png)!important;
}
#players [data-stars="2"] .star,
[data-stars="2"] .star {
    background-image: url(https://i.imgur.com/TvzjXIV.png)!important;
}
#players [data-stars="1"] .star,
[data-stars="1"] .star {
    background-image: url(https://i.imgur.com/TvzjXIV.png)!important;
}
/*----------In-Game----------*/
#racetrack .star {
    background: url(https://i.imgur.com/VBpRIEE.png) 50% 50% no-repeat;
    background-size: cover;
}
#players .star::before {
     display: none;
}
.stars.player-stars
{
  top: 58%;
    margin-left: 0.4%;
     transform: scale(0.6);
    overflow:visible;
}
#players .star {
    width:0.4rem!important;
	height:0.4rem!important;
    transform: rotate(-20deg)!important;
}
#lyrics {
    font-family: "Franklin Gothic";
    font-weight: lighter;
    left: 4.3%;
    top:89%;
    font-size: 17.5%;
}
#lyrics .line.current,
#lyrics .line.previous
{
    margin-top: -0.5% !important;
}

#lyrics .line
{
    margin-top: 0.8% !important;
}
#racetrack.animate .platform {
    display: none;
}
#racetrack { 
 top:22%;
}
#racetrack .progress-bar {
    left: 60%;
    width: 51%;
}
/*----------coach----------*/
#pictos {
    width: 29.6%;
    z-index: 9999
}
#pictos.multi-coach {
    width: 41.2%!important;
    right: 0%!important;
}
.picto {
    filter: drop-shadow(4px -2px 2px gray);
    left: -1.5%
}
.multi-coach .picto {
    left: -9%
}
/*----------Tutorial----------*/
#just-dance-now .tutorial {
position: relative;
	width: 100%;
	height: 100%;
background-image: url(https://i.imgur.com/DE2FaOK.png);
	background-position: bottom;
	background-size: cover;
	background-repeat: no-repeat;
}
/*----------Modal----------*/
#just-dance-now .modal .concept p, #just-dance-now .modal span {
font-family: "Bahnschrift SemiBold";
}
.modal__close {
font-family: "Bahnschrift SemiBold";
}
.concept__title {
font-family: "Bahnschrift SemiBold";
}
.modal__container::before {
    content: '';
    background-color: #6500fe;
    opacity: 1;
    width: 80%;
    height: 140%;
    -webkit-transform-origin: top left;
    -ms-transform-origin: top left;
    transform-origin: top left;
    -webkit-transform: rotate(-12deg);
    -ms-transform: rotate(-12deg);
    transform: rotate(-12deg);
    position: absolute;
    z-index: -1;
}
.steps__number {
    background: #ffffff;
    color: #7909f5;
    border: 0.035rem solid #5506ac;
    width: 0.55rem;
    height: 0.55rem;
    display: inline-block;
    border-radius: 50%;
    text-align: center;
    font-size: 0.4rem !important;
    font-family: "Bahnschrift SemiBold"!important;
    line-height: 0.48rem;
    position: absolute;
    top: 5%;
}
}
