css:
@-moz-document domain("bitbucket.org") {
* {
    background-color: #0b0b0b !important;
    color: #fff9;
}
code {
    color: white;
}
*::selection {
    background-color: #ffb7b7 !important;
}
.mtk20 {
    color: #fff9;
}
.mtk26 {
    color: #2263d2;
}
.mtk1 {
    color: #fffffff5;
}
.mtk35 {
    color: #e50606;
}
.mtk6 {
    color: hsla(240, 27%, 50%, 1);
}
body {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-attachment: scroll !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    color: #FAFAFA !important;
}

div[role="alert"] {
    background: #161616 !important;
}

div[role="alert"] > div > div {
    color: #FAFAFA !important;
}

body.adg3 .aui-dialog2 > * {
    background: #161616 !important;
    color: #FAFAFA !important;
}

.aui-message,
.aui-message.info,
.repo-watch-container > div > div,
.dialog-page-body,
.dialog-title,
.dialog-button-panel,
#share-repo-dialog,
.share-form > form > table > tbody > tr,
.workflow-box {
    background: #161616 !important;
    color: #FAFAFA !important;
}

body.adg3 .aui-dropdown2.aui-style-default .active,
.bb-typeahead-suggestions .bb-typeahead-item a {
    background: #161616 !important;
    color: #FAFAFA !important;
}

body.adg3 .aui-dropdown2.aui-style-default:hover a:hover:not(.aui-dropdown2-disabled) {
    background: #0973BF !important;
    color: #FAFAFA !important;
}

.TSSGp {
    background: #0973BF !important;
}

.jQxoqj {
    background-color: rgba(9, 30, 66, 0.42) !important;
}

a,
a:visited,
button {
    color: #0973BF !important;
}

body.adg3 .aui-button.aui-button-primary {
    background: #0973BF !important;
    color: #FAFAFA !important;
}

.gLYoj,
.bYLQmh,
.aui-page-panel-nav,
.iYJWvo,
.ketCUk,
#adg3-navigation > div > div > div > div {
    background: #1C1C1C !important;
    border-right: solid #0973BF 1px !important;
}

a[role="menuitem"]:hover,
a[role="menuitem"]:focus {
    background: #161616 !important;
}

.gSdlKe,
.fWTyfo {
    background: #131313 !important;
}

body.adg3 .aui-inline-dialog .aui-inline-dialog-contents,
body.adg3 .aui-inline-dialog .contents,
body.adg3 .aui-tabs.horizontal-tabs > .tabs-menu > .menu-item > a,
body.adg3 .aui-tabs.horizontal-tabs > .tabs-menu > .menu-item,
.download-repo,
.aui.repository-list > tbody > tr {
    background: #161616 !important;
}

h1,
h2,
h3,
h4,
h5,
h6,
.fkiETL,
.aui-nav-heading,
.select2-result-label,
.filter-label,
.size,
table.aui > tbody > tr > td,
time,
div[data-role="droplistGroupHeading"] > span,
.pull-request-list-filter,
.blank-state--text,
small {
    color: #FAFAFA !important;
}

.newsfeed .news-item time,
.stat-label,
.metadata-label,
label,
legend > span,
.graph-container > span,
#commit-header .commit-tags,
.metadata,
.user-ip,
.repository-list--byline {
    color: #CCC !important;
}

.stat-label:hover {
    color: #FAFAFA !important;
    text-decoration-line: underline !important;
}

form.aui .buttons-container,
.aui-page-panel,
.iterable-item {
    background: transparent !important;
}

.iterable-item:hover {
    background: #161616 !important;
}

.aui-button {
    background: #161616 !important;
    border: solid #CCC 1px !important;
}

.aui-button:hover {
    border: solid #0973BF 1px !important;
}

.fVojKL,
.gVewQh > a,
.aui-nav-selected,
.filter-status > li[aria-pressed="true"] a {
    background: #161616 !important;
}

.fPheOp > div > div:hover,
.WPmns:hover,
.aui-navgroup-inner > ul > li > a:hover,
.filter-status > li a:hover,
.aui-navgroup-vertical .aui-nav > li > a:focus {
    background: #131313 !important;
}

.readme-edit-button.aui-button.aui-button-subtle {
    background: transparent !important;
}

.readme-edit-button.aui-button.aui-button-subtle:hover {
    background: #1C1C1C !important;
    border: solid #0973BF 1px !important;
    margin-top: -1px !important;
    margin-right: -1px !important;
    margin-bottom: 11px !important;
}

#protocols-list-section {
    background: #161616 !important;
    color: #FAFAFA !important;
}

#protocols-list-section > div[role="group"] > .item-link.https > span,
#protocols-list-section > div[role="group"] > .item-link.ssh > span {
    background: #161616 !important;
    color: #FAFAFA !important;
}

#protocols-list-section > div[role="group"] > .item-link.https > span:hover,
#protocols-list-section > div[role="group"] > .item-link.ssh > span:hover {
    background: #0973BF !important;
}

.aui-buttons .clone-url-input {
    background: #3C3C3C !important;
    color: #FAFAFA !important;
}

textarea,
input,
.aui-select2-drop.select2-drop.filter-bar-async .select2-search > input,
ak-editor-bitbucket > div,
form.aui .aui-select2-container .select2-choices {
    background: #3C3C3C !important;
    color: #FAFAFA !important;
}

ak-editor-bitbucket > div > div > div > div > button > span,
ak-editor-bitbucket > div > div > div > div > div > button > span,
ak-editor-bitbucket > div > div > span > div > div > div > button > span,
ak-editor-bitbucket > div > div > span > button > span,
ak-editor-bitbucket > div > div > div > div > div > div > button > span,
ak-editor-bitbucket > div > div > div > div > div > div > div > div > div > button > span > span > span > svg,
.usage--helptext {
    color: #FAFAFA !important;
}

ak-editor-bitbucket > div > div:nth-child(1) {
    background: #1C1C1C !important;
    border-bottom: solid #0973BF 1px !important;
}

span[data-role="droplistItem"]:hover {
    background: #0973BF !important;
}

span[data-role="droplistItem"] > span > span {
    color: #FAFAFA !important;
}

div[data-role="droplistContent"] {
    background: #161616 !important;
}

span[role="presentation"] > span {
    color: #0973BF !important;
}

span[role="presentation"] > span:hover {
    background: #161616 !important;
}

.aui-select2-container.select2-container .select2-choice {
    background: #3C3C3C !important;
    color: #FAFAFA !important;
}

.aui-select2-drop.select2-drop,
.aui-list-truncate,
.aui-list-truncate > li > a {
    background: #161616 !important;
}

.aui-select2-drop.select2-drop .select2-results .select2-highlighted,
.aui-dropdown2.aui-style-default > ul > li li:hover :not([data-operation="color"]),
.aui-list-truncate > li > a:hover {
    background: #0973BF !important;
    color: #FAFAFA !important;
}

.aui-nav.aui-nav-breadcrumbs > li {
    background: transparent !important;
}

#branch-pjax > div > table > tfoot > tr {
    background: #1C1C1C !important;
    color: #FAFAFA !important;
}

.aui-lozenge-complete.aui-lozenge-subtle {
    background: #3C3C3C !important;
    color: #CCC !important;
}

.ssh-key,
#members-pjax > tr,
#snippets-pjax > div > table > tbody > tr > td {
    background: #161616 !important;
}

.usage--table.aui > tbody > tr {
    background: transparent !important;
}

body.adg3 .filter-bar-async.aui-select2-drop.select2-drop .select2-search {
    background: #161616 !important;
}

.filter-bar .query-container .input-icon {
    background: transparent !important;
}

.select2-chosen,
body.adg3 .aui-select2-drop.select2-drop .select2-results .select2-highlighted span,
body.adg3 .filter-bar .aui-select2-container.select2-container.filter-bar-async .select2-choice,
body.adg3 .filter-bar .aui-select2-container.select2-container.filter-bar-async .select2-default,
body.adg3 .filter-bar .aui-select2-container.select2-container.select2-dropdown-open.filter-bar-async .select2-choice,
body.adg3 .filter-bar .aui-select2-container.select2-container a.select2-choice,
body.adg3 .filter-bar .aui-select2-container.select2-container.select2-dropdown-open .select2-choice {
    color: #FAFAFA !important;
}

body.adg3 .filter-bar .aui-select2-container.select2-container.filter-bar-async .select2-default:hover,
body.adg3 .filter-bar .aui-select2-container.select2-container.filter-bar-async .select2-choice:hover,
body.adg3 .filter-bar .aui-select2-container.select2-container a.select2-choice:hover {
    background: #2C2C2C !important;
}

#branch-pjax > div > table > thead > tr > th:hover {
    background: #1C1C1C !important;
}

.aui-icon-dropdown {
    border-top-color: #FAFAFA !important;
}

.sortable-table--column-header[aria-sort="none"][data-initial-sort-direction="ascending"]:hover::after {
    border-bottom-color: #FAFAFA !important;
}

.sortable-table--column-header[aria-sort="none"][data-initial-sort-direction="descending"]:hover::after {
    border-top-color: #FAFAFA !important;
}

.sortable-table--column-header[aria-sort="ascending"],
.sortable-table--column-header[aria-sort="descending"],
.sortable-table--column-header:hover,
.sortable-table--column-header:focus {
    background: transparent !important;
    color: #FAFAFA !important;
}

.sortable-table--column-header[aria-sort="descending"]::after {
    border-top-color: #FAFAFA !important;
}

.sortable-table--column-header[aria-sort="ascending"]::after {
    border-bottom-color: #FAFAFA !important;
}

.maskable.has-mask::after {
    background-color: rgba(0, 0, 0, 0.4) !important;
}

/*Source and Commit View*/
.file-source-container .toolbar,
.highlighttable td.annotations,
.highlighttable td.linenos,
.bb-udiff .heading,
.refract-container .gutter {
    background: #2C2C2C !important;
}

.refract-container .line-numbers::after,
.refract-container .line-numbers::before {
    color: #FAFAFA !important;
}

.refract-container .line-numbers:hover::after,
.refract-container .line-numbers:hover::before {
    color: #CCC !important;
}

.skipped-container .ellipsis {
    background: #2C2C2C !important;
    color: #FAFAFA !important;
}

.refract-content-container .source,
.skipped-container .skipped-bottom,
.skipped-container .skipped-top,
.skipped-container .skipped-middle {
    background: #161616 !important;
    color: #FAFAFA !important;
}

.refract-container .addition pre.source > ins {
    background: #252 !important;
}

.refract-container .addition pre.source {
    background: #002800 !important;
    opacity: 1 !important;
    color: #FAFAFA !important;
}

.refract-container .deletion pre.source > del {
    background: darkred !important;
}

.refract-container .deletion pre.source {
    background: #440000 !important;
    opacity: 1 !important;
    color: #FAFAFA !important;
}

pre {
    background: #161616 !important;
}

/*Syntax Highlighting*/
.code/*Background*/
{
    background: #161616 !important;
}

.nn/*Using-Import Package*/
{
    color: cornflowerblue !important;
}

.nc/*This Class Name*/
{
    color: cornflowerblue !important;
}

.nf/*Method Name*/
{
    color: cornflowerblue !important;
}

.kt/*Keyword*/
{
    color: #0973BF !important;
}

.k/*Null*/
{
    color: #0973BF !important;
}

.m/*Numeric Value*/
{
    color: lightgreen !important;
}
}
