css:
@-moz-document domain("deepl.com") {
/************************************************ NAV ************************************************/

.dl_header,
.dl_header > nav,
.dl_cookie_footer__inner {
    background-color: #333 !important;
    color: white;
}

.dl_header_menu_v2__separator {
    border-color: #555;
}

.dl_header_menu_v2__logo > span {
    color: #81c1ff;
}

.dl_header_menu_v2__links__item,
.dl_header_menu_v2__links__item:visited,
.dl_header_menu_v2__links__item:active,
.dl_header_menu_v2__links__item:focus {
    color: #81c1ff;
}

.dl_header_menu_v2__links__item:hover,
.dl_header_menu_v2__links__item.is-active {
    border-color: #81c1ff;
}

.dl_header_menu_v2__login_button > button {
    color: #81c1ff;
}

.dl_header_menu_v2__buttons__menu > button::before,
.dl_header_menu_v2__buttons__menu > button::after {
    color: #81c1ff;
}

.dl_header_menu_v2__buttons__menu > button:hover::before,
.dl_header_menu_v2__buttons__menu > button:hover::after {
    color: #b4daff;
}

.dl_header_menu_v2__login_button > button:hover {
    color: #b4daff;
}

.dl_header_menu_v2__logo > img {
    filter: invert(1) hue-rotate(180deg);
}


button.dl_menu__login__form__pass__forgot {
    color: #333;
}

input#login_email, input#login_password {
    background-color: white;
}

/************************************************ POPUP ************************************************/

.dl_app_popup__content {
    background-color: #272727;
    border-color: #333;
}

/************************************************ MAIN ************************************************/

body.dl_body--redesign {
    background-color: #222 !important;
    color: white;
}

.dl_body--redesign .lmt--web .lmt__language_select__active {
    color: white !important;
}

.dl_body--redesign .lmt--web .lmt__language_select .lmt__language_select__active {
    color: white !important;
}

/* bullet */
.dl_ad_pro__features_item::before {
    background-color: white;
}

.lmt--web .lmt__language_select > button strong {
    color: #81c1ff;
}
.lmt--web .lmt__language_select > button:hover strong,
.lmt--web .lmt__language_select > button:hover .lmt__language_select__opener {
    color: #b4daff;
}

.lmt--web .lmt__language_select > button::-moz-focus-inner {
    border: 0;
}

.lmt__language_select__menu {
    background-color: #333;
}
.lmt__language_select__menu button {
    color: white;
}
.lmt__language_select__menu button:hover {
    background-color: #57c4ff36;
}

/* Translator: separator */
.dl_body--redesign .lmt__textarea_separator .lmt__textarea_separator__vertical_line {
    background-color: #222;
}
.dl_body--redesign .lmt__textarea_separator .lmt__textarea_separator__border_inner {
    border-color: #222;
    background-color: #333;
}
.dl_body--redesign .lmt__textarea_separator .lmt__textarea_separator__arrow {
    border-color: #81c1ff;
}
.dl_body--redesign .lmt--web .lmt__textarea_container.lmt__textarea_container--focus,
.dl_body--redesign .lmt__textarea_separator .lmt__textarea_separator__border_outer {
    border-color: #81c1ff !important;
}
.dl_body--redesign .lmt__textarea_separator .lmt__textarea_separator__busy {
    background-color: #333;
}
.lmt--active_translation_request .lmt__textarea_separator .lmt__textarea_separator__busy::before {
    filter: invert(1) hue-rotate(180deg);
}

.lmt--active_translation_request .lmt__side_container--target .lmt__inner_textarea_container {
    box-shadow: inset 0 0 1px 14px #333, inset 0 0 0px rgba(0, 0, 0, 0);
}
@keyframes lmt__inner_textarea_container__busy_animation {
  0% {
    box-shadow: inset 0 0 0px 0 #333, inset 0 0 0px 0px rgba(92, 154, 212, 0);
  }
  10% {
    box-shadow: inset 0 0 0px 1px #333, inset 0 0 0px 2px rgba(92, 154, 212, 0.5);
  }
  20% {
    box-shadow: inset 0 0 1px 3px #333, inset 0 0 1px 4px rgba(92, 154, 212, 0.5);
  }
  40% {
    box-shadow: inset 0 0 1px 8px #333, inset 0 0 1px 8px rgba(92, 154, 212, .3);
  }
}

.dl_body--redesign .lmt--web .lmt__textarea_container {
    background-color: #333;
    color: white;
}

.lmt--web .lmt__textarea {
    color: white;
}

.dl_body--redesign .lmt__textarea_separator .lmt__textarea_separator__border_outer::before {
    background-color: #333;
}

.lmt__clear_text_button {
    color: #666;
}
.lmt__clear_text_button :hover {
    color: #81c1ff;
}

.docTrans_translator_upload_button,
.docTrans_translator_upload_button__inner > button {
    background-color: #333;
}

.lmt--web .docTrans__help_popup {
    background-color: #333;
    color: white;
}
.docTrans__help_popup__icons img {
    filter: invert(1) hue-rotate(180deg);
}

.docTrans_translator_upload_button__inner > button {
    color: #81c1ff;
    stroke: #81c1ff;
}
.docTrans_translator_upload_button__inner > button:hover {
    color: #b4daff;
    stroke: #b4daff;
}

.lmt__target_toolbar .lmt__target_toolbar__generic > button,
.lmt__target_toolbar .lmt__target_toolbar__copy > button,
.lmt__target_toolbar .lmt__target_toolbar__save > button,
.lmt__target_toolbar .lmt__target_toolbar__share > button {
    color: #81c1ff;
    stroke: #81c1ff;
}
.lmt__target_toolbar--visible .lmt__target_toolbar__generic > button:hover,
.lmt__target_toolbar--visible .lmt__target_toolbar__share > button:hover,
.lmt__target_toolbar--visible .lmt__target_toolbar__copy > button:hover,
.lmt__target_toolbar--visible .lmt__target_toolbar__save > button:hover {
    color: #b4daff;
    stroke: #b4daff;
}
.lmt__target_toolbar > div:first-child > button > svg > path {
    fill: #333;
}

.lmt__translations_as_text h2::before {
    border-color: #666;
}
.lmt__translations_as_text__text_btn {
    color: white;
}


.lmt__source_textarea_overlay .lmt__source_textarea_overlay__sentence--active {
    background-color: #292929;
    color: #b4daff;
}

.lmt__edit_text_popup {
    background-color: #222;
}

.lmt__edit_text_popup li {
    color: white;
}
.lmt__edit_text_popup .lmt__edit_text_popup__entry--active {
    background-color: #69d5;
}

/************************************************ DOC UPLOAD ************************************************/

.lmt--web .docTrans_drop_target_overlay_failure {
    background-color: #220808;
}

.docTrans_drop_target_overlay {
    background-color: #222;
}

.lmt--web .docTrans_drop_target__desc {
    color: white;
}

.docTrans_drop_target_overlay.docTrans_drop_target_overlay--active {
    background-color: #222;
    outline: 2px dashed #59d6f9;
}

.docTrans_document .docTrans_document__target_block {
    background-color: #222;
    color: white;
}

.docTrans_document .docTrans_document__source_block {
    background-color: #333;
}

.docTrans_drop_target_overlay,
.docTrans_drop_target_overlay.docTrans_drop_target_overlay--active {
    box-shadow: 0 0 0px 2px #222, 0 0 0px rgba(0, 0, 0, 0.2);
}

.dl_body--redesign .lmt__textarea_separator .lmt__textarea_separator__border_outer {
    background-color: #222;
}



.docTrans_document .docTrans_document__document_ready_label,
.docTrans_document .docTrans_document__document_ready_additional_text a,
.docTrans_document .docTrans_document__target_lang_select h2,
.docTrans_document .docTrans_document__status_label {
    color: #ccc;
}


.docTrans_document[doc-trans-file-type=docx] .docTrans_document__file_icon {
    filter: invert(1) hue-rotate(180deg);
}

.docTrans_document .docTrans_document__filename_label {
    color: white;
}

.docTrans_document .docTrans_document__target_block {
    border-color: #81c1ff !important;
}


.docTrans_document .docTrans_document__target_lang_select li button.docTrans_document__target_lang_select__entry {
    color: #81c1ff;
}

.docTrans_document .docTrans_document__target_lang_select li button.docTrans_document__target_lang_select__entry:hover {
    color: #b4daff;
}

.docTrans_document .docTrans_document__target_lang_select__entry,
.docTrans_document .docTrans_document__cancel_button {
    color: #81c1ff;
}

.docTrans_document .docTrans_document__target_lang_select__entry:hover,
.docTrans_document .docTrans_document__cancel_button:hover,
.docTrans_document .docTrans_document__target_lang_select__entry:active,
.docTrans_document .docTrans_document__cancel_button:active {
    color: #b4daff;
}

.docTrans_document .docTrans_document__progress_image {
    filter: invert(1) hue-rotate(180deg);
}

.docTrans_document .docTrans_document__download_button a, .docTrans_document .docTrans_document__download_again_button {
    color: white;
}

.dl_hr_with_arrow {
    border-color: #444;
}
.dl_hr_with_arrow::before {
    background-color: #222;
    border-color: #444;
}

.docTrans_document .docTrans_document__document_ready_additional_text a:hover {
    color: #81c1ff;
}


.docTrans_document .docTrans_document__upload_progress, .docTrans_document .docTrans_document__progress {
    background-color: #333;
}

.docTrans_document .docTrans_document__upload_progress > div, .docTrans_document .docTrans_document__progress > div {
    background-color: #81c1ff;
    background-image: linear-gradient(45deg,
        rgba(0, 0, 0, 0.2) 25%, transparent 25%,
        transparent 50%, rgba(0, 0, 0, 0.2) 50%,
        rgba(0, 0, 0, 0.2) 75%, transparent 75%, transparent);
}

/************************************************ DICT ************************************************/

.lmt__dict .lmt__dict__inner {
    background-color: #333;
    color: white;
}

.lmt__dict .isForeignTerm .tag_lemma,
.lmt__dict .isForeignTerm .tag_lemma a,
.lmt__dict .isForeignTerm .tag_s,
.lmt__dict .isForeignTerm .tag_s a,
.lmt__dict .isForeignTerm .tag_trans_src_context {
    color: white;
}

.lmt__dict .tag_lemma a,
.lmt__dict .tag_s {
    color: #81c1ff;
}

.lmt__dict .isForeignTerm .tag_trans,
.lmt__dict .isForeignTerm .tag_trans a,
.lmt__dict .isForeignTerm .tag_t,
.lmt__dict .isForeignTerm .tag_t a,
.lmt__dict .isForeignTerm .tag_trans {
    color: #81c1ff;
}

.lmt__dict .source .tag_s,
.lmt__dict .source .tag_usage,
.lmt__dict .source .tag_formname,
.lmt__dict .source .tag_wordtype,
.lmt__dict .source .formlink,
.lmt__dict .source .tag_forms,
.lmt__dict .source .grammar_info {
    color: #bbb !important;
}

.lmt__dict .tag_trans {
    color: white;
}

.lmt__dict .tag_t {
    color: #ddd;
}

.lmt__dict .tag_lemma {
    color: #81c1ff;
}


/************************************************ AD ************************************************/

.lmt__pro_ad__features_item::before {
    background-color: white;
}

.dl_button--3 {
    color: white;
    border-color: #fff2;
}
.dl_button--3:hover, .dl_button--3:active {
    color: #81c1ff;
    border-color: #81c1ff;
}

.dl_button--1 {
    color: black;
    background-color: #d9806c;
}
.dl_button--1:hover, .dl_button--1:active {
    background-color: #81c1ff;
}

/************************************************ QUOTE ************************************************/

.lmt_quote__inner::before {
    filter: invert(0.7) hue-rotate(180deg) saturate(2.5);
}

#lmt_quotes_article .lmt_quotes_nav_entry {
    background-color: #666;
}

#lmt_quotes_article .lmt_quotes_nav_entry--active {
    background-color: #81c1ff;
}


/************************************************ FOOTER ************************************************/

.dl_body--redesign .dl_footer {
    background-color: #222 !important;
}

.dl_body--redesign .dl_footer .dl_footer__container {
    border-color: #444;
}
.dl_body--redesign .dl_footer .dl_footer__langselect::before {
    background-color: #444;
}

.dl_body--redesign .dl_footer .dl_menu_item,
.dl_body--redesign .dl_footer .dl_menu_item.dl_footer__langselect select {
    color: #999;
}

.dl_body--redesign .dl_footer .dl_footer__langselect::after {
    color: #ddd;
}

.dl_body--redesign .dl_footer .dl_footer__social--twitter,
.dl_body--redesign .dl_footer .dl_footer__social--facebook,
.dl_body--redesign .dl_footer .dl_footer__social--linkedin {
    background-color: #666;
}



/************************************************ START PAGE ************************************************/

.dl_body--startpage #dl_home_article .dl_background {
    background: none;
}

.dl_body--startpage #dl_home_article .dl_background::after {
    content: "";
    position: absolute;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: #222222e7;
}

.dl_body--startpage #dl_home_article .dl_main_deepl_logo {
    filter: invert(1) hue-rotate(180deg);
}

.dl_body--startpage #dl_home_article .dl_home_article__translator_box,
.dl_body--startpage #dl_home_article .dl_home_article__linguee_box,
.dl_body--startpage #dl_hiring_article .dl_hiring_article__text_box {
    background-color: #333;
    color: white;
    border-color: #54abff;
}

.dl_body--startpage #dl_hiring_article .dl_hiring_article__text_box > h2,
.dl_body--startpage #dl_home_article .dl_home_article__linguee_box > h2,
.dl_body--startpage #dl_home_article .dl_home_article__translator_box > h2 {
    color: white;
}

.dl_body--startpage #dl_hiring_article .dl_hiring_article__text_box > p,
.dl_body--startpage #dl_home_article .dl_home_article__linguee_box > p,
.dl_body--startpage #dl_home_article .dl_home_article__translator_box > p {
    color: white;
}

.dl_body--startpage #dl_home_article section > p,
.dl_body--startpage #dl_vision_article h2 {
    color: #bbb;
}

.dl_button--2 {
    color: black;
    background-color: #54abff;
}
.dl_button--2:hover {
    background-color: #b4daff;
}


/************************************************ PRESS ************************************************/

.dl_default_text_section {
    color: white;
}

hr {
    background-color: #444;
}

.dl_blog_abstract__post_title > a:visited,
.dl_blog_abstract__post_title > a:link {
    color: #54abff;
}

.dl_internal_link_row:visited,
.dl_internal_link_row:link {
    color: #54abff;
}

.dl_internal_link_row:hover {
    color: #b4daff;
}

.dl_default_link,
.is3_element > .dl_field_error_text > a:visited,
.is3_element > .dl_field_error_text > a:link,
.dl_default_links > a:visited,
.dl_default_links > a:link,
.dl_default_text_section h2 > a:visited,
.dl_default_text_section h2 > a:link,
.dl_default_text_section p > a:visited,
.dl_default_text_section p > a:link,
.dl_default_text_section ul > li > a:visited,
.dl_default_text_section ul > li > a:link,
.dl_default_text_section ol > li > a:visited,
.dl_default_text_section ol > li > a:link {
    color: #54abff;
}

.dl_default_link:hover,
.is3_element > .dl_field_error_text > a:hover:visited,
.is3_element > .dl_field_error_text > a:hover:link,
.dl_default_links > a:hover:visited,
.dl_default_links > a:hover:link,
.dl_default_text_section h2 > a:hover:visited,
.dl_default_text_section h2 > a:hover:link,
.dl_default_text_section p > a:hover:visited,
.dl_default_text_section p > a:hover:link,
.dl_default_text_section ul > li > a:hover:visited,
.dl_default_text_section ul > li > a:hover:link,
.dl_default_text_section ol > li > a:hover:visited,
.dl_default_text_section ol > li > a:hover:link,
.dl_default_link:focus,
.is3_element > .dl_field_error_text > a:focus:visited,
.is3_element > .dl_field_error_text > a:focus:link,
.dl_default_links > a:focus:visited,
.dl_default_links > a:focus:link,
.dl_default_text_section h2 > a:focus:visited,
.dl_default_text_section h2 > a:focus:link,
.dl_default_text_section p > a:focus:visited,
.dl_default_text_section p > a:focus:link,
.dl_default_text_section ul > li > a:focus:visited,
.dl_default_text_section ul > li > a:focus:link,
.dl_default_text_section ol > li > a:focus:visited,
.dl_default_text_section ol > li > a:focus:link {
    color: #b4daff;
}

[href^="http"].dl_default_link::after,
.is3_element > .dl_field_error_text > a[href^="http"]:link::after,
.dl_default_links > a[href^="http"]:link::after,
.dl_default_text_section h2 > a[href^="http"]:link::after,
.dl_default_text_section p > a[href^="http"]:link::after,
.dl_default_text_section ul > li > a[href^="http"]:link::after,
.dl_default_text_section ol > li > a[href^="http"]:link::after {
    filter: invert(1);
}

.dl_press_release_section__footnote {
    color: #aaa;
}

.dl_press_release_section h3 {
    color: #bbb;
}

.dl_default_text_section h3 {
    color: white;
}

#press_comparison_article .dl_graph_section .dl_content tfoot tr td {
    color: #999;
}

#press_comparison_article .publications_list > h3 {
    color: white;
}

#press_comparison_article .publications_list > div a {
    color: #54abff;
}

#press_history_article .points {
    filter: invert(0.6);
}


/************************************************ PRO ************************************************/

.dl_body--redesign .dl_pro_features {
    background-color: #333;
}

.dl_feature_card > .dl_feature_card__icon > img {
    filter: invert(1) hue-rotate(180deg);
}

.dl_body--redesign .dl_pro_pricing_article {
    background-color: #282828;
    box-shadow: inset 0 -1px 0 #2a4564;
}

.dl_body--redesign .dl_pro_pricing__usertype_container button {
    background-color: #282828 !important;
}

.dl_body--redesign .dl_pro_pricing__usertype_container button .dl_pro_pricing__usertype_btn_inner {
    border-color: #444;
}

.dl_body--redesign .dl_pro_pricing__usertype_container .dl_pro_pricing__usertype_tab--active {
    background-color: #1b2837 !important;
}

.dl_body--redesign .dl_pro_pricing__usertype_container .dl_pro_pricing__usertype_tab--active {
    color: white;
    box-shadow: inset 0 0 0 1px #2a4564;
    margin-bottom: -1px;
    position: relative;
}

.dl_body--redesign .dl_pro_pricing__usertype_container button {
    color: #aaa;
    box-shadow: inset 0 -1px 0 #2a4564;
}

.dl_body--redesign .dl_pro_pricing__usertype_container button:hover {
    color: #54abff;
}

.dl_body--redesign .dl_pro_pricing__usertype_container button:not(.dl_pro_pricing__usertype_tab--active):hover .dl_pro_pricing__usertype_btn_inner {
    border-color: #54abff;
}

.dl_body--redesign .dl_pro_article_style {
    background-color: #222;
}

.dl_body--redesign .dl_pro_article_style__pricing {
    background-color: #1b2837;
}

.dl_body--redesign .dl_pro_pricing__subscription_period_container .dl_pro_pricing__subscription_period {
    background-color: transparent;
    color: white;
    border-color: #54abff;
}
.dl_body--redesign .dl_pro_pricing__subscription_period--enabled {
    background-color: #54abff !important;
    color: black !important;
}

.dl_product {
    background-color: #fff1;
}
.dl_body--redesign .dl_product__name_header {
    color: white;
}
.dl_body--redesign .dl_product_price__info {
    color: #a5c1d3;
}

.dl_body--redesign .dl_product__details_section__header::before {
    filter: invert(1) hue-rotate(180deg);
}

.dl_product__details_section p > a:visited,
.dl_product__details_section p > a:link,
.dl_default_link {
    color: #54abff;
}
.dl_product__details_section p > a:hover:link,
.dl_default_link:hover,
.dl_product__details_section p > a:focus:link,
.dl_default_link:focus {
    color: #b4daff;
}

.dl_body--redesign .dl_pro_pricing_configurator__teamsize_option {
    background-color: transparent;
    border-color: #54abff;
    color: white;
}
.dl_body--redesign .dl_pro_pricing_configurator__teamsize_option--active,
.dl_body--redesign .dl_pro_pricing_configurator__teamsize_option--active:hover {
    color: black;
    background-color: #54abff;
}

.dl_link, .dl_cookie_footer__inner p a,
.dl_pro_pricing__see_faqs_row a {
    color: #54abff;
}
.dl_link:hover, .dl_cookie_footer__inner p a:hover,
.dl_pro_pricing__see_faqs_row a:hover {
    color: white;
}

.dl_body--redesign .dl_pro_pricing__hr {
    background-color: #444;
}

.dl_link--more::after {
    filter: invert(1) hue-rotate(180deg);
}

/************************************************ FAQ ************************************************/

.dl_faq_section .dl_faq_section__answer_box > label > a:visited,
.dl_faq_section .dl_faq_section__answer_box p > a:visited,
.dl_faq_section .dl_faq_section__answer_box li > a:visited,
.dl_faq_section > p > a:visited,
.dl_faq_section .dl_faq_section__answer_box > label > a:link,
.dl_faq_section .dl_faq_section__answer_box p > a:link,
.dl_faq_section .dl_faq_section__answer_box li > a:link,
.dl_faq_section > p > a:link, .dl_default_link {
    color: #54abff;
}
.dl_faq_section .dl_faq_section__answer_box > label > a:hover:visited,
.dl_faq_section .dl_faq_section__answer_box p > a:hover:visited,
.dl_faq_section .dl_faq_section__answer_box li > a:hover:visited,
.dl_faq_section > p > a:hover:visited,
.dl_faq_section .dl_faq_section__answer_box > label > a:hover:link,
.dl_faq_section .dl_faq_section__answer_box p > a:hover:link,
.dl_faq_section .dl_faq_section__answer_box li > a:hover:link,
.dl_faq_section > p > a:hover:link,
