css:
@-moz-document url("https://www.deepl.com"), url-prefix("https://www.deepl.com/translator") {

.dl_body, .dl_body--text_page {
    color: white;
}
.dl_body--redesign[_dl-dragging-root] {
    background-color: hsl(210, 10%, 10%) !important;
}


.lmt__stickyMenubar .lmt__stickyMenubar_whiteBackground {
    border-top-color: hsl(210, 10%, 10%);
    animation: none;
}
.lmt__stickyMenubar_whiteBackground--show {
    background-color: hsl(210, 10%, 15%);
}

.dl_body--redesign .lmt--web .lmt__language_select .lmt__language_select__active {
    color: #ddd;
}

.lmt--web .lmt__language_select > button strong {
    color: hsl(210, 100%, 70%);
}
.lmt--web .lmt__language_select > button:hover strong {
    color: hsl(210, 100%, 85%);
}

.lmt__language_select__opener {
    color: #bbb;
}
.lmt--web .lmt__language_select > button:hover .lmt__language_select__opener {
    color: hsl(210, 100%, 85%);
}

.lmt__formalitySwitch .lmt__formalitySwitch__toggler {
    color: #bbb;
}
.lmt__formalitySwitch .lmt__formalitySwitch__toggler_icon {
    border-top-color: #bbb;
    border-right-color: #bbb;
}


.lmt__formalitySwitch .lmt__formalitySwitch__toggler,
.lmt__glossary_button {
    border-color: rgba(255, 255, 255, 0.07);
    color: #bbb;
}
.lmt__formalitySwitch .lmt__formalitySwitch__toggler:hover .lmt__formalitySwitch__toggler_icon {
    border-color: hsl(210, 100%, 70%);
}
.lmt__formalitySwitch .lmt__formalitySwitch__toggler:hover {
    color: hsl(210, 100%, 70%);
}
.lmt__glossary_button .lmt__glossary_onOffSwitch_label:hover {
    color: hsl(210, 100%, 70%);
}

.switchOption--1Hyj0.variant_light--23An1 {
    border-color: hsl(210, 60%, 35%);
}

.switchOption--1Hyj0.variant_light--23An1.active--2XCvR {
    background-color: hsl(210, 60%, 35%);
    color: rgba(255, 255, 255, 0.8);
}
.switchOption--1Hyj0.variant_light--23An1 {
    color: hsl(210, 100%, 70%);
}
.switchOption--1Hyj0.variant_light--23An1:hover:not(.active--2XCvR):not(.disabled--86T0S) {
    color: rgba(255, 255, 255, 0.8);
}


.lmt__dict .lmt__dict__inner {
    background-color: hsl(210, 10%, 12%);
}
.lmt__dict .lmt__dict__inner > div {
    filter: invert(1) hue-rotate(180deg) brightness(120%);
}

.dl_pro__banner--card,
.dl_pro__banner--features {
    background-color: hsl(210, 10%, 15%);
}
img.dl_pro__banner--featureIcon {
    filter: invert(1) hue-rotate(180deg);
}


.lmt__textarea_container,
.docTrans_document > .docTrans_document__source_block,
.docTrans_document > .docTrans_document__target_block,
.lmt__textarea_separator__border_outer {
    filter: invert(1) hue-rotate(180deg);
}

.lmt__textarea_separator .lmt__textarea_separator__vertical_line {
    background-color: hsl(210, 10%, 10%);
}

.lmt__textarea_separator .lmt__textarea_separator__border_inner {
    background-color: hsl(210, 10%, 10%);
    border-color: hsl(210, 10%, 10%);
}

.releasegroup_df-1397 .lmt__textarea_separator__arrow {
    border-color: hsl(210, 100%, 70%);
}

.lmt__textarea_separator .lmt__textarea_separator__busy {
    background-color: hsl(210, 10%, 10%);
}


#lmt_quotes_article .lmt_quotes_nav_entry {
    background-color: hsl(210, 10%, 20%);
}

#lmt_quotes_article .lmt_quotes_nav_entry--active {
    background-color: hsl(210, 100%, 70%);
}


.lmt_quote__inner {
    background-color: hsl(210, 10%, 15%);
}
.lmt_quote__inner::before {
    filter: invert(1) hue-rotate(180deg);
}
.lmt_quote__text, .lmt_quote__source {
    color: #ddd;
}
.lmt_quote__source a {
    color: hsl(210, 100%, 70%);
}



.dl_app_popup__content {
    background-color: hsl(210, 10%, 20%);
    border-color: hsl(210, 10%, 23%);
}
.dl_button--3 {
    color: white;
    border-color: #aaa;
}
.dl_button--3:hover, .dl_button--3:active {
    color: hsl(210, 100%, 70%);
    border-color: hsl(210, 100%, 70%);
}
.lmt__formalitySwitch_ad {
    color: black;
}
.lmt__formalitySwitch_ad__bg {
    background-color: rgba(205, 205, 205, 0.5);
}



.lmt--short_text .lmt__edit_text_popup {
    scrollbar-color: #00587d transparent;
}
.lmt--short_text .lmt__edit_text_popup:hover {
    scrollbar-color: #0082b9 transparent;
}
.dl_body--redesign .lmt__edit_text_popup {
    background-color: hsl(210, 10%, 12%);
    box-shadow: 0px 2px 10px rgba(0, 0, 0, 0.6)
}
.lmt__edit_text_popup li {
    color: #999;
    border-bottom-color: rgba(255, 255, 255, 0.04);
}
.lmt__edit_text_popup .lmt__edit_text_popup__entry--active {
    background-color: #2f2514;
    color: #eee;
}


}

@-moz-document url-prefix("https://www.deepl.com/home") {
body, .dl_body--redesign {
    background-color: hsl(210, 10%, 10%) !important;
    color: white;
}

.dl_body--startpage #dl_vision_article h2 {
    color: #ddd;
}

.dl_background,
.dl_body--startpage #dl_home_article .dl_main_deepl_logo {
    filter: invert(1) hue-rotate(180deg);
}

.dl_body--startpage #dl_home_article section > p {
    color: #bbb;
}

.dl_body--startpage #dl_home_article .dl_home_article__translator_box,
.dl_body--startpage #dl_home_article .dl_home_article__linguee_box,
.dl_body--startpage #dl_hiring_article .dl_hiring_article__text_box {
    background-color: hsl(210, 10%, 15%);
    color: white;
}

.dl_body--startpage #dl_hiring_article .dl_hiring_article__text_box,
.dl_body--startpage #dl_home_article .dl_home_article__linguee_box,
.dl_body--startpage #dl_home_article .dl_home_article__translator_box {
    border-top-color: hsl(210, 100%, 70%);
}

.dl_body--startpage #dl_hiring_article .dl_hiring_article__text_box > h2,
.dl_body--startpage #dl_home_article .dl_home_article__linguee_box > h2,
.dl_body--startpage #dl_home_article .dl_home_article__translator_box > h2 {
    color: #ddd;
}

.dl_body--startpage #dl_hiring_article .dl_hiring_article__text_box > p,
.dl_body--startpage #dl_home_article .dl_home_article__linguee_box > p,
.dl_body--startpage #dl_home_article .dl_home_article__translator_box > p {
    color: white;
}

.dl_button--2 {
    background-color: hsl(210, 100%, 70%);
    color: black;
}

.header-component .white-center-logo .navbar {
    background-color: transparent;
}



}

@-moz-document url("https://www.deepl.com/"), url-prefix("https://www.deepl.com/home"), url-prefix("https://www.deepl.com/translator") {
nav img[alt="DeepL logo"] {
    filter: brightness(130%) invert(1) hue-rotate(180deg);
}

.dl_header {
    background-color: hsl(210, 10%, 15%) !important;
}

.dl_header_menu_v2__logo > span {
    color: hsl(210, 100%, 83%);
}

.dl_header_menu_v2__separator {
    border-color: hsl(210, 10%, 20%);
}

.dl_header_menu_v2__links__item,
.dl_header_menu_v2__links__item:visited,
.dl_header_menu_v2__links__item:active,
.dl_header_menu_v2__links__item:focus,
.dl_header_menu_v2__login_button > button {
    color: white;
}

.dl_header_menu_v2__links__item:hover,
.dl_header_menu_v2__links__item.is-active,
.dl_header_menu_v2__login_button > button:hover {
    color: hsl(210, 100%, 83%);
    border-bottom-color: hsl(210, 100%, 74%);
}

.dl_header_menu_v2__buttons__menu > button::before,
.dl_header_menu_v2__buttons__menu > button::after {
    color: white;
}
.dl_header_menu_v2__buttons__menu > button:hover::before, .dl_header_menu_v2__buttons__menu > button:hover::after {
    color: hsl(210, 100%, 83%);
}


.dl_footer__container {
    border-color: hsl(210, 10%, 20%);
}
.dl_footer__langselect::before {
    background-color: hsl(210, 10%, 20%);
}
.dl_footer__social--twitter:not(:hover),
.dl_footer__social--facebook:not(:hover),
.dl_footer__social--linkedin:not(:hover) {
    filter: brightness(65%) contrast(300%);
}

}
