css:
@-moz-document url-prefix("https://abal.moe/Eris/") {
section.content.no-space-right {
    margin-right: 0;
    padding-right: 2em;
    background: #36393f;
    color: white;
}

.checkmark {
    display: inline-block;
    padding-right: .5em;
    background: #36393f;
}
section.content {
    padding: 1em;
    margin-right: 2em;
    background: #36393f;
    color: lightgrey;
}

.container .menu {
    flex: 0 0 12em;
    order: 0;
    padding-top: 1em;
    background: #36393f;
}

.container .page {
    flex: 1;
    order: 1;
    background: #36393f;
}

.grey {
    color: grey;
}

ul.menu .top-level {
    padding-left: 1rem;
    background: #36393f;
}

ul.menu, ul.menu li {
    display: inline-block;
    width: 100%;
    background-color: #36393f;
}

a, a:visited {
    color: #0096cf;
}

ul.menu .menu-section-heading.top-level {
    padding-top: .75rem;
    color: white;
}

.space-right {
    margin-right: 2em;
    color: white;
}

ul.menu li a.active, ul.menu li a:hover {
    color: white;
    background-color: #2c4251;
}

.search-bar-suggestions {
    border: 1px solid #ccc;
    border-bottom-left-radius: 2px;
    border-bottom-right-radius: 2px;
    box-sizing: border-box;
    font-size: 100%;
    list-style: none;
    margin: 0;
    padding: 0;
    position: relative;
    top: -1px;
    color: lightgrey
}

.search-bar-suggestions .highlighted {
    background: black
}

}
