css:
@-moz-document url-prefix("https://www.exclusivelygames.com/forum/") {
html {
    background-color: #020308 !important;
}
html,
body,
input,
textarea,
select,
button {
    background-color: #020308;
}
html,
body,
input,
textarea,
select,
button {
    border-color: #4f4f4f;
    color: #fdfcf7;
}
a {
    color: #2494ff;
}
table {
    border-color: #424242;
}
::placeholder {
    color: #c4bfb5;
}
::selection {
    background-color: #0055db;
    color: #ffffff;
}
::-moz-selection {
    background-color: #0055db;
    color: #ffffff;
}
input:-webkit-autofill,
textarea:-webkit-autofill,
select:-webkit-autofill {
    background-color: #4b5400 !important;
    color: #fdfcf7 !important;
}
::-webkit-scrollbar {
    background-color: #07080e;
    color: #d2cdc6;
}
::-webkit-scrollbar-thumb {
    background-color: #131924;
}
::-webkit-scrollbar-thumb:hover {
    background-color: #192331;
}
::-webkit-scrollbar-thumb:active {
    background-color: #203045;
}
::-webkit-scrollbar-corner {
    background-color: #020308;
}

body {
    background-color: #020308;
    background-image: none, none;
    color: #f2f1ec;
}
code {
    color: #fdfcf7;
    background-color: #05060b;
    background-image: none, none;
}
input[type=\"text\"], input[type=\"password\"], textarea, select {
 border-top-color: #33393c;
border-right-color: #33393c;
border-bottom-color: #33393c;
border-left-color: #33393c;











}

input[type=\"text\"][readonly], textarea[readonly] {
 color: #c3bdb5;











}

input:-moz-placeholder,
textarea:-moz-placeholder {
    color: #d2cdc6 !important;
}
input::placeholder,
textarea::placeholder {
    color: #d2cdc6 !important;
}
::-webkit-input-placeholder {
    color: #d2cdc6 !important;
}
input::placeholder,
textarea::placeholder {
    color: #d2cdc6 !important;
}
.restore u {
    text-decoration-color: currentcolor;
}
.restore abbr,
.restore acronym {
    border-bottom-color: #323739;
}

.restore th,
.restore td {
    border-top-color: #323739;
    border-right-color: #323739;
    border-bottom-color: #323739;
    border-left-color: #323739;
}
.h-warning {
    color: #ff4343;
}
.textbox {
    border-top-color: #33393c;
    border-right-color: #33393c;
    border-bottom-color: #33393c;
    border-left-color: #33393c;
    background-color: #020308;
    background-image: none,
    none;
    color: #fdfcf7;
}
.warning {
    border-top-color: #442121;
    border-right-color: #442121;
    border-bottom-color: #442121;
    border-left-color: #442121;
    background-color: #360000;
    background-image: none;
}
.error {
    border-top-color: #442121;
    border-right-color: #442121;
    border-bottom-color: #442121;
    border-left-color: #442121;
    background-color: #360000;
    background-image: none;
}
.placeholder {
    color: #d2cdc6 !important;
}



.split-button .split-button-wrapper .submenu {
    border-top-color: #33393c;
    border-right-color: #33393c;
    border-bottom-color: #33393c;
    border-left-color: #33393c;
}
.split-button.open .split-button-wrapper .submenu {
    border-top-color: currentcolor;
}
.split-button .split-button-wrapper .submenu li {
    border-bottom-color: #2d3335;
    border-top-color: #1e3d48;
}
.split-button .split-button-wrapper .submenu li:last-child {
    border-bottom-color: currentcolor;
}
.horizontal-arrows .left-arrow {
    border-right-color: #2c3237;
}
.horizontal-arrows .right-arrow {
    border-left-color: #2c3237;
}
.horizontal-arrows {
    border-top-color: #33393c;
    border-right-color: #33393c;
    border-bottom-color: #33393c;
    border-left-color: #33393c;
    background-color: #020308;
    background-image: none,
    none;
    color: #fdfcf7;
}
.switch-container {
    color: #d8d4cd;
}
.switch-control {
    border-top-color: #2d363b;
    border-right-color: #2d363b;
    border-bottom-color: #2d363b;
    border-left-color: #2d363b;
}
.switch-control .switch-label {
    background-color: #0c0e15;
    background-image: none,
    linear-gradient(#0e121a, #07090e),
    none;
    color: #cbc4bc;
}
.switch-control .switch-label.on {
    background-color: #0663a2;
    background-image: none,
    linear-gradient(#0666a2, #0665a1),
    none;
    color: #ffffff;
}
.switch-control .switch-label .switch-handle {
    background-color: #05060b;
    background-image: none,
    linear-gradient(#020308, #0b0d14),
    none;
}
.switch-control .switch-label.off .switch-handle {
    border-right-color: #2d363b;
}
.switch-control .switch-label.on .switch-handle {
    border-left-color: #2d363b;
}
.field-set .field-row .field-desc {
    color: #c8c2ba;
}
.pointer-left,
.pointer-up {
    background-color: transparent;
    background-image: none;
}
.pointer-left {
    border-right-color: #242424;
    border-top-color: transparent;
    border-bottom-color: transparent;
}
.pointer-up {
    border-bottom-color: #242424;
    border-right-color: transparent;
    border-left-color: transparent;
}
.bubble-flyout {
    border-top-color: #352d2d;
    border-right-color: #352d2d;
    border-bottom-color: #352d2d;
    border-left-color: #352d2d;
}
.bubble-flyout i,
.bubble-flyout u {
    border-top-color: transparent;
    border-right-color: transparent;
    border-bottom-color: transparent;
    border-left-color: currentcolor;
}
.bubble-flyout u {
    border-right-color: transparent;
}
.bubble-flyout i {
    border-right-color: #352d2d;
}
.divider,
.content-divider {
    border-top-color: #323739;
    border-right-color: #323739;
    border-bottom-color: #323739;
    border-left-color: #323739;
}
.grayed-out {
    color: #dbd8d2;
}
.grip-icon div {
    color: #d3cec7;
}
.shadow {
    box-shadow: #0d0f17 1px 2px 3px;
}
.avatar {
    border-top-color: #2b2b2b;
    border-right-color: #2b2b2b;
    border-bottom-color: #2b2b2b;
    border-left-color: #2b2b2b;
}
.mainmenu li {
    border-top-color: transparent;
    border-right-color: transparent;
    border-bottom-color: transparent;
    border-left-color: transparent;
    color: inherit;
}
.mainmenu li a {
    color: #83b7df;
}
.mainmenu.has-hover li:hover {
    border-top-color: #333333;
    border-right-color: #333333;
    border-bottom-color: #333333;
    border-left-color: #333333;
    background-color: #020308;
    background-image: none;
}
.mainmenu .submenu li a:hover {
    background-color: #090c13;
    background-image: none;
    color: #ffffff;
}
.mainmenu .submenu {
    border-top-color: #333333;
    border-right-color: #333333;
    border-bottom-color: #333333;
    border-left-color: #333333;
    background-color: rgba(0, 0, 0, 0);
    background-image: linear-gradient(#020308, #07090e);
}
.mainmenu.pos-top .submenu {
    background-color: rgba(0, 0, 0, 0);
    background-image: linear-gradient(#07090e, #020308);
}

.divider-container .overlap {
    background-color: #db00db;
    background-image: none,
    none;
    color: #c1bbb3;
}
.divider-light {
    border-top-color: #485b71;
    border-right-color: transparent;
    border-bottom-color: #475563;
    border-left-color: transparent;
}
.field-desc {
    color: #c8c2ba;
}


.ui-selectable-helper {
    border-top-color: #616161;
    border-right-color: #616161;
    border-bottom-color: #616161;
    border-left-color: #616161;
}


.ui-datepicker .ui-datepicker-buttonpane {
    background-image: none;
    border-left-color: currentcolor;
    border-right-color: currentcolor;
    border-bottom-color: currentcolor;
}
.ui-dialog {
    outline-color: currentcolor;
}
.ui-dialog .ui-dialog-content {
    border-top-color: currentcolor;
    border-right-color: currentcolor;
    border-bottom-color: currentcolor;
    border-left-color: currentcolor;
    background-color: rgba(0, 0, 0, 0);
    background-image: none;
}
.ui-dialog .ui-dialog-buttonpane {
    background-image: none;
}


.ui-progressbar-indeterminate .ui-progressbar-value {
    background-image: none;
}

.ui-tabs .ui-tabs-panel {
    background-color: rgba(0, 0, 0, 0);
    background-image: none;
}
.ui-widget-content {
    border-top-color: #383838;
    border-right-color: #383838;
    border-bottom-color: #383838;
    border-left-color: #383838;
    background-color: #020308;
    background-image: none;
    color: #edebe6;
}
.ui-widget-content a {
    color: #edebe6;
}
.ui-widget-header {
    border-top-color: #383838;
    border-right-color: #383838;
    border-bottom-color: #383838;
    border-left-color: #383838;
    background-color: #11151f;
    background-image: none;
}
.ui-widget-header a {
    color: #edebe6;
}
.ui-state-default,
.ui-widget-content .ui-state-default,
.ui-widget-header .ui-state-default {
    border-top-color: #2f2f2f;
    border-right-color: #2f2f2f;
    border-bottom-color: #2f2f2f;
    border-left-color: #2f2f2f;
    background-color: #090c13;
    background-image: none;
    color: #d7d3cc;
}
.ui-state-default a,
.ui-state-default a:link,
.ui-state-default a:visited {
    color: #d7d3cc;
    text-decoration-color: currentcolor;
}
.ui-state-hover,
.ui-widget-content .ui-state-hover,
.ui-widget-header .ui-state-hover,
.ui-state-focus,
.ui-widget-content .ui-state-focus,
.ui-widget-header .ui-state-focus {
    border-top-color: #3c3c3c;
    border-right-color: #3c3c3c;
    border-bottom-color: #3c3c3c;
    border-left-color: #3c3c3c;
    background-color: #0d1118;
    background-image: none;
    color: #edece6;
}
.ui-state-hover a,
.ui-state-hover a:hover,
.ui-state-hover a:link,
.ui-state-hover a:visited,
.ui-state-focus a,
.ui-state-focus a:hover,
.ui-state-focus a:link,
.ui-state-focus a:visited {
    color: #edece6;
    text-decoration-color: currentcolor;
}
.ui-state-active,
.ui-widget-content .ui-state-active,
.ui-widget-header .ui-state-active {
    border-top-color: #0023db;
    border-right-color: #0023db;
    border-bottom-color: #0023db;
    border-left-color: #0023db;
    background-color: #182030;
    background-image: none;
    color: #edece6;
}
.ui-state-active a,
.ui-state-active a:link,
.ui-state-active a:visited {
    color: #edece6;
    text-decoration-color: currentcolor;
}
.ui-state-highlight,
.ui-widget-content .ui-state-highlight,
.ui-widget-header .ui-state-highlight {
    border-top-color: #ab9000;
    border-right-color: #ab9000;
    border-bottom-color: #ab9000;
    border-left-color: #ab9000;
    background-color: #231b00;
    background-image: none;
    color: #e5e1db;
}
.ui-state-highlight a,
.ui-widget-content .ui-state-highlight a,
.ui-widget-header .ui-state-highlight a {
    color: #e5e1db;
}
.ui-state-error,
.ui-widget-content .ui-state-error,
.ui-widget-header .ui-state-error {
    border-top-color: #d40000;
    border-right-color: #d40000;
    border-bottom-color: #d40000;
    border-left-color: #d40000;
    background-color: #2d0000;
    background-image: none;
    color: #ff4f4f;
}
.ui-state-error a,
.ui-widget-content .ui-state-error a,
.ui-widget-header .ui-state-error a {
    color: #ff4f4f;
}
.ui-state-error-text,
.ui-widget-content .ui-state-error-text,
.ui-widget-header .ui-state-error-text {
    color: #ff4f4f;
}
.ui-state-disabled,
.ui-widget-content .ui-state-disabled,
.ui-widget-header .ui-state-disabled {
    background-image: none;
}



.ui-widget-overlay {
    background-color: #182030;
    background-image: none;
}
.ui-tooltip-content {
    border-top-color: #0000ff;
    border-right-color: #0000ff;
    border-bottom-color: #0000ff;
    border-left-color: #0000ff;
}
.ui-tooltip-titlebar {
    border-top-color: #0000ff;
    border-right-color: #0000ff;
    border-bottom-color: #0000ff;
    border-left-color: #0000ff;
}
.ui-tooltip-titlebar .ui-state-default {
    outline-color: currentcolor;
}
.ui-tooltip-icon .ui-icon {
    color: inherit;
    background-color: transparent;
    background-image: none;
}
.ui-tooltip-default .ui-tooltip-titlebar,
.ui-tooltip-default .ui-tooltip-content {
    border-top-color: #bc9b00;
    border-right-color: #bc9b00;
    border-bottom-color: #bc9b00;
    border-left-color: #bc9b00;
    background-color: #666600;
    color: #d7d3cc;
}
.ui-tooltip-default .ui-tooltip-titlebar {
    background-color: #725d00;
}
.ui-tooltip-default .ui-tooltip-icon {
    border-top-color: #303030;
    border-right-color: #303030;
    border-bottom-color: #303030;
    border-left-color: #303030;
    background-color: #07080e;
    background-image: none;
    color: #c8c2ba;
}
.ui-tooltip-default .ui-tooltip-titlebar .ui-state-hover {
    border-top-color: #383838;
    border-right-color: #383838;
    border-bottom-color: #383838;
    border-left-color: #383838;
    color: #f5f3ef;
}
.ui-tooltip .ui-tooltip-tip,
.ui-tooltip .ui-tooltip-tip * {
    color: #afdeff;
    background-color: transparent;
    background-image: none;
    border-top-color: transparent;
    border-right-color: transparent;
    border-bottom-color: transparent;
    border-left-color: transparent;
}
#qtip-overlay div {
    background-color: #000000;
}
.ui-tooltip-light .ui-tooltip-titlebar,
.ui-tooltip-light .ui-tooltip-content {
    border-top-color: #2b2b2b;
    border-right-color: #2b2b2b;
    border-bottom-color: #2b2b2b;
    border-left-color: #2b2b2b;
    color: #dedad4;
}
.ui-tooltip-light .ui-tooltip-content {
    background-color: #020308;
}
.ui-tooltip-light .ui-tooltip-titlebar {
    background-color: #07080e;
}
.ui-tooltip-dark .ui-tooltip-titlebar,
.ui-tooltip-dark .ui-tooltip-content {
    border-top-color: #555555;
    border-right-color: #555555;
    border-bottom-color: #555555;
    border-left-color: #555555;
    color: #ffffff;
}
.ui-tooltip-dark .ui-tooltip-content {
    background-color: #203045;
}
.ui-tooltip-dark .ui-tooltip-titlebar {
    background-color: #203045;
}
.ui-tooltip-dark .ui-tooltip-icon {
    border-top-color: #505050;
    border-right-color: #505050;
    border-bottom-color: #505050;
    border-left-color: #505050;
}
.ui-tooltip-dark .ui-tooltip-titlebar .ui-state-hover {
    border-top-color: #555555;
    border-right-color: #555555;
    border-bottom-color: #555555;
    border-left-color: #555555;
}
.ui-tooltip-cream .ui-tooltip-titlebar,
.ui-tooltip-cream .ui-tooltip-content {
    border-top-color: #aa8f00;
    border-right-color: #aa8f00;
    border-bottom-color: #aa8f00;
    border-left-color: #aa8f00;
    color: #eac989;
}
.ui-tooltip-cream .ui-tooltip-content {
    background-color: #5b5600;
}
.ui-tooltip-cream .ui-tooltip-titlebar {
    background-color: #725f00;
}
.ui-tooltip-red .ui-tooltip-titlebar,
.ui-tooltip-red .ui-tooltip-content {
    border-top-color: #940d0d;
    border-right-color: #940d0d;
    border-bottom-color: #940d0d;
    border-left-color: #940d0d;
    color: #f78c8c;
}
.ui-tooltip-red .ui-tooltip-content {
    background-color: #740000;
}
.ui-tooltip-red .ui-tooltip-titlebar {
    background-color: #840000;
}
.ui-tooltip-red .ui-tooltip-icon {
    border-top-color: #940d0d;
    border-right-color: #940d0d;
    border-bottom-color: #940d0d;
    border-left-color: #940d0d;
}
.ui-tooltip-red .ui-tooltip-titlebar .ui-state-hover {
    border-top-color: #940d0d;
    border-right-color: #940d0d;
    border-bottom-color: #940d0d;
    border-left-color: #940d0d;
}
.ui-tooltip-green .ui-tooltip-titlebar,
.ui-tooltip-green .ui-tooltip-content {
    border-top-color: #579d0b;
    border-right-color: #579d0b;
    border-bottom-color: #579d0b;
    border-left-color: #579d0b;
    color: #d5fdab;
}
.ui-tooltip-green .ui-tooltip-content {
    background-color: #2d5500;
}
.ui-tooltip-green .ui-tooltip-titlebar {
    background-color: #3b6606;
}
.ui-tooltip-blue .ui-tooltip-titlebar,
.ui-tooltip-blue .ui-tooltip-content {
    border-top-color: #085a7f;
    border-right-color: #085a7f;
    border-bottom-color: #085a7f;
    border-left-color: #085a7f;
    color: #84bbdb;
}
.ui-tooltip-blue .ui-tooltip-content {
    background-color: #001b33;
}
.ui-tooltip-blue .ui-tooltip-titlebar {
    background-color: #002135;
}
.ui-tooltip-shadow {
    box-shadow: rgba(0, 0, 0, 0.15) 1px 1px 3px 1px;
}
.ui-tooltip-youtube {
    box-shadow: #182030 0px 0px 3px;
}
.ui-tooltip-youtube .ui-tooltip-titlebar,
.ui-tooltip-youtube .ui-tooltip-content {
    background-color: rgba(0, 0, 0, 0.85);
    background-image: none;
    color: #ffffff;
    border-top-color: #303030;
    border-right-color: #303030;
    border-bottom-color: #303030;
    border-left-color: #303030;
}
.ui-tooltip-youtube .ui-tooltip-icon {
    border-top-color: #595959;
    border-right-color: #595959;
    border-bottom-color: #595959;
    border-left-color: #595959;
}
.ui-tooltip-youtube .ui-tooltip-titlebar .ui-state-hover {
    border-top-color: #555555;
    border-right-color: #555555;
    border-bottom-color: #555555;
    border-left-color: #555555;
}
.ui-tooltip-jtools {
    background-color: rgba(0, 0, 0, 0.7);
    background-image: linear-gradient(#203045 0%, #0c0e15 100%);
    border-top-color: #272727;
    border-right-color: #272727;
    border-bottom-color: #272727;
    border-left-color: #272727;
    box-shadow: #182030 0px 0px 12px;
}
.ui-tooltip-jtools .ui-tooltip-titlebar,
.ui-tooltip-jtools .ui-tooltip-content {
    background-color: transparent;
    background-image: none;
    color: #ffffff;
    border-top-color: transparent;
    border-right-color: transparent;
    border-bottom-color: transparent;
    border-left-color: transparent;
}
.ui-tooltip-jtools .ui-tooltip-icon {
    border-top-color: #4d4d4d;
    border-right-color: #4d4d4d;
    border-bottom-color: #4d4d4d;
    border-left-color: #4d4d4d;
}
.ui-tooltip-jtools .ui-tooltip-titlebar .ui-state-hover {
    border-top-color: #555555;
    border-right-color: #555555;
    border-bottom-color: #555555;
    border-left-color: #555555;
}
.ui-tooltip-cluetip {
    box-shadow: rgba(0, 0, 0, 0.4) 4px 4px 5px;
}
.ui-tooltip-cluetip .ui-tooltip-titlebar {
    background-color: #43432f;
    color: #ffffff;
