css:
@-moz-document domain("trello.com") {
/*	Board	*/

#header {
	background-color: #ba9352 !important;
}

.board-canvas,
.board-header {
	background-color: #fbfbfb !important;
}

.board-header {
	padding: 16px 8px;
}

.board-header a {
    color: #9c9c9c;
}

.body-dark-board-background .board-header {
    background-color: transparent;
    padding: 16px 4px;
	margin-bottom: 0px;
}

.header-btn.new-notifications:not(.display-count):not(.display-dot) {
	background-color: #000;
}

.header-btn.new-notifications:not(.display-count):not(.display-dot):hover {
	background-color: rgb( 0, 0, 0, .5);
}

.body-custom-board-background .board-header-btn:not(.no-edit):hover {
	background-color: rgb(182, 147, 75, .7);
}

.phenom .mark-read:hover
.phenom .mark-read:hover:before,
.phenom .mark-read:before,
.phenom .mark-unread:hover:before {
	background-color: rgb(182, 147, 75, .7) !important;
	border-color: #ba9352  !important;
}

.phenom.mod-is-card.mod-unread .unread-cover, .phenom.mod-is-card .unread-cover, .phenom.mod-is-not-card.mod-unread .unread-cover, .phenom.mod-is-not-card .unread-cover,
.phenom.mod-is-card .mark-read:hover, .phenom.mod-is-card .mark-unread:hover, .phenom.mod-is-card.mod-unread .mark-read:hover, .phenom.mod-is-card.mod-unread .mark-unread:hover, .phenom.mod-is-not-card .mark-read:hover, .phenom.mod-is-not-card .mark-unread:hover, .phenom.mod-is-not-card.mod-unread .mark-read:hover, .phenom.mod-is-not-card.mod-unread .mark-unread:hover, 
.phenom.mod-is-card .mark-read:hover, .phenom.mod-is-card .mark-unread:hover, .phenom.mod-is-card.mod-unread .mark-read:hover, .phenom.mod-is-card.mod-unread .mark-unread:hover, .phenom.mod-is-not-card .mark-read:hover, .phenom.mod-is-not-card .mark-unread:hover, .phenom.mod-is-not-card.mod-unread .mark-read:hover, .phenom.mod-is-not-card.mod-unread .mark-unread:hover {
	background-color: rgb(182, 147, 75, .2);
}





/*	Lists & Cards	*/
.list-header {
	display: flex;
    align-items: center;
    justify-content: flex-start;
}

.list-header-num-cards {
    width: 90px;
    text-align: right;
    display: block;
}

.list {
    background-color: #f5f5f5;
	border-radius: 8px;
    padding: 8px 6px;
}

.list-wrapper {
	width: 400px;
    min-width: 300px;
}


.list-card {
	max-width: 100%;
    border-radius: 6px;
    margin: 8px 4px;
    margin-top: 0;
    padding: 2px;
    border: 2px solid #fff;
    box-shadow: 0 0 15px rgba(0, 0, 0, .1);
}

.list-card:hover {
    background-color: #fff;
    border: 2px solid rgba(182, 147, 75, .7);
    transition: .3s;
}

.list-card-operation {
	background-color: #fff;
}

.list-card-operation:hover {
	background-color: #fff;
    color: #ba9352;
}

.u-fancy-scrollbar::-webkit-scrollbar-track-piece {
	background: rgba(0, 0, 0, 0.07);   
}

.icon-lg, 
.icon-sm,
.badge.is-due-now .badge-icon,
.badge.is-complete .badge-icon,
.badge.is-due-soon .badge-icon,
.badge-text,
.open-card-composer {
    color: #9c9c9c;
}

.badge.is-complete,
.badge.is-due-now,
.badge.is-due-soon {
    background-color: #fff;
}

.open-card-composer {
    padding: 10px;
    margin: 0 12px 12px 12px;
    transition: .15s;
}

.open-card-composer:hover {
    border-radius: 8px;
    color: #ba9352;
    background-color: #fff;
    text-decoration: none;
	box-shadow: 0 0 15px rgba(0, 0, 0, 0.1);
}

.open-card-composer:hover .icon-add {
    color: #ba9352;
}

.body-custom-board-background .list-wrapper.mod-add.is-idle {
    visibility: hidden;
}







/*	Incard	*/
.window {
	width: 960px;
	padding: 20px;
    background-color: #fff;
    border-radius: 10px;
}
  
.window-main-col {
	width: 740px;
}

.button-link {
	background-color: #fff;
	box-shadow: none;
}

.button-link:hover {
	background-color: #e5e5e5;
	box-shadow: none;
}

.small-window .window-sidebar {
  position: static;
}

.window-module {
	border-top: 1px solid #e5e5e5;
	margin-top: 30px;
    padding-top: 30px;
}

.board-header-btn-text, .board-header-btn-icon {
	color: #000 !important;
}

.description-content-fade-button-text {
	background: #fff;
}

.description-content-fade-button {
	background: linear-gradient(180deg,rgba(235,238,240,0),rgba(235,238,240,0) 370px,#ffffffe6 400px);
}

.description-content-fade-button:hover {
	background: linear-gradient(180deg,rgba(235,238,240,0),rgba(235,238,240,0) 370px,#ffffff9c 400px);
}

p {
    text-overflow: ellipsis;
    word-wrap: break-word;
    font-size: 12pt;
    line-height: 20pt;
    padding: 10px;
}

li {
    font-size: 12pt;
}

.action-comment {
    background-color: #fff;
	box-shadow: none;   
    width: 100%;
}

.phenom {
    padding-bottom: 60px;
	margin: 40px -20px 0 40px;
    border-bottom: 1px solid #000;
}






/*	Labels	*/
.card-lables-cover {
	background-color: #fff;
}

.card-label-green {
	background-color: #3f815b;
}

.card-label-yellow {
	background-color: #fefe4e;
	color: #6b6b00;
}

.card-label-orange {
	background-color: #ffb018;
}

.card-label-red {
	background-color: #dd4d3a;
}

.card-label-purple {
	background-color: #5a4099;
}

.card-label-blue {
	background-color: #4332d2;
}

.card-label-sky {
	background-color: #cae845;
	color: #495900;
}

.card-label-lime {
	background-color: #7eae2b;
}

.card-label-pink {
	background-color: #fff;
	color: #000;
}

.card-label-black {
	background-color: #000;
}
}
