css:
@-moz-document domain("zportal.nl") {
@keyframes rainbowcycle {
    0% {color: #ff0000;}
    5% {color: #ff2200;}
    10% {color: #ff4400;}
    15% {color: #ff6600;}
    20% {color: #ff8800;}
    25% {color: #ffaa00;}
    30% {color: #ffcc00;}
    35% {color: #ffdd00;}
    40% {color: #ffff00;}
    45% {color: #aaff00;}
    50% {color: #88ff00;}
    55% {color: #44ff00;}
    60% {color: #00ff00;}
    65% {color: #00bb44;}
    70% {color: #008888;}
    75% {color: #0044bb;}
    80% {color: #0000ff;}
    85% {color: #2200dd;}
    90% {color: #4400cc;}
    95% {color: #6600aa;}
    100% {color: #880088;}
}
@keyframes backgroundcycle {
    0% {background-color: #ff0000;}
    5% {background-color: #ff2200;}
    10% {background-color: #ff4400;}
    15% {background-color: #ff6600;}
    20% {background-color: #ff8800;}
    25% {background-color: #ffaa00;}
    30% {background-color: #ffcc00;}
    35% {background-color: #ffdd00;}
    40% {background-color: #ffff00;}
    45% {background-color: #aaff00;}
    50% {background-color: #88ff00;}
    55% {background-color: #44ff00;}
    60% {background-color: #00ff00;}
    65% {background-color: #00bb44;}
    70% {background-color: #008888;}
    75% {background-color: #0044bb;}
    80% {background-color: #0000ff;}
    85% {background-color: #2200dd;}
    90% {background-color: #4400cc;}
    95% {background-color: #6600aa;}
    100% {background-color: #880088;}
}
@keyframes slide {
    from {background-position-y: 0px;}
    to {background-position-y: 640px;}
}
.zermeloSideMenu {
    background-image: url("https://i.imgur.com/muUIQtk.png");
    background-size: 100% auto;
    background-color: #ecf0f1;
    animation: slide;
    animation-duration: 20s;
    animation-iteration-count: infinite;
    animation-timing-function: linear;
}
.zermeloTitleSmall {
    color: #000000;
}
.zermeloSideMenuItemSelected {
    background-color: rgba(0,0,0,0);
}
.zermeloSideMenuItemSelected * {
    filter: brightness(0);
}
.zermeloSideMenuItemSelected:hover {
    background-color: #08407b;
}
.zermeloSideMenuItem {
    background-color: rgba(0,0,0,0);
}
.zermeloSideMenuItem:hover {
    background-color: #08407b;
}
.zermeloTopBar, .zermeloTopBar * {
    background-image: url("https://i.kym-cdn.com/photos/images/original/000/128/096/Nyancatless20110725-22047-2vf3z5.gif");
}
a, *, h2 {
    color: #08407b;
}
.zermeloModule * {
    color: #000000;
}
.form {
    background-image: url("https://i.kym-cdn.com/photos/images/original/000/128/096/Nyancatless20110725-22047-2vf3z5.gif");
}
.form .logo {
    filter: brightness(9999);
}
.form .button {
    filter: saturate(0) brightness(3);
    color: #000000;
}
.form p, .form p *, .form a, .form label {
    color: #ffffff;
}
}
