css:
@-moz-document domain("vivo.sx") {
#fb-root {
    display: none !important;
}
header {
    display: none !important;
}
footer {
    display: none !important;
}
#light-background {
    display: none !important;
}
#player {
    border: dotted 1px #b50000 !important;
}
.watch-footer {
    display: none !important;
}
.stream-share {
    display: none !important;
}
section.app_overview {
    background: #000 !important;
}
div#alert-stream, div#alert-browser, div#alert-throttle {
    display: none !important;
}
.clearfix {
    display: none !important;
}
.vivo-content-box {
    border-radius: 5px !important;
    background: #000 !important;
    color: #eee !important;
    padding: 0 !important;
}
section.vivo-content.stream {
    padding-top: 0 !important;
    padding-bottom: 0 !important;
}
.vivo-content-box-wrapper .vivo-content-box h1 {
    color: #b50000 !important;
    font-size: 16px !important;
}
.vivo-content-box-wrapper .vivo-content-box-watch h1 strong {
    font-weight: 100 !important;
    color: #b50000 !important;
    font-size: 15px !important;
}
.h1, .h2, .h3, .h4, .h5, .h6, h1, h2, h3, h4, h5, h6 {
    font-family: inherit !important;
    font-weight: 100 !important;
    line-height: 1 !important;
    color: #b50000 !important;
}
#_atssh {
    display: none !important;
}
.fp-filters, .fp-logo, .watch-footer {
    display: none !important;
}
.addthis-smartlayers, div#at4-follow, div#at4-share, div#at4-thankyou, div#at4-whatsnext {
    display: none !important;
}
.container {
    width: 90% !important;
}
.vivo-content-box-wrapper {
    padding-left: 0 !important;
    padding-right: 0 !important;
}
html {
    font-size: 62.5% !important;
    -webkit-tap-highlight-color: #555 !important;
    background-color: #000 !important;
}
#AdWidgetContainer {
    display: none !important;
}
div[onclick*="("] {
    display: none !important;
}
.fp-ui, .fp-icon {
    color: #b50000 !important;
    font-style: normal !important;
    text-decoration: none !important;
}
.fp-color, .fp-progress, .fp-bar>.fp-progress.animated{
    background-color: #4a0000 !important;
    border: none !important;
    color: #b50000 !important;
    font-style: normal !important;
    text-decoration: none !important;
}
#preload {
    background: #000 !important;
}
.preload_loader div {
    background-color: rgb(181, 0, 0) !important;
    border-radius: 1px !important;
}
.vivo-video-data-holder {
    margin-top: 10px !important;
    color: #b50000 !important;
}
section.vivo-content.stream {
    padding-top: 10px !important;
}
.flowplayer .fp-color {
    background-color: #ff0000 !important;
}
.fp-elapsed, .fp-duration, .fp-duration:before {
    color: rgb(181, 0, 0) !important;
}
}
